SELECT COUNT(*) AS VALUE FROM ( select 'CR_0101IR' as if_tp, dlr_cd, to_number(if_seq) as if_seq, updatedate, createdate, ifresult, iffailmsg from CR_0101IR
union select 'CR_0601IR' as if_tp, dlr_cd, to_number(if_seq) as if_seq, updatedate , createdate, ifresult, iffailmsg from CR_0601IR
union select 'CR_0602IR' as if_tp, dlr_cd, to_number(if_seq) as if_seq, updatedate , createdate, ifresult, iffailmsg from CR_0602IR
union select 'CR_0211IR' as if_tp, dlr_cd, to_number(if_seq) as if_seq, updatedate , createdate, ifresult, iffailmsg from CR_0211IR
union select 'CR_0101IS_BM' as if_tp, dlr_cd, to_number(if_seq) as if_seq, updatedate , createdate, ifresult, iffailmsg from CR_0101IS_BM
)T1 INNER JOIN CM_0101T T2 ON T1.DLR_CD = T2.DLR_CD LEFT OUTER JOIN CR_1301T T3 ON T1.DLR_CD = T3.DLR_CD
AND T2.OPEN_DT >= #{sOpenStartDt}
AND T2.OPEN_DT <= #{sOpenEndDt}
AND T3.IF_SUCCESS_DT >= #{sIfSuccessStartDt}
AND T3.IF_SUCCESS_DT <= #{sIfSuccessEndDt}

SELECT * FROM (
    SELECT  ROWNUM AS RNUM, T.* FROM (
        SELECT T1.* FROM (
                SELECT 'CR_0101IR' AS IF_TP, DLR_CD, TO_NUMBER(IF_SEQ) AS IF_SEQ, UPDATEDATE, CREATEDATE, IFRESULT, IFFAILMSG
                  FROM CR_0101IR

                UNION
                SELECT 'CR_0601IR' AS IF_TP, DLR_CD, TO_NUMBER(IF_SEQ) AS IF_SEQ, UPDATEDATE , CREATEDATE, IFRESULT, IFFAILMSG
                  FROM CR_0601IR

                UNION
                SELECT 'CR_0602IR' AS IF_TP, DLR_CD, TO_NUMBER(IF_SEQ) AS IF_SEQ, UPDATEDATE , CREATEDATE, IFRESULT, IFFAILMSG
                  FROM CR_0602IR

                UNION
                SELECT 'CR_0211IR' AS IF_TP, DLR_CD, TO_NUMBER(IF_SEQ) AS IF_SEQ, UPDATEDATE , CREATEDATE, IFRESULT, IFFAILMSG
                  FROM CR_0211IR


                UNION
                SELECT 'CR_0101IS_BM' AS IF_TP, DLR_CD, TO_NUMBER(IF_SEQ) AS IF_SEQ, UPDATEDATE , CREATEDATE, IFRESULT, IFFAILMSG
                  FROM CR_0101IS_BM


            ) T1
        INNER JOIN CM_0101T T2 ON T1.DLR_CD = T2.DLR_CD
        LEFT OUTER JOIN CR_1301T T3 ON T1.DLR_CD = T3.DLR_CD


                AND T2.OPEN_DT >= #{sOpenStartDt}


                AND T2.OPEN_DT <= #{sOpenEndDt}


                AND T3.IF_SUCCESS_DT >= #{sIfSuccessStartDt}


                AND T3.IF_SUCCESS_DT <= #{sIfSuccessEndDt}


                AND T2.DMS_USE_YN = #{sDmsUseYn}


                AND T2.ALIVE_YN = #{sAliveYn}


        ORDER BY T1.DLR_CD
        ) T


            ROWNUM <= #{lastIndex}


)


        RNUM > #{firstIndex}

SELECT * FROM (
    SELECT  ROWNUM AS RNUM, T.* FROM (
        SELECT T1.DLR_CD, MAX(T2.OPEN_DT) AS OPEN_DT, MAX(T2.DMS_IP_NM) AS DMS_IP_NM, COUNT(T1.IF_SEQ) AS CNT, MIN(T1.UPDATEDATE) AS MIN_UPDATEDATE, MAX(T3.IF_SUCCESS_DT) AS IF_SUCCESS_DT FROM (
                SELECT 'CR_0101IR' AS IF_TP, DLR_CD, TO_NUMBER(IF_SEQ) AS IF_SEQ, UPDATEDATE, CREATEDATE, IFRESULT, IFFAILMSG
                  FROM CR_0101IR

                UNION
                SELECT 'CR_0601IR' AS IF_TP, DLR_CD, TO_NUMBER(IF_SEQ) AS IF_SEQ, UPDATEDATE , CREATEDATE, IFRESULT, IFFAILMSG
                  FROM CR_0601IR

                UNION
                SELECT 'CR_0602IR' AS IF_TP, DLR_CD, TO_NUMBER(IF_SEQ) AS IF_SEQ, UPDATEDATE , CREATEDATE, IFRESULT, IFFAILMSG
                  FROM CR_0602IR

                UNION
                SELECT 'CR_0211IR' AS IF_TP, DLR_CD, TO_NUMBER(IF_SEQ) AS IF_SEQ, UPDATEDATE , CREATEDATE, IFRESULT, IFFAILMSG
                  FROM CR_0211IR


                UNION
                SELECT 'CR_0101IS_BM' AS IF_TP, DLR_CD, TO_NUMBER(IF_SEQ) AS IF_SEQ, UPDATEDATE , CREATEDATE, IFRESULT, IFFAILMSG
                  FROM CR_0101IS_BM


            ) T1
        INNER JOIN CM_0101T T2 ON T1.DLR_CD = T2.DLR_CD
        LEFT OUTER JOIN CR_1301T T3 ON T1.DLR_CD = T3.DLR_CD


                AND T2.OPEN_DT >= #{sOpenStartDt}


                AND T2.OPEN_DT <= #{sOpenEndDt}


                AND T3.IF_SUCCESS_DT >= #{sIfSuccessStartDt}


                AND T3.IF_SUCCESS_DT <= #{sIfSuccessEndDt}


                AND T2.DMS_USE_YN = #{sDmsUseYn}


                AND T2.ALIVE_YN = #{sAliveYn}


        GROUP BY T1.DLR_CD
        ORDER BY T1.DLR_CD
        ) T


            ROWNUM <= #{lastIndex}


)


        RNUM > #{firstIndex}

