mapper.update:
/* [chn.bhmc.dms.cmm.ath.service.dao.RoleMappingDAO.deleteRoleMappingByRoleId] */
UPDATE CM_0803T SET USE_YN = 'N'
WHERE SYS_CD = #{sysCd}
    AND ROLE_ID = #{roleId}

mapper.delete:
/* [chn.bhmc.dms.cmm.ath.service.dao.RoleMappingDAO.delateRoleMappingByRefTpAndRefId] */
DELETE
FROM CM_0803T
WHERE
    SYS_CD = #{sysCd}
    AND REF_TP = #{refTp}
    AND REF_ID = #{refId}

mapper.insert:
/* [chn.bhmc.dms.cmm.ath.service.dao.RoleMappingDAO.selectAndInsertRoleMapping] */
INSERT INTO CM_0803T(
    SYS_CD
    ,ROLE_ID
    ,REF_TP
    ,REF_ID
    ,REG_DT
    ,REG_USR_ID
    ,USE_YN
)
SELECT
    SYS_CD
    ,ROLE_ID
    ,REF_TP
    ,#{destRefId}
    ,SYSDATE
    ,#{regUsrId}
    ,USE_YN
FROM CM_0803T
WHERE SYS_CD = #{sysCd}
    AND REF_TP = #{refTp}
    AND REF_ID = #{srcRefId}

mapper.select:
/* [chn.bhmc.dms.cmm.ath.service.dao.RoleMappingDAO.selectRoleMappings] */
SELECT
     T1.ROLE_ID
    ,T1.ROLE_NM
    ,T2.ROLE_DETL_CONT
FROM CM_0804T T1
LEFT JOIN CM_0803T T2 ON T1.SYS_CD = T2.SYS_CD AND T1.ROLE_ID = T2.ROLE_ID
WHERE
    T1.SYS_CD = #{sSysCd}

    AND T2.REF_TP = #{sRefTp}


    AND T2.REF_ID = #{sRefId}

    AND T1.USE_YN = 'Y'
    AND T2.USE_YN = 'Y'

        AND T1.ROLE_ID NOT IN

            #{roleId}

SELECT T2.USR_ID ,T2.USR_NM FROM CM_0803T T1 INNER JOIN CM_0801T T2 ON T1.REF_ID = T2.USR_ID AND T1.REF_TP = 'U' WHERE T1.SYS_CD = #{sysCd}
AND T1.ROLE_ID IN
#{roleId}
AND T2.DLR_CD = #{dlrCd} AND T1.USE_YN = 'Y' AND T2.DEL_YN = 'N' AND T2.ENABLED_YN = 'Y'
