mapper.select:

SELECT T1.* FROM (
    SELECT DLR_CD, DLR_NM
    FROM CM_0101T
    WHERE
        DLR_CD LIKE #{sText}
        OR DLR_NM LIKE #{sText}
    ORDER BY DLR_NM ASC
) T1
WHERE ROWNUM <= #{maxRows}

/* [chn.bhmc.dms.cmm.cmp.service.dao.DealerDAO.selectDealersByCondition] */
SELECT * FROM (
    SELECT ROWNUM AS RNUM, T1.* FROM (
        SELECT * FROM CM_0101T





                        AND DLR_CD LIKE #{sDlrCd}


                        AND DLR_CD = #{sDlrCd}







                        AND DLR_NM LIKE #{sDlrNm}


                        AND DLR_NM = #{sDlrNm}




                AND DLR_GRP_CD = #{sDlrGrpCd}


                AND DMS_USE_YN = #{sDmsUseYn}


                AND SDPT_CD = #{sSdptCd}


                AND DIST_OFFICE_CD = #{sDistOfficeCd}


                AND SALE_DLR_SUNG_CD = #{sSaleDlrSungCd}


                AND SALE_DLR_CITY_CD = #{sSaleDlrCityCd}


                AND DLR_CD IN

                    #{dlrCd}



                AND SYS_OWNER_YN = #{sSysOwnerYn}


                AND USE_CENTER_DMS_YN = #{sUseCenterDmsYn}


                AND DEL_YN = #{sDelYn}


                AND OPEN_DT = TO_CHAR(TO_DATE(#{sOpenDt}, 'YYYYMMDD')+1, 'YYYYMMDD')


        ORDER BY




                        DLR_CD ASCDESC
                        DLR_NM ASCDESC
                        DLR_GRP_CD ASCDESC
                        OWN_RGST_NM ASCDESC
                        TEL_NO ASCDESC
                        FAX_NO ASCDESC
                        CALL_CEN_TEL_NO ASCDESC
                        EMGC_CEN_TEL_NO ASCDESC
                        SER_DLR_TEL_NO ASCDESC
                        SAL_DLR_TEL_NO ASCDESC

                            DLR_CD ASC





                DLR_CD ASC


        ) T1


            ROWNUM <= #{lastIndex}


)


        RNUM > #{firstIndex}

/* [chn.bhmc.dms.cmm.cmp.service.dao.DealerDAO.selectDealersByConditionCnt] */
SELECT COUNT(*) FROM CM_0101T





                AND DLR_CD LIKE #{sDlrCd}


                AND DLR_CD = #{sDlrCd}







                AND DLR_NM LIKE #{sDlrNm}


                AND DLR_NM = #{sDlrNm}




        AND DLR_GRP_CD = #{sDlrGrpCd}


        AND DMS_USE_YN = #{sDmsUseYn}


        AND SDPT_CD = #{sSdptCd}


        AND DIST_OFFICE_CD = #{sDistOfficeCd}


        AND SALE_DLR_SUNG_CD = #{sSaleDlrSungCd}


        AND SALE_DLR_CITY_CD = #{sSaleDlrCityCd}


        AND DLR_CD IN

            #{dlrCd}



        AND SYS_OWNER_YN = #{sSysOwnerYn}


        AND USE_CENTER_DMS_YN = #{sUseCenterDmsYn}


        AND DEL_YN = #{sDelYn}


        AND OPEN_DT = #{openDt}

mapper.update:
/* [chn.bhmc.dms.cmm.cmp.service.dao.DealerDAO.updateDcsMigStat] */
UPDATE CM_0101T SET
    MIG_PRG_STAT = #{migPrgStat}

   ,MIG_START_DT = SYSDATE
   ,MIG_END_DT = NULL


   ,MIG_END_DT = SYSDATE

WHERE DLR_CD = #{dlrCd}

mapper.select:
/* [chn.bhmc.dms.cmm.cmp.service.dao.DealerDAO.selectDealerListByConditionCnt] */
SELECT COUNT(*) AS VALUE
  FROM CM_0101T
 WHERE DMS_USE_YN = 'Y'


       AND CS_AREA_CCBZ_ID = #{sCsAreaCcbzId}



   AND SDPT_CD = #{sSdptCd}


   AND DIST_OFFICE_CD = #{sDistOfficeCd}


   AND SALE_DLR_SUNG_CD = #{sSaleDlrSungCd}


   AND SALE_DLR_CITY_CD = #{sSaleDlrCityCd}

ORDER BY DLR_CD

/* [chn.bhmc.dms.cmm.cmp.service.dao.DealerDAO.selectDealerListByCondition] */
SELECT DLR_CD
      ,'['||DLR_CD||']'||DLR_SHT_NM AS DLR_NM
  FROM CM_0101T
 WHERE DMS_USE_YN = 'Y'
   --服务业务报表 经销商表增加区域经理权限 贾明 2018-9-20 start

       AND CS_AREA_CCBZ_ID = #{sCsAreaCcbzId}

   --服务业务报表 经销商表增加区域经理权限 贾明 2018-9-20 end
   --服务业务报表 科长可以查看全部区域经理经销商信息 田佳兴 2020-9-20 start

       AND CS_AREA_CCBZ_ID IN ( SELECT USR_ID FROM CM_0801T WHERE  DEPT_CD = #{sDeptCd} ) 

   --服务业务报表 科长可以查看全部区域经理经销商信息 田佳兴 2020-9-20 end

   AND SDPT_CD = #{sSdptCd}


   AND DIST_OFFICE_CD = #{sDistOfficeCd}


   AND SALE_DLR_SUNG_CD = #{sSaleDlrSungCd}


   AND SALE_DLR_CITY_CD = #{sSaleDlrCityCd}

ORDER BY DLR_CD
