mapper.insert:
/* [chn.bhmc.dms.cmm.nms.service.dao.NotificationTargetDAO.updateNotificationTarget] */
UPDATE CM_2103T SET
     MESG_TMPL_TP_LIST = #{mesgTmplTpList}
    ,UPDT_USR_ID = #{updtUsrId}
    ,UPDT_DT = SYSDATE
WHERE 
	ALRT_POLICY_GRP_ID = #{alrtPolicyGrpId}
    AND ALRT_POLICY_ID = #{alrtPolicyId}
    AND REF_TP = #{refTp}
    AND REF_ID = #{refId}

mapper.delete:
/* [chn.bhmc.dms.cmm.nms.service.dao.NotificationTargetDAO.deleteNotificationTarget] */
DELETE FROM CM_2103T
WHERE ALRT_POLICY_GRP_ID = #{alrtPolicyGrpId}
    AND ALRT_POLICY_ID = #{alrtPolicyId}
    AND REF_TP = #{refTp}
    AND REF_ID = #{refId}

mapper.select:
/* [chn.bhmc.dms.cmm.nms.service.dao.NotificationTargetDAO.selectNotificationTargetByKey] */
SELECT * FROM CM_2103T
WHERE 
	ALRT_POLICY_GRP_ID = #{alrtPolicyGrpId}
    AND ALRT_POLICY_ID = #{alrtPolicyId}
    AND REF_TP = #{refTp}
    AND REF_ID = #{refId}

/* [chn.bhmc.dms.cmm.nms.service.dao.NotificationTargetDAO.selectNotificationTargetsByCondition] */ SELECT ROWNUM AS RNUM ,V1.ALRT_POLICY_GRP_NM ,V2.ALRT_POLICY_NM ,V3.* FROM CM_2101T V1 INNER JOIN CM_2102T V2 ON V1.ALRT_POLICY_GRP_ID = V2.ALRT_POLICY_GRP_ID INNER JOIN CM_2103T V3 ON V1.ALRT_POLICY_GRP_ID = V3.ALRT_POLICY_GRP_ID AND V2.ALRT_POLICY_ID = V3.ALRT_POLICY_ID WHERE V1.ALRT_POLICY_GRP_ID = #{sAlrtPolicyGrpId} AND V2.ALRT_POLICY_ID = #{sAlrtPolicyId}
AND V3.REF_TP = #{sRefTp}
ORDER BY
V3.REF_ID ASCDESC V3.REF_NM ASCDESC V3.MESG_TMPL_TP_LIST ASCDESC
V3.REF_ID ASC
V3.REF_ID ASC

mapper.delete:
/* [chn.bhmc.dms.cmm.nms.service.dao.NotificationTargetDAO.deleteNotificationExcludeTarget] */
DELETE FROM CM_2105T
WHERE ALRT_POLICY_GRP_ID = #{alrtPolicyGrpId}
    AND ALRT_POLICY_ID = #{alrtPolicyId}
    AND USR_ID = #{usrId}

mapper.select:
/* [chn.bhmc.dms.cmm.nms.service.dao.NotificationTargetDAO.selectNotificationExcludeTargetByKey] */
SELECT * FROM CM_2105T
WHERE 
	ALRT_POLICY_GRP_ID = #{alrtPolicyGrpId}
    AND ALRT_POLICY_ID = #{alrtPolicyId}
    AND USR_ID = #{usrId}

/* [chn.bhmc.dms.cmm.nms.service.dao.NotificationTargetDAO.selectNotificationTargetRoleUsers] */ SELECT T1.ALRT_POLICY_GRP_ID ,T1.ALRT_POLICY_ID ,T1.MESG_TMPL_TP_LIST ,T2.USR_ID ,T2.USR_NM ,T2.HP_NO ,T2.EMAIL FROM CM_2103T T1 INNER JOIN CM_0801T T2 ON T1.REF_ID = T2.USR_ID AND T1.REF_TP = 'U' WHERE T1.ALRT_POLICY_GRP_ID = #{alrtPolicyGrpId} AND T1.ALRT_POLICY_ID = #{alrtPolicyId}
UNION
SELECT T1.ALRT_POLICY_GRP_ID ,T1.ALRT_POLICY_ID ,T1.MESG_TMPL_TP_LIST ,T2.USR_ID ,T2.USR_NM ,T2.HP_NO ,T2.EMAIL FROM CM_2103T T1 INNER JOIN CM_0801T T2 ON T1.REF_ID = T2.DEPT_CD WHERE T1.ALRT_POLICY_GRP_ID = #{alrtPolicyGrpId} AND T1.ALRT_POLICY_ID = #{alrtPolicyId} AND T1.REF_TP = 'D'
