mapper.select:
/* [chn.bhmc.dms.crm.cmm.service.dao.CallInfoDAO.selectCallInfoByConditionCnt] */
SELECT COUNT(*) AS VALUE
  FROM CR_0605T T1

/* [chn.bhmc.dms.crm.cmm.service.dao.CallInfoDAO.selectCallInfoByCondition] */
SELECT * FROM (
    SELECT ROWNUM AS RNUM, T1.* FROM (
        SELECT T1.*
        FROM CR_0605T T1

        ORDER BY REG_DT DESC
    ) T1


            AND ROWNUM <= #{lastIndex}


)


        RNUM > #{firstIndex}

/* [chn.bhmc.dms.crm.cmm.service.dao.CallInfoDAO.selectNextCallListNo] */
SELECT FU_GET_CARLINE_NO() AS SEQ
FROM   DUAL

mapper.update:
/* [chn.bhmc.dms.crm.cmm.service.dao.CallInfoDAO.updateCallInfo] */ UPDATE CR_0605T --콜리스트 SET DLR_CD = #{dlrCd} --DLR_CD ,CALL_LIST_NO = #{callListNo} --콜리스트번호 ,CUST_NO = #{custNo} --고객번호 ,VIN_NO1 = #{vinNo1} --차대번호1 ,VIN_NO2 = #{vinNo2} --차대번호2 ,TEL_NO = #{telNo} --연락처 ,CALL_LIST_CD = #{callListCd} --콜리스트코드 정비(SERVICE), 만족도조사(SURVEY), 캠페인(CAMPAIGN), IDCC리드(IDCCLEAD) ,CALL_LIST_REQ_NO = #{callListReqNo} --콜리스트요청번호 ,REQ_DEPT_CD = #{reqDeptCd} --요청부서코드 ,REQ_USR_ID = #{reqUsrId} --요청사용자ID ,REMARK = #{remark} --비고 ,REG_URL = #{regUrl} --녹취URL ,STAT_CD = #{statCd} --상태코드 CRM608
,NEXT_CALL_DT = #{nextCallDt} --다음콜일자
,UPDT_USR_ID = #{updtUsrId} --수정자ID ,UPDT_DT = SYSDATE WHERE 1 = 1 AND DLR_CD = #{dlrCd} --DLR_CD AND CALL_LIST_NO = #{callListNo} --콜리스트번호
