mapper.sql:
LEFT OUTER JOIN CM_0801T USRT ON CR0501T.REG_USR_ID = USRT.USR_ID



    AND CR0501T.DLR_CD = #{sDlrCd}






                    AND CR0501T.CUST_CARE_NM LIKE #{sCustCareNm}


                AND CR0501T.CUST_CARE_NM LIKE #{sCustCareNm}





        AND CR0501T.ACTIVE_TP_CD = #{sActiveTpCd}



        AND CR0501T.AUTO_ACTIVE_YN = #{sAutoActiveYn}







        AND CR0501T.START_DT >= #{sStartStartDt}




        AND CR0501T.START_DT < #{sStartEndDt}

mapper.select:
/* [chn.bhmc.dms.crm.sac.service.dao.CustomerCareMgmtDAO.selectCustomerCareMgmtsByConditionCnt] */
SELECT COUNT(*) AS VALUE
FROM CR_0501T CR0501T

/* [chn.bhmc.dms.crm.sac.service.dao.CustomerCareMgmtDAO.selectCustomerCareMgmtsByCondition] */ SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT
CR0501T.DLR_CD --딜러코드 ,CR0501T.CUST_CARE_SEQ --고객케어일련번호 ,CR0501T.CUST_CARE_NM --고객케어명 ,CR0501T.ACTIVE_TP_CD --실행유형코드 ,CR0501T.AUTO_ACTIVE_YN --자동실행여부 ,CR0501T.ACTIVE_TMPL_ID --실행템플릿ID ,CR0501T.CUST_EXTR_TERM_NO --고객추출조건번호 ,CR0501T.CUST_EXTR_AUTO_YN --고객추출자동여부 ,CR0501T.CUST_EXTR_CYCLE_CONT --고객추출주기내용 ,CR0501T.CUST_CARE_CONT --고객케어내용 ,CR0501T.FILE_DOC_NO --첨부파일문서번호 ,CR0501T.USE_YN --사용여부 ,CR0501T.REG_USR_ID --등록자ID ,CR0501T.REG_DT --등록일자 ,CR0501T.UPDT_USR_ID --수정자ID ,CR0501T.UPDT_DT --수정일자
,CR0501T.EXTR_DGRE_CNT --추출차수 ,CR0501T.LAST_EXTR_DT --최종추출일자 ,CR0501T.START_DT --시작일자 ,CR0501T.END_DT --종료일자 ,CR0501T.BAT_OPER_ID --배치아이디
,USRT.USR_NM AS REG_USR_NM --등록자이름 FROM CR_0501T CR0501T
ORDER BY
CR0501T.CUST_CARE_NM ASCDESC CR0501T.CUST_CARE_CONT ASCDESC CR0501T.CUST_EXTR_AUTO_YN ASCDESC CR0501T.USE_YN ASCDESC CR0501T.START_DT ASCDESC USRT.USR_NM ASCDESC CR0501T.REG_DT ASCDESC
CR0501T.DLR_CD ASCDESC CR0501T.CUST_CARE_SEQ ASCDESC CR0501T.ACTIVE_TP_CD ASCDESC CR0501T.AUTO_ACTIVE_YN ASCDESC CR0501T.ACTIVE_TMPL_ID ASCDESC CR0501T.CUST_EXTR_TERM_NO ASCDESC CR0501T.CUST_EXTR_CYCLE_CONT ASCDESC CR0501T.FILE_DOC_NO ASCDESC CR0501T.UPDT_USR_ID ASCDESC CR0501T.UPDT_DT ASCDESC CR0501T.EXTR_DGRE_CNT ASCDESC CR0501T.LAST_EXTR_DT ASCDESC CR0501T.START_DT ASCDESC CR0501T.END_DT ASCDESC
CR0501T.CUST_CARE_SEQ DESC
CR0501T.CUST_CARE_SEQ DESC
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

/* [chn.bhmc.dms.crm.sac.service.dao.CustomerCareMgmtDAO.selectCustomerCareMgmtByKey] */ SELECT
CR0501T.DLR_CD --딜러코드 ,CR0501T.CUST_CARE_SEQ --고객케어일련번호 ,CR0501T.CUST_CARE_NM --고객케어명 ,CR0501T.ACTIVE_TP_CD --실행유형코드 ,CR0501T.AUTO_ACTIVE_YN --자동실행여부 ,CR0501T.ACTIVE_TMPL_ID --실행템플릿ID ,CR0501T.CUST_EXTR_TERM_NO --고객추출조건번호 ,CR0501T.CUST_EXTR_AUTO_YN --고객추출자동여부 ,CR0501T.CUST_EXTR_CYCLE_CONT --고객추출주기내용 ,CR0501T.CUST_CARE_CONT --고객케어내용 ,CR0501T.FILE_DOC_NO --첨부파일문서번호 ,CR0501T.USE_YN --사용여부 ,CR0501T.REG_USR_ID --등록자ID ,CR0501T.REG_DT --등록일자 ,CR0501T.UPDT_USR_ID --수정자ID ,CR0501T.UPDT_DT --수정일자
,CR0501T.EXTR_DGRE_CNT --추출차수 ,CR0501T.LAST_EXTR_DT --최종추출일자 ,CR0501T.START_DT --시작일자 ,CR0501T.END_DT --종료일자 ,CR0501T.BAT_OPER_ID --배치아이디
,USRT.USR_NM AS REG_USR_NM --등록자이름
FROM CR_0501T CR0501T LEFT OUTER JOIN CM_0801T USRT ON CR0501T.REG_USR_ID = USRT.USR_ID WHERE CR0501T.DLR_CD = #{sDlrCd} AND CR0501T.CUST_CARE_SEQ = #{sCustCareSeq}

mapper.insert:
/* [chn.bhmc.dms.crm.sac.service.dao.CustomerCareMgmtDAO.insertCustomerCareMgmt] */ INSERT INTO DMSDB.CR_0501T ( --고객캐어미션 DLR_CD --딜러코드 ,CUST_CARE_SEQ --고객케어일련번호 ,CUST_CARE_NM --고객케어명 ,ACTIVE_TP_CD --실행유형코드 ,AUTO_ACTIVE_YN --자동실행여부 ,ACTIVE_TMPL_ID --실행템플릿ID ,CUST_EXTR_TERM_NO --고객추출조건번호 ,CUST_EXTR_AUTO_YN --고객추출자동여부 ,CUST_EXTR_CYCLE_CONT --고객추출주기내용 ,CUST_CARE_CONT --고객케어내용 ,FILE_DOC_NO --첨부파일문서번호 ,USE_YN --사용여부 ,REG_USR_ID --등록자ID ,REG_DT --등록일자 ,START_DT --시작일자 ,END_DT --종료일자 ,BAT_OPER_ID --배치아이디
) VALUES (
#{dlrCd} --딜러코드 ,SEQ_CR_0501T.NEXTVAL --고객케어일련번호 ,#{custCareNm} --고객케어명 ,#{activeTpCd} --실행유형코드 ,#{autoActiveYn} --자동실행여부 ,#{activeTmplId} --실행템플릿ID ,#{custExtrTermNo} --고객추출조건번호 ,#{custExtrAutoYn} --고객추출자동여부 ,#{custExtrCycleCont} --고객추출주기내용 ,#{custCareCont} --고객케어내용 ,#{fileDocNo} --첨부파일문서번호 ,#{useYn} --사용여부 ,#{regUsrId} --등록자ID ,SYSDATE --등록일자 ,#{startDt} --시작일자 ,#{endDt} --종료일자 ,#{batOperId} --배치아이디 )
SELECT SEQ_CR_0501T.currval AS custCareSeq FROM DUAL

mapper.update:
/* [chn.bhmc.dms.crm.sac.service.dao.CustomerCareMgmtDAO.deleteCustomerCareMgmt] */
DELETE FROM DMSDB.CR_0501T
WHERE DLR_CD = #{sDlrCd}
AND CUST_CARE_SEQ      = #{sCustCareSeq}              --고객케어일련번호
