mapper.sql:
LEFT OUTER JOIN CM_0801T USRT ON CR0306T.REG_USR_ID = USRT.USR_ID


    AND CR0306T.DLR_CD = #{sDlrCd}
    AND CR0306T.CONTACT_BIZ_CD = #{sContactBizCd}
    AND CR0306T.REF_TABLE_NM = #{sRefTableNm}
    AND CR0306T.REF_KEY_NM = #{sRefKeyNm}


        AND CR0306T.CUST_NO = #{sCustNo }

mapper.select:
/* [chn.bhmc.dms.crm.sac.service.dao.ContactHistoryDAO.selectMsgHistorysByCondition] */ SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT CR0306T.DLR_CD --딜러코드 ,CR0306T.CONTACT_NO --접촉번호 ,CR0306T.CUST_NO --고객번호 ,CR0306T.CONTACT_STAT_CD --접촉 입력,출력코드 I :IN / O:OUT ,CR0306T.CONTACT_TP --접촉유형 (문자,WECHAT,EMAIL,전화) ,CR0306T.CONTACT_BIZ_CD --업무(고객,리드,기회,VOC,만족도,캠페인,멤버십 ,CR0306T.CONTACT_CONT --접촉내용 ,CR0306T.REF_TABLE_NM --출처참조테이블명 ,CR0306T.REF_KEY_NM --출처참조키명 ,CR0306T.REG_USR_ID --등록자ID ,USRT.USR_NM AS REG_USR_NM --등록자이름 ,CR0306T.REG_DT --등록일자 FROM CR_0306T CR0306T --접촉통합
ORDER BY
CR0306T.DLR_CD ASCDESC CR0306T.CONTACT_NO ASCDESC CR0306T.CUST_NO ASCDESC CR0306T.CONTACT_STAT_CD ASCDESC CR0306T.CONTACT_TP ASCDESC CR0306T.CONTACT_BIZ_CD ASCDESC CR0306T.CONTACT_CONT ASCDESC CR0306T.REF_TABLE_NM ASCDESC CR0306T.REF_KEY_NM ASCDESC CR0306T.REG_USR_ID ASCDESC CR0306T.REG_DT ASCDESC CR0306T.UPDT_USR_ID ASCDESC CR0306T.UPDT_DT ASCDESC
CR0306T.CONTACT_NO DESC
CR0306T.CONTACT_NO DESC
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

/* [chn.bhmc.dms.crm.sac.service.dao.ContactHistoryDAO.selectMsgHistorysByCondition] */
SELECT COUNT(*) AS VALUE
FROM CR_0306T CR0306T
