mapper.update:
/* [chn.bhmc.dms.crm.cso.service.dao.SalesOpptFailOtherBrandMgmtDAO.updateSalesOpptFailOtherBrand] */
    UPDATE CR_0220T
      SET
       INTR_CARLINE_CD                                       = #{intrCarlineCd}
       ,OTHER_BRAND_CARLINE_NM                    = #{otherBrandCarlineNm}
       ,USE_YN                                                       = #{useYn}
       ,UPDT_USR_ID                                              = #{updtUsrId}
       ,UPDT_DT                                                     = SYSDATE
    WHERE SEQ                                                    = #{seq}

mapper.insert:
 /* [chn.bhmc.dms.crm.cso.service.dao.SalesOpptFailOtherBrandMgmtDAO.insertSalesOpptFailOtherBrand] */
