  ,USE_YN
 ,REG_USR_ID         --등록자ID
 ,REG_DT             --등록일자
 ,UPDT_USR_ID
 ,UPDT_DT
)
VALUES
(
 SEQ_CR_0220T.NEXTVAL
 ,#{intrCarlineCd}
 ,#{otherBrandCarlineNm}
 ,#{useYn}
 ,#{regUsrId}
 ,SYSDATE
 ,#{updtUsrId}
 ,SYSDATE
)

mapper.select:
/* [chn.bhmc.dms.cmm.sci.service.dao.SalesOpptFailOtherBrandMgmtDAO.selectSalesOpptFailMgmtByCondition] */
     SELECT * FROM (
        SELECT ROWNUM AS RNUM, T1.* FROM (
            SELECT * FROM CR_0220T


                INTR_CARLINE_CD = #{sIntrCarlineCd}


        ORDER BY




                                                 INTR_CARLINE_CD ASCDESC            
                                  OTHER_BRAND_CARLINE_NM ASCDESC            
                                                            CR_0220T.USE_YN ASCDESC            




                CR_0220T.SEQ DESC


        ) T1


                ROWNUM <= #{lastIndex}


    )


            RNUM > #{firstIndex}

/* [chn.bhmc.dms.cmm.sci.service.dao.SalesOpptFailOtherBrandMgmtDAO.selectSalesOpptFailOtherBrandMgmtsByConditionCnt] */
SELECT COUNT(*) AS VALUE FROM CR_0220T


                INTR_CARLINE_CD = #{sIntrCarlineCd}
