mapper.sql:
INNER JOIN CR_0101T CR0101T ON CR0201T.CUST_NO = CR0101T.CUST_NO AND CR0201T.DLR_CD = CR0101T.DLR_CD AND CR0101T.DEL_YN = 'N'
LEFT OUTER JOIN CM_0801T SCT ON CR0101T.MNG_SC_ID = SCT.USR_ID
LEFT OUTER JOIN CM_0801T USRT ON CR0201T.REG_USR_ID = USRT.USR_ID

INNER JOIN CR_0203T CR0203T ON CR0201T.SALE_OPPT_NO = CR0203T.REF_KEY_NM AND CR0203T.REF_TABLE_NM = 'CR_0201T' AND CR0203T.FLAG_YN = 'Y'



    AND CR0201T.DLR_CD = #{sDlrCd}



        AND CR0101T.MNG_SC_ID = #{sScId}







                    AND CR0101T.CUST_NM LIKE #{sCustNm}


                AND CR0101T.CUST_NM LIKE #{sCustNm}






        AND CR0203T.CARLINE_CD = #{sIntrCarLine}


        AND CR0203T.MODEL_CD = #{sIntrModel}




        AND CR0201T.TRSF_STAT_CD = #{sTrsfStatCd}




        AND CR0201T.SALE_OPPT_STEP_CD = #{sSaleOpptStepCd}





        AND CR0201T.REG_DT >= #{sStartRegDt}




        AND CR0201T.REG_DT < #{sEndRegDt}





        AND CR0201T.CUST_NO = #{sCustNo}

mapper.select:
/* [chn.bhmc.dms.crm.cso.service.dao.SalesOpptMgmtDAO.selectSalesOpptMgmtsByConditionCnt] */

SELECT COUNT(*) AS VALUE
FROM CR_0201T CR0201T

/* [chn.bhmc.dms.crm.cso.service.dao.SalesOpptMgmtDAO.selectSalesOpptMgmtsByCondition] */ SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT CR0201T.DLR_CD --딜러코드 ,CR0201T.SALE_OPPT_NO --판매기회일련번호 ,CR0201T.LEAD_NO --리드번호 ,CR0201T.CUST_NO --고객번호 ,CR0101T.CUST_NM --고객명 ,CR0101T.CUST_CD --잠재/보유고객 ,CR0201T.SALE_OPPT_STEP_CD --판매기회단계코드 ,CR0201T.EXPC_SALE_DT --예상판매일자 ,CR0203T.CARLINE_CD ,FN_GET_CARLINE_NM(CR0203T.CARLINE_CD) AS CARLINE_NM ,CR0203T.MODEL_CD ,FN_GET_MODEL_NM(CR0203T.MODEL_CD) AS MODEL_NM ,CR0201T.SUCC_PRB_CD --성공확률코드 ,CR0201T.SALE_OPPT_SRC_CD --판매기회출처코드 ,CR0201T.HP_NO --휴대폰번호 ,CR0201T.REMARK --비고 ,CR0201T.TRSF_STAT_CD --전출상태코드 ,CR0201T.BHMC_YN --BHMC여부 ,CR0201T.REG_USR_ID --등록자ID ,USRT.USR_NM AS REG_USR_NM --등록자이름 ,CR0201T.REG_DT --등록일자
,CR0101T.MNG_SC_ID AS SC_ID --SCID ,SCT.USR_NM AS SC_NM --SC이름 FROM CR_0201T CR0201T
ORDER BY
CR0101T.CUST_NM ASCDESC CR0201T.REMARK ASCDESC CR0203T.CARLINE_CD ASCDESC CR0203T.MODEL_CD ASCDESC CR0201T.EXPC_SALE_DT ASCDESC CR0201T.SUCC_PRB_CD ASCDESC SCT.USR_NM ASCDESC CR0201T.REG_DT ASCDESC
CR0201T.SALE_OPPT_SRC_CD ASCDESC CR0201T.SALE_OPPT_STEP_CD ASCDESC CR0201T.TRSF_STAT_CD ASCDESC
CR0201T.SALE_OPPT_NO DESC
CR0201T.SALE_OPPT_NO DESC
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

/* [chn.bhmc.dms.crm.cso.service.dao.SalesOpptMgmtDAO.selectSalesOpptHistorysByConditionCnt] */
SELECT COUNT(*) AS VALUE
FROM CR_0201HT
WHERE SALE_OPPT_NO = #{sSaleOpptNo}
AND DLR_CD = #{sDlrCd}

/* [chn.bhmc.dms.crm.cso.service.dao.SalesOpptMgmtDAO.selectSalesOpptHistorysByCondition] */
SELECT * FROM (
    SELECT ROWNUM AS RNUM, T1.* FROM (
        SELECT
            *
        FROM CR_0201HT
        WHERE SALE_OPPT_NO = #{sSaleOpptNo}
        AND DLR_CD = #{sDlrCd}
        ORDER BY REG_DT DESC
    ) T1


            ROWNUM <= #{lastIndex}


)


        RNUM > #{firstIndex}

/* [chn.bhmc.dms.crm.cso.service.dao.SalesOpptMgmtDAO.selectSalesOpptDupCnt] */
SELECT  COUNT(*) AS VALUE
FROM  CR_0201T CR0201T
INNER JOIN CR_0101T CR0101T ON CR0201T.CUST_NO = CR0101T.CUST_NO AND CR0201T.DLR_CD = CR0101T.DLR_CD AND CR0101T.DEL_YN = 'N'
INNER JOIN CR_0203T CR0203T ON CR0201T.SALE_OPPT_NO = CR0203T.REF_KEY_NM AND CR0203T.REF_TABLE_NM = 'CR_0201T' AND CR0203T.FLAG_YN = 'Y'
WHERE CR0201T.DLR_CD  = #{sDlrCd}
AND CR0201T.CUST_NO = #{sCustNo}

AND CR0201T.SALE_OPPT_STEP_CD IN ('02','03','04','05')


AND CR0201T.REG_DT BETWEEN SYSDATE-#{sDupConfigDay} AND SYSDATE
