mapper.select:
/* [chn.bhmc.dms.crm.css.service.dao.SatisFactionResultMgmtDAO.selectSatisFactionResultMgmtByConditionCnt] */
SELECT  COUNT(T1.SEQ) AS VALUE
FROM    CR_0909T T1

/* [chn.bhmc.dms.crm.css.service.dao.SatisFactionResultMgmtDAO.selectSatisFactionResultMgmtByCondition] */ SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT T1.SEQ --일련번호 ,T1.TITLE_NM ,T1.DIST_DT ,DECODE(T1.DIST_DT,'','N','Y') AS DIST_YN ,T1.DISPLAY_YN ,T1.DLR_CD ,SCT.USR_NM AS REG_USR_NM ,T1.REG_USR_ID --등록자ID ,T1.REG_DT --등록일자 ,T1.UPDT_USR_ID --수정자ID ,T1.UPDT_DT --수정일자 FROM CR_0909T T1 LEFT OUTER JOIN CM_0801T SCT ON T1.REG_USR_ID = SCT.USR_ID
ORDER BY
T1.SEQ ASCDESC
T1.REG_DT DESC
T1.REG_DT DESC
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

/* [chn.bhmc.dms.crm.css.service.dao.SatisFactionResultMgmtDAO.selectSatisFactionResultDtlHeaderMgmtByCondition] */
SELECT  T1.HEADER_CONT
  FROM   CR_0910T T1
 WHERE   T1.UPPER_SEQ = #{sSeq}

    AND  T1.DLR_CD = #{sDlrCd}

 GROUP BY T1.HEADER_CONT
 ORDER BY min(T1.SEQ) ASC

/* [chn.bhmc.dms.crm.css.service.dao.SatisFactionResultMgmtDAO.selectSatisFactionResultDtlMgmtByConditionCnt] */
SELECT  COUNT(T1.SEQ) AS VALUE
FROM    CR_0910T T1

mapper.insert:
/* [chn.bhmc.dms.crm.css.service.dao.SatisFactionResultMgmtDAO.insertSatisFactionResultMgmt] */
SELECT SEQ_CR_0909T.currval AS SEQ FROM DUAL
INSERT INTO CR_0909T --BHMC만족도설문조사업로드 ( SEQ --일련번호 ,TITLE_NM --제목명 ,DIST_DT --배포일자 ,DISPLAY_YN --출력여부 ,DLR_CD --딜러코드 ,REG_USR_ID --등록자ID ,REG_DT --등록일자 ,UPDT_USR_ID --수정자ID ,UPDT_DT --수정일자 ) VALUES ( SEQ_CR_0909T.NEXTVAL ,#{titleNm} --제목명 ,#{distDt} --배포일자 ,#{displayYn} --출력여부 ,#{dlrCd} --딜러코드 ,#{regUsrId} --등록자ID ,SYSDATE ,#{updtUsrId} --수정자ID ,SYSDATE )
