/* [chn.bhmc.dms.crm.dmm.service.dao.BlueMembershipCalcDAO.selectBlueMembershipCalcsByConditionCnt] */
SELECT  COUNT(*) AS VALUE
FROM (
        SELECT  COUNT(*)
        FROM    CR_0101T A INNER JOIN CR_0808T B
                ON A.DLR_CD = B.DLR_CD
                AND A.CUST_NO = B.CUST_NO
                AND A.BLUE_MEMBERSHIP_NO = B.BLUE_MEMBERSHIP_NO
        WHERE   B.POINT_TP = '+'
        AND     A.DLR_CD = #{sDlrCd}


                AND B.OCCR_DT >= #{sOccrDtFrom}




                AND B.OCCR_DT < #{sOccrDtTo}



            AND     A.CUST_NO = #{sCustNo}

        UNION ALL
        SELECT  COUNT(*)
        FROM    CR_0101T A INNER JOIN CR_0808T B
                ON A.DLR_CD = B.DLR_CD
                AND A.CUST_NO = B.CUST_NO
                AND A.BLUE_MEMBERSHIP_NO = B.BLUE_MEMBERSHIP_NO
        WHERE   B.POINT_TP = '-'
        AND     A.DLR_CD = #{sDlrCd}


                AND B.OCCR_DT >= #{sOccrDtFrom}




                AND B.OCCR_DT < #{sOccrDtTo}



            AND     A.CUST_NO = #{sCustNo}

)

/* [chn.bhmc.dms.crm.dmm.service.dao.BlueMembershipCalcDAO.selectBlueMembershipCalcsByCondition] */ SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT DLR_CD ,SUM(SAVE_POINT_VAL) AS TOTAL_SAVE_POINT_VAL ,SUM(EMPLOY_POINT_VAL) AS TOTAL_EMPLOY_POINT_VAL ,TO_CHAR(OCCR_DT,'YYYY-MM') AS CALCULATE_MONTH FROM ( SELECT A.DLR_CD ,OCCR_POINT_VAL AS SAVE_POINT_VAL ,0 AS EMPLOY_POINT_VAL ,B.OCCR_DT AS OCCR_DT FROM CR_0101T A INNER JOIN CR_0808T B ON A.DLR_CD = B.DLR_CD AND A.CUST_NO = B.CUST_NO AND A.BLUE_MEMBERSHIP_NO = B.BLUE_MEMBERSHIP_NO WHERE B.POINT_TP = '+' AND A.DLR_CD = #{sDlrCd}
AND B.OCCR_DT >= #{sOccrDtFrom}
AND B.OCCR_DT < #{sOccrDtTo}
AND A.CUST_NO = #{sCustNo}
UNION ALL SELECT A.DLR_CD ,0 AS SAVE_POINT_VAL ,OCCR_POINT_VAL AS EMPLOY_POINT_VAL ,B.OCCR_DT AS OCCR_DT FROM CR_0101T A INNER JOIN CR_0808T B ON A.DLR_CD = B.DLR_CD AND A.CUST_NO = B.CUST_NO AND A.BLUE_MEMBERSHIP_NO = B.BLUE_MEMBERSHIP_NO WHERE B.POINT_TP = '-' AND A.DLR_CD = #{sDlrCd}
AND B.OCCR_DT >= #{sOccrDtFrom}
AND B.OCCR_DT < #{sOccrDtTo}
AND A.CUST_NO = #{sCustNo}
) GROUP BY DLR_CD, TO_CHAR(OCCR_DT,'YYYY-MM') ORDER BY
CALCULATE_MONTH ASCDESC TOTAL_SAVE_POINT_VAL ASCDESC TOTAL_EMPLOY_POINT_VAL ASCDESC
CALCULATE_MONTH DESC
CALCULATE_MONTH DESC
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

/* [chn.bhmc.dms.crm.dmm.service.dao.BlueMembershipCalcDAO.selectBlueMembershipCalcDetailsByConditionCnt] */
SELECT  COUNT(*) AS VALUE
FROM    CR_0101T A INNER JOIN CR_0808T B
        ON A.DLR_CD = B.DLR_CD
        AND A.CUST_NO = B.CUST_NO
        AND A.BLUE_MEMBERSHIP_NO = B.BLUE_MEMBERSHIP_NO
WHERE   A.DLR_CD = #{sDlrCd}


        AND B.OCCR_DT >= #{sOccrDtFrom}




        AND B.OCCR_DT < #{sOccrDtTo}



    AND     A.CUST_NO = #{sCustNo}

/* [chn.bhmc.dms.crm.dmm.service.dao.BlueMembershipCalcDAO.selectBlueMembershipCalcDetailsByCondition] */ SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT A.DLR_CD ,A.CUST_NM ,B.POINT_HIS_SEQ ,B.BLUE_MEMBERSHIP_NO ,B.CUST_NO ,B.POINT_CD ,B.POINT_TP ,B.OCCR_POINT_VAL ,'('||B.POINT_TP||') '||TO_CHAR(B.OCCR_POINT_VAL,'FM9,999,999,999') AS OCCR_POINT_VALADD ,B.OCCR_DT ,B.PRSN_DEPT_NM ,B.PRSN_ID ,C.USR_NM AS PRSN_NM ,B.REG_USR_ID ,B.REG_DT ,B.UPDT_USR_ID ,B.UPDT_DT FROM CR_0101T A INNER JOIN CR_0808T B ON A.DLR_CD = B.DLR_CD AND A.CUST_NO = B.CUST_NO AND A.BLUE_MEMBERSHIP_NO = B.BLUE_MEMBERSHIP_NO LEFT OUTER JOIN CM_0801T C ON C.USR_ID = B.PRSN_ID WHERE A.DLR_CD = #{sDlrCd}
AND B.OCCR_DT >= #{sOccrDtFrom}
AND B.OCCR_DT < #{sOccrDtTo}
AND A.CUST_NO = #{sCustNo}
ORDER BY
B.OCCR_DT ASCDESC A.CUST_NM ASCDESC B.POINT_CD ASCDESC B.POINT_TP ASCDESC B.OCCR_POINT_VAL ASCDESC
B.OCCR_DT DESC
B.OCCR_DT DESC
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}
