mapper.insert:
/* [chn.bhmc.dms.crm.dmm.service.dao.MembershipTransDAO.insertMembershipTrans] */
    INSERT INTO CR_0806T    --양도양수마스터
    (
        DLR_CD
        ,TRSF_RECEIVE_SEQ
        ,TRSF_MEMBERSHIP_NO
        ,RECEIVE_MEMBERSHIP_NO
        ,POINT_VAL
        ,TRSF_REASON_CD
        ,TRSF_RECEIVE_REL_CD
        ,FILE_DOC_NO
        ,SIGN_DOC_NO
        ,USE_YN
        ,REG_USR_ID
        ,REG_DT
    )
    VALUES
    (
        #{dlrCd}
        ,SEQ_CR_0806T.NEXTVAL
        ,#{trsfMembershipNo}
        ,#{receiveMembershipNo}
        ,#{pointVal}
        ,#{trsfReasonCd}
        ,#{trsfReceiveRelCd}
        ,#{fileDocNo}
        ,#{signDocNo}
        ,#{useYn}
        ,#{regUsrId}
        ,SYSDATE
    )

        SELECT SEQ_CR_0806T.currval AS TRSF_RECEIVE_SEQ FROM DUAL

mapper.select:
/* [chn.bhmc.dms.crm.dmm.service.dao.MembershipTransDAO.selectMembershipTransMngsByCondition] */ SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT * FROM ( SELECT A.DLR_CD ,D.CUST_NO AS TRSF_CUST_NO ,D.CUST_NM AS TRSF_CUST_NM ,E.CUST_NO AS RECEIVE_CUST_NO ,E.CUST_NM AS RECEIVE_CUST_NM ,A.TRSF_RECEIVE_SEQ ,A.TRSF_MEMBERSHIP_NO ,A.RECEIVE_MEMBERSHIP_NO ,A.POINT_VAL ,A.TRSF_REASON_CD ,A.TRSF_RECEIVE_REL_CD ,A.FILE_DOC_NO ,A.SIGN_DOC_NO ,A.REG_USR_ID ,F.SIGN_END_DT ,F.SIGN_STAT_CD ,F.TEMP_SAVE_YN ,F.REQ_EMP_NO ,F.SIGN_DOC_ID ,F.REF_DOC_PARAMS ,F.REQ_USR_NM ,F.REQ_DT ,F.REQ_CONT ,F.REQ_USR_ID ,F.REQ_TITLE_NM FROM CR_0806T A INNER JOIN CM_1001T F ON A.SIGN_DOC_NO = F.SIGN_DOC_NO INNER JOIN CR_0801T B ON A.DLR_CD = B.DLR_CD AND A.TRSF_MEMBERSHIP_NO = B.MEMBERSHIP_NO INNER JOIN CR_0801T C ON A.DLR_CD = B.DLR_CD AND A.RECEIVE_MEMBERSHIP_NO = C.MEMBERSHIP_NO INNER JOIN CR_0101T D ON B.DLR_CD = D.DLR_CD AND B.CUST_NO = D.CUST_NO INNER JOIN CR_0101T E ON C.DLR_CD = E.DLR_CD AND C.CUST_NO = E.CUST_NO
ORDER BY
D.CUST_NM ASCDESC E.CUST_NM ASCDESC A.POINT_VAL ASCDESC A.REG_USR_ID ASCDESC
TRSF_RECEIVE_SEQ DESC
TRSF_RECEIVE_SEQ DESC
) ) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

mapper.update:
/* [chn.bhmc.dms.crm.dmm.service.dao.MembershipTransDAO.updateMembershipTransPointVal] */
    UPDATE  CR_0806T
    SET     POINT_VAL = #{pointVal}
            ,TRSF_REASON_CD = #{trsfReasonCd}
            ,TRSF_RECEIVE_REL_CD = #{trsfReceiveRelCd}
    WHERE   DLR_CD = #{dlrCd}
    AND     TRSF_RECEIVE_SEQ = #{trsfReceiveSeq}

mapper.select:
/* [chn.bhmc.dms.crm.dmm.service.dao.MembershipTransDAO.selectMembershipTransMngByKey] */ SELECT A.DLR_CD ,A.TRSF_RECEIVE_SEQ ,A.TRSF_MEMBERSHIP_NO ,A.RECEIVE_MEMBERSHIP_NO ,A.POINT_VAL ,A.TRSF_REASON_CD ,A.TRSF_RECEIVE_REL_CD ,A.FILE_DOC_NO ,A.SIGN_DOC_NO ,A.REG_USR_ID ,F.SIGN_END_DT ,F.SIGN_STAT_CD ,F.TEMP_SAVE_YN ,F.REQ_EMP_NO ,F.SIGN_DOC_ID ,F.REF_DOC_PARAMS ,F.REQ_USR_NM ,F.REQ_DT ,F.REQ_CONT ,F.REQ_USR_ID ,F.REQ_TITLE_NM FROM CR_0806T A INNER JOIN CM_1001T F ON A.SIGN_DOC_NO = F.SIGN_DOC_NO
A.DLR_CD = #{sDlrCd}
AND A.SIGN_STAT_CD = #{sSignStatCd}
AND A.SIGN_DOC_NO = #{sSignDocNo}
AND A.SIGN_DOC_ID = #{sSignDocId}
