mapper.select:
/* [chn.bhmc.dms.crm.slm.service.dao.LeadInfoDAO.selectLeadInfoByCondition] */ SELECT * FROM ( SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT T1.* , FN_GET_CARLINE_NM(T5.CARLINE_CD) AS CARLINE_NM -- 관심차종 이름 , FN_GET_MODEL_NM(T5.MODEL_CD) AS MODEL_NM -- 관심모델 이름 , SCT.USR_NM AS MNG_SC_NM , T2.CUST_NM , T2.CUST_CD , T2.PREF_COMM_NO , T2.PREF_COMM_MTH_CD , T2.CUST_TP , T2.HP_NO AS CUST_HP_NO , T2.OFFICE_TEL_NO , T2.MNG_SC_ID , T3.USR_NM AS REG_USR_NM , T4.USR_NM AS PRSN_NM , (SELECT COUNT(*) FROM CR_0605T WHERE CALL_LIST_CD = 'IDCCLEAD' AND CALL_LIST_REQ_NO = T1.LEAD_NO) AS CALL_COUNT , (SELECT MAX(B.STAT_CD) KEEP (DENSE_RANK FIRST ORDER BY B.CALL_LIST_NO DESC) FROM CR_0605T B WHERE B.CALL_LIST_CD = #{sCallListCd} AND B.CALL_LIST_REQ_NO = T1.LEAD_NO ) AS CALL_STAT_CD , (SELECT MAX(B.NEXT_CALL_DT) KEEP (DENSE_RANK FIRST ORDER BY B.CALL_LIST_NO DESC) FROM CR_0605T B WHERE B.CALL_LIST_CD = #{sCallListCd} AND B.CALL_LIST_REQ_NO = T1.LEAD_NO ) AS NEXT_CALL_DT FROM CR_0202T T1 INNER JOIN CR_0101T T2 ON T1.DLR_CD = T2.DLR_CD AND T1.CUST_NO = T2.CUST_NO AND T2.DEL_YN='N' LEFT OUTER JOIN CM_0801T T3 ON T1.DLR_CD = T3.DLR_CD AND T1.REG_USR_ID = T3.USR_ID LEFT OUTER JOIN CM_0801T T4 ON T1.DLR_CD = T4.DLR_CD AND T1.PRSN_ID = T4.USR_ID LEFT OUTER JOIN CR_0203T T5 ON T1.DLR_CD = T5.DLR_CD AND T5.REF_TABLE_NM = 'CR_0202T' AND T5.FLAG_YN = 'Y' AND T1.LEAD_NO = T5.REF_KEY_NM LEFT OUTER JOIN CM_0801T SCT ON T2.MNG_SC_ID = SCT.USR_ID
ORDER BY
T1.LEAD_TP ASCDESC T2.CUST_NM ASCDESC CUST_HP_NO ASCDESC T2.TEL_NO ASCDESC T1.HP_NO ASCDESC T1.LEAD_SRC_CD ASCDESC T1.BHMC_YN ASCDESC PRSN_NM ASCDESC REG_USR_NM ASCDESC MNG_SC_NM ASCDESC T1.STAT_CD ASCDESC T1.REG_DT ASCDESC
T1.REG_DT DESC
T1.REG_DT DESC
) T1
)
AND ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

/* [chn.bhmc.dms.crm.slm.service.dao.LeadInfoDAO.selectLeadDupByKey] */
SELECT  COUNT(*) AS VALUE
  FROM  CR_0202T T1
 WHERE  T1.DLR_CD  = #{dlrCd}

   AND  T1.CUST_NO = #{custNo}


   AND  T1.LEAD_TP = #{leadTp}


   AND  T1.STAT_CD IN ('01','02')


   AND  T1.REG_DT BETWEEN SYSDATE-#{leadDupConfigDay} AND SYSDATE

/* [chn.bhmc.dms.crm.slm.service.dao.LeadInfoDAO.selectNextLeadNo] */
SELECT FU_GET_LEAD_NO() AS LEAD_NO
FROM   DUAL
