mapper.select:
/* [chn.bhmc.dms.mis.tmp.service.dao.TargetReferSameBsnsDAO.selectTargetReferSameBsnsMainSearchCnt] */
SELECT COUNT(*) AS VALUE FROM MS_0106T
WHERE REF_YY_MM_DD = '99999999'

/* [chn.bhmc.dms.mis.tmp.service.dao.TargetReferSameBsnsDAO.selectTargetReferSameBsnsMainSearch] */ SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM (
SELECT SUBSTR(REF_YY_MM_DD, 1, 4) ||'-'|| SUBSTR(REF_YY_MM_DD, 5, 2) ||'-'|| SUBSTR(REF_YY_MM_DD, 7, 2) AS referDt ,SUBSTR(REF_YY_MM_DD, 1, 4) AS referYearDt ,SUBSTR(REF_YY_MM_DD, 5, 2) AS referMonthDt ,SUBSTR(REF_YY_MM_DD, 7, 2) AS referDayDt ,VSIT_REQ_RATE AS visitReqVal ,VSIT_DEAL_RATE AS visitDealVal ,TOT_DEAL_RATE AS totDealVal ,PRSN_INFO_OFFER_RATE AS perOfterVal ,SUCC_RATE AS successVal ,GOAL_ACHV_RATE AS targetAchVal ,GOODS_CAR_QTY AS goodsCarQty ,INS_APPLY_RATE AS insApplyRate ,FINC_APPLY_RATE AS fincApplyRate FROM MS_0106T WHERE REF_YY_MM_DD = '99999999' ) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

mapper.insert:
/* [chn.bhmc.dms.mis.tmp.service.dao.TargetReferSameBsnsDAO.insertReferSameBsns] */
INSERT INTO MS_0106T(
     REF_YY_MM_DD
    ,VSIT_REQ_RATE
    ,VSIT_DEAL_RATE
    ,TOT_DEAL_RATE
    ,PRSN_INFO_OFFER_RATE
    ,SUCC_RATE
    ,GOAL_ACHV_RATE
    ,REG_USR_ID
    ,REG_DT
    ,UPDT_USR_ID
    ,UPDT_DT
    ,GOODS_CAR_QTY
    ,INS_APPLY_RATE
    ,FINC_APPLY_RATE
)VALUES(
      #{referDt}
    , #{visitReqVal}
    , #{visitDealVal}
    , #{totDealVal}
    , #{perOfterVal}
    , #{successVal}
    , #{targetAchVal}
    , #{regUsrId}
    , SYSDATE
    , #{regUsrId}
    , SYSDATE
    , #{goodsCarQty}
    , #{insApplyRate}
    , #{fincApplyRate}
)

mapper.update:
/* [chn.bhmc.dms.mis.tmp.service.dao.TargetReferSameBsnsDAO.updateReferSameBsns] */
UPDATE MS_0106T SET
    VSIT_REQ_RATE           = #{visitReqVal}
    , VSIT_DEAL_RATE        = #{visitDealVal}
    , TOT_DEAL_RATE         = #{totDealVal}
    , PRSN_INFO_OFFER_RATE  = #{perOfterVal}
    , SUCC_RATE             = #{successVal}
    , GOAL_ACHV_RATE        = #{targetAchVal}
    , UPDT_USR_ID           = #{updtUsrId}
    , UPDT_DT               = SYSDATE
    , GOODS_CAR_QTY         = #{goodsCarQty}
    , INS_APPLY_RATE        = #{insApplyRate}
    , FINC_APPLY_RATE       = #{fincApplyRate}
WHERE REF_YY_MM_DD  = '99999999'

