mapper.update:
UPDATE SA_0117T
   SET FINC_CMP_CD = #{fincCmpCd}
     , FINC_ITEM_NM = #{fincItemNm}
     , FINC_ITEM_CD = #{fincItemCd}
     , FINC_ITEM_TP = #{fincItemTp}
     , FINC_ITEM_PRID = #{fincItemPrid}
     , CREDIT_GRADE_CD = #{creditGradeCd}
     , CUST_TP = #{custTp}
     , CONTRACT_AMT = #{contractAmt}
     , PREPAY_RATE = #{prepayRate}
     , PREPAY_DL_AMT = #{prepayDlAmt}
     , LATEPAY_DL_AMT = #{latepayDlAmt}
     , CASH_PRC = #{cashPrc}
     , MM_PAY_AMT_RATE = #{mmPayAmtRate}
     , REM_INTR_RATE = #{remIntrRate}
     , INST_INTR_RATE = #{instIntrRate}
     , HDLG_FEE_RATE = #{hdlgFeeRate}
     , HDLG_FEE_AMT = #{hdlgFeeAmt}
     , GRTE_INCA_RATE = #{grteIncaRate}
     , GRTE_INC_AMT = #{grteIncAmt}
     , STAMP_AMT = #{stampAmt}
     , AUTH_AMT = #{authAmt}
     , INST_SBODT_AMT = #{instSbodtAmt}
     , BP_CD = #{bpCd}
     , CONTRACT_AMT_RATE = #{contractAmtRate}
     , USE_YN = #{useYn}
     , FINC_ITEM_KIND_CD = #{fincItemKindCd}
     , UPDT_USR_ID = #{updtUsrId}
     , UPDT_DT = SYSDATE
 WHERE DLR_CD = #{dlrCd}
   AND FINC_CMP_CD = #{fincCmpCd}
   AND FINC_ITEM_CD = #{fincItemCd}

mapper.select:
SELECT COUNT(*) AS VALUE
  FROM SA_0117T
 WHERE 1=1

   AND DLR_CD = #{sDlrCd}


   AND FINC_CMP_CD = #{sFincCmpCd}


   AND FINC_ITEM_TP = #{sFincItemTp}

SELECT * FROM ( SELECT ROWNUM AS RNUM , T1.* FROM ( SELECT DLR_CD , FINC_CMP_CD , FINC_ITEM_NM , FINC_ITEM_CD , FINC_ITEM_TP , FINC_ITEM_PRID , CREDIT_GRADE_CD , CUST_TP , CONTRACT_AMT , PREPAY_RATE , PREPAY_DL_AMT , LATEPAY_DL_AMT , CASH_PRC , MM_PAY_AMT_RATE , REM_INTR_RATE , INST_INTR_RATE , HDLG_FEE_RATE , HDLG_FEE_AMT , GRTE_INCA_RATE , GRTE_INC_AMT , STAMP_AMT , AUTH_AMT , INST_SBODT_AMT , BP_CD , CONTRACT_AMT_RATE , USE_YN , FINC_ITEM_KIND_CD , REG_USR_ID , REG_DT , UPDT_USR_ID , UPDT_DT FROM SA_0117T WHERE 1=1
AND DLR_CD = #{sDlrCd}
AND FINC_CMP_CD = #{sFincCmpCd}
AND FINC_ITEM_TP = #{sFincItemTp}
ORDER BY
FINC_CMP_CD ASCDESC FINC_ITEM_NM ASCDESC FINC_ITEM_TP ASCDESC FINC_ITEM_PRID ASCDESC
FINC_ITEM_NM ASC
FINC_ITEM_NM ASC
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

/* [chn.bhmc.dms.sal.bsc.service.dao.FinancialProductsManagementDAO.selectFincTargetModelSearchsByCondition] */
SELECT COUNT(*)
  FROM SA_0174T      /* 금융상품대상모델 */
 WHERE DLR_CD      = #{sDlrCd}
   AND FINC_CMP_CD = #{sFincCmpCd}
   AND FINC_ITEM_CD = #{sFincItemCd}

mapper.delete:
/* [chn.bhmc.dms.sal.bsc.service.dao.FinancialProductsManagementDAO.deleteFincModel] */
DELETE
  FROM SA_0174T    --금융상품대상모델
 WHERE DLR_CD      = #{dlrCd}             --딜러코드
   AND FINC_CMP_CD  = #{fincCmpCd}
   AND FINC_ITEM_CD = #{fincItemCd}
   AND MODEL_CD    = #{modelCd}           --모델코드

mapper.select:
SELECT FINC_CMP_CD , FINC_ITEM_CD , FINC_ITEM_NM , INST_INTR_RATE , GRTE_INC_AMT , HDLG_FEE_AMT FROM SA_0117T WHERE DLR_CD = #{sDlrCd}
AND FINC_ITEM_KIND_CD = #{sFincItemKindCd}
AND USE_YN = 'Y' ORDER BY FINC_CMP_CD ASC, FINC_ITEM_CD ASC
