mapper.select:
SELECT COUNT(*) AS VALUE FROM (SELECT SALE_DLR_SUNG_CD, SALE_DLR_SUNG_NM FROM (SELECT DISTINCT (CM0101.SALE_DLR_SUNG_CD) AS SALE_DLR_SUNG_CD, (SELECT CSTL_NM FROM CM_2701T WHERE CSTL_CD = CM0101.SALE_DLR_SUNG_CD) AS SALE_DLR_SUNG_NM, CM0101.SDPT_CD FROM CM_0101T CM0101 WHERE CM0101.SALE_DLR_SUNG_CD <> 'ZQ' AND CM0101.SALE_DLR_SUNG_CD IS NOT NULL
AND CM0101.SDPT_CD = #{sSdptCd}
AND CM0101.DIST_OFFICE_CD = #{sDistOfficeCd}
) WHERE SALE_DLR_SUNG_NM IS NOT NULL )

SELECT SALE_DLR_SUNG_CD, SALE_DLR_SUNG_NM FROM (SELECT DISTINCT (CM0101.SALE_DLR_SUNG_CD) AS SALE_DLR_SUNG_CD, (SELECT CSTL_NM FROM CM_2701T WHERE CSTL_CD = CM0101.SALE_DLR_SUNG_CD) AS SALE_DLR_SUNG_NM, CM0101.SDPT_CD FROM CM_0101T CM0101 WHERE CM0101.SALE_DLR_SUNG_CD <> 'ZQ' AND CM0101.SALE_DLR_SUNG_CD IS NOT NULL
AND CM0101.SDPT_CD = #{sSdptCd}
AND CM0101.DIST_OFFICE_CD = #{sDistOfficeCd}
) WHERE SALE_DLR_SUNG_NM IS NOT NULL ORDER BY SALE_DLR_SUNG_CD ASC

SELECT COUNT(*) AS VALUE FROM (SELECT SALE_DLR_CITY_NM FROM (SELECT DISTINCT (CM0101.SALE_DLR_CITY_CD) AS SALE_DLR_CITY_CD, (SELECT CITY_NM FROM CM_3201T WHERE CSTL_CD = CM0101.SALE_DLR_SUNG_CD AND CITY_CD = CM0101.SALE_DLR_CITY_CD) AS SALE_DLR_CITY_NM, CM0101.SDPT_CD, CM0101.DIST_OFFICE_CD, CM0101.SALE_DLR_SUNG_CD FROM CM_0101T CM0101 WHERE 1 = 1 AND CM0101.SALE_DLR_CITY_CD IS NOT NULL
AND CM0101.SDPT_CD = #{sSdptCd}
AND CM0101.DIST_OFFICE_CD = #{sDistOfficeCd}
AND CM0101.SALE_DLR_SUNG_CD = #{sSaleDlrSungCd}
) WHERE SALE_DLR_CITY_NM IS NOT NULL)

SELECT SALE_DLR_CITY_CD, SALE_DLR_CITY_NM FROM (SELECT DISTINCT (CM0101.SALE_DLR_CITY_CD) AS SALE_DLR_CITY_CD, (SELECT CITY_NM FROM CM_3201T WHERE CSTL_CD = CM0101.SALE_DLR_SUNG_CD AND CITY_CD = CM0101.SALE_DLR_CITY_CD) AS SALE_DLR_CITY_NM, CM0101.SDPT_CD, CM0101.DIST_OFFICE_CD, CM0101.SALE_DLR_SUNG_CD FROM CM_0101T CM0101 WHERE 1 = 1 AND CM0101.SALE_DLR_CITY_CD IS NOT NULL
AND CM0101.SDPT_CD = #{sSdptCd}
AND CM0101.DIST_OFFICE_CD = #{sDistOfficeCd}
AND CM0101.SALE_DLR_SUNG_CD = #{sSaleDlrSungCd}
) WHERE SALE_DLR_CITY_NM IS NOT NULL ORDER BY SALE_DLR_CITY_CD ASC

SELECT COUNT(*) AS VALUE
  FROM (
        SELECT DLR_CD,DLR_NM
          FROM CM_0101T CM0101
         WHERE 1=1
           AND CM0101.SDPT_CD = #{sSdptCd}
           AND CM0101.DIST_OFFICE_CD = #{sDistOfficeCd}
           AND CM0101.SALE_DLR_SUNG_CD = #{sSaleDlrSungCd}
           AND CM0101.SALE_DLR_CITY_CD = #{sSaleDlrCityCd}
       )

SELECT DLR_CD,DLR_NM
  FROM CM_0101T CM0101
 WHERE 1=1
   AND CM0101.SDPT_CD = #{sSdptCd}
   AND CM0101.DIST_OFFICE_CD = #{sDistOfficeCd}
   AND CM0101.SALE_DLR_SUNG_CD = #{sSaleDlrSungCd}
   AND CM0101.SALE_DLR_CITY_CD = #{sSaleDlrCityCd}

SELECT NVL(MAX(EVAL_GRADE_CD),0) AS EVAL_GRADE_CD
  FROM SA_0156T
 WHERE 1=1
   AND DLR_CD = #{dlrCd}
   AND USR_ID = #{usrId}
   AND WRK_DSTIN_CD = #{wrkDstinCd}
