mapper.select:
/* [chn.bhmc.dms.sal.cnt.service.dao.ContractPackageDAO.selectContractPackagesByConditionCnt] */
     SELECT COUNT(*)
      FROM SA_0231T A    --집단계약헤더
      LEFT OUTER JOIN SA_0132T B           /* 2급딜러 */
        ON A.LV2_DLR_ORG_CD = B.LV2_DLR_CD


         AND A.DLR_CD           = #{sDlrCd}


         AND A.CONTRACT_TP      = #{sContractTp}


         AND A.SALE_EMP_NO      = #{sSaleEmpNo}


         AND A.CONTRACT_CUST_NO = #{sContractCustNo}


         AND A.CUST_TP          = #{sCustTp}





               AND A.GRP_CONTRACT_NO LIKE #{sGrpContractNo}


             AND A.GRP_CONTRACT_NO = #{sGrpContractNo}




         AND A.CONTRACT_STAT_CD = #{sContractStatCd}


         AND A.CONTRACT_DT  >=  #{sStartContractDt}


         AND A.CONTRACT_DT  <=  #{sEndContractDt}

/* [chn.bhmc.dms.sal.cnt.service.dao.ContractPackageDAO.selectContractPackagesByCondition] */ SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT A.DLR_CD --딜러코드 , A.GRP_CONTRACT_NO --그룹계약번호 , A.CONTRACT_TP --계약유형 , A.CONTRACT_STAT_CD --계약상태코드 , A.CONTRACT_DT --계약일자 , A.SALE_EMP_NO --판매사원번호 , C.USR_NM AS SALE_EMP_NM , A.SALE_MNG_NM --판매관리번호 , A.CONTRACT_CUST_NO --계약고객번호 , A.CUST_NM --고객명 , A.CUST_TP --고객유형 , A.TEL_NO1 --전화번호1 , A.TEL_NO2 --전화번호2 , A.MATH_DOC_TP --주요인증문서유형 , A.SSN_CRN_NO --주민사업자등록번호 , A.DL_REQ_DT --인도요청일자 , A.CONFIRM_DT --확정일자 , A.CANC_DT --취소일자 , A.SALE_OPPT_SEQ --판매기회번호 , A.PAY_TP --지불유형 , A.CARLINE_CNT --차종수량 , A.CAR_CNT --차량수량 , A.TOT_SALE_AMT --총판매금액 , A.LV2_DLR_YN --2급딜러여부 , A.LV2_DLR_ORG_CD --2급딜러조직코드 , B.LV2_DLR_NM AS LV2_DLR_ORG_NM , A.REMARK --비고 FROM SA_0231T A --집단계약헤더 LEFT OUTER JOIN SA_0132T B /* 2급딜러 */ ON A.LV2_DLR_ORG_CD = B.LV2_DLR_CD LEFT OUTER JOIN CM_0801T C /* 사원 */ ON A.SALE_EMP_NO = C.USR_ID AND A.DLR_CD = C.DLR_CD
AND A.DLR_CD = #{sDlrCd}
AND A.CONTRACT_TP = #{sContractTp}
AND A.SALE_EMP_NO = #{sSaleEmpNo}
AND A.CONTRACT_CUST_NO = #{sContractCustNo}
AND A.CUST_TP = #{sCustTp}
AND A.GRP_CONTRACT_NO LIKE #{sGrpContractNo}
AND A.GRP_CONTRACT_NO = #{sGrpContractNo}
AND A.CONTRACT_STAT_CD = #{sContractStatCd}
AND A.CONTRACT_DT >= #{sStartContractDt}
AND A.CONTRACT_DT <= #{sEndContractDt}
ORDER BY
A.GRP_CONTRACT_NO ASCDESC A.CONTRACT_DT ASCDESC A.CONTRACT_TP ASCDESC A.CONTRACT_STAT_CD ASCDESC A.CONTRACT_CUST_NO ASCDESC A.CUST_NM ASCDESC A.SALE_EMP_NO ASCDESC A.CARLINE_CNT ASCDESC A.CAR_CNT ASCDESC A.TOT_SALE_AMT ASCDESC
A.DLR_CD ASC, A.GRP_CONTRACT_NO DESC
A.DLR_CD ASC, A.GRP_CONTRACT_NO DESC
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

mapper.update:
/* [chn.bhmc.dms.sal.cnt.service.dao.ContractPackageDAO.deleteContractPackage] */
    UPDATE SA_0231T
       SET CONTRACT_STAT_CD = '90'
         , UPDT_USR_ID      = #{updtUsrId}              --수정자ID
         , UPDT_DT          = SYSDATE
     WHERE DLR_CD = #{dlrCd}
       AND GRP_CONTRACT_NO = #{grpContractNo}

mapper.select:
/* [chn.bhmc.dms.sal.cnt.service.dao.ContractPackageDAO.selectPackageItemSearchCnt] */
    SELECT COUNT(*)
      FROM SA_0232T A    --집단계약차종


         AND A.DLR_CD          = #{sDlrCd}


         AND A.GRP_CONTRACT_NO = #{sGrpContractNo}

/* [chn.bhmc.dms.sal.cnt.service.dao.ContractPackageDAO.selectPackageItemSearch] */ SELECT A.DLR_CD --딜러코드 , A.GRP_CONTRACT_NO --그룹계약번호 , A.SEQ --일련번호 , A.CARLINE_CD --차종코드 , FN_GET_CARLINE_NM(A.CARLINE_CD) AS CARLINE_NM , A.MODEL_CD --모델코드 , FN_GET_MODEL_NM(A.MODEL_CD) AS MODEL_NM , A.OCN_CD --OCN코드 , FN_GET_OCN_NM(A.MODEL_CD, A.OCN_CD) AS OCN_NM , A.EXT_COLOR_CD --외장색상코드 , FN_GET_EXT_COLOR_NM(A.EXT_COLOR_CD) AS EXT_COLOR_NM , A.INT_COLOR_CD --내장색상코드 , FN_GET_INT_COLOR_NM(A.INT_COLOR_CD) AS INT_COLOR_NM , A.RETL_PRC --소매가격 , A.PROMOTION_NO --프로모션코드 , A.PROMOTION_AMT --프로모션금액 , A.CAR_CNT --차량수량 , A.REAL_CAR_AMT --실제차량금액 , A.REAL_PAY_AMT --실제지불금액 FROM SA_0232T A --집단계약차종
AND A.DLR_CD = #{sDlrCd}
AND A.GRP_CONTRACT_NO = #{sGrpContractNo}
ORDER BY
A.CARLINE_CD ASCDESC A.MODEL_CD ASCDESC A.OCN_CD ASCDESC A.EXT_COLOR_CD ASCDESC A.INT_COLOR_CD ASCDESC A.RETL_PRC ASCDESC A.PROMOTION_NO ASCDESC A.PROMOTION_AMT ASCDESC A.CAR_CNT ASCDESC A.REAL_CAR_AMT ASCDESC A.REAL_PAY_AMT ASCDESC
A.DLR_CD ASC , A.GRP_CONTRACT_NO ASC , A.SEQ DESC
A.DLR_CD ASC , A.GRP_CONTRACT_NO ASC , A.SEQ DESC

/* [chn.bhmc.dms.sal.cnt.service.dao.ContractPackageDAO.selectCntPackagePromotionSearchCnt] */ SELECT COUNT(*) FROM ( SELECT SA0113.DLR_CD , SA0113.PROMOTION_CD , SA0113.PROMOTION_NM , SA0113.PROMOTION_TP /* SAL010 프로모션유형 */ , SA_0102.CARLINE_CD , SA_0115.MODEL_CD , SA0113.START_DT , SA0113.END_DT , SA0113.APPLY_TP -- 비율(R) , 금액(F) , SA0113.PROMOTION_AMT , SA0113.IMS_BUDN_RATE -- 제조사부담율 , SA0113.DLR_BUDN_RATE -- 딜러부담율 , SA0113.REG_USR_ID , SA0113.REG_DT , SA0113.UPDT_USR_ID , SA0113.UPDT_DT , SA0116.CONTRACT_TP -- N: 일반판매, P:선출고판매, Q:집단판매 FROM SA_0113T SA0113 /* 프로모션 헤더 */ INNER JOIN SA_0115T SA_0115 /* 프로모션 모델 */ ON SA0113.DLR_CD = SA_0115.DLR_CD AND SA0113.PROMOTION_CD = SA_0115.PROMOTION_CD INNER JOIN SA_0116T SA0116 /* 프로모션 계약유형 */ ON SA0113.DLR_CD = SA0116.DLR_CD AND SA0113.PROMOTION_CD = SA0116.PROMOTION_CD Left OUTER JOIN SA_0102T SA_0102 ON SA_0115.MODEL_CD = SA_0102.MODEL_CD WHERE SA0113.USE_YN = 'Y' AND SA0113.APPROVE_CD = 'C' AND SA0116.CONTRACT_TP = #{sContractTp} AND SA_0115.MODEL_CD = #{sModelCd} AND #{sStartContractDt} BETWEEN SA0113.START_DT AND SA0113.END_DT -- 계약일 AND SA0113.DLR_CD = #{sDlrCd} AND SA0113.PROMOTION_OWN_CD = #{sPromotionOwnCd} -- 프로모션 주관 01:법인, 02:딜러
UNION
SELECT SA0113.DLR_CD , SA0113.PROMOTION_CD , SA0113.PROMOTION_NM , SA0113.PROMOTION_TP /* SAL010 프로모션유형 */ , SA_0102.CARLINE_CD , SA_0115.MODEL_CD , SA0113.START_DT , SA0113.END_DT , SA0113.APPLY_TP -- 비율(R) , 금액(F) , SA0113.PROMOTION_AMT , SA0113.IMS_BUDN_RATE -- 제조사부담율 , SA0113.DLR_BUDN_RATE -- 딜러부담율 , SA0113.REG_USR_ID , SA0113.REG_DT , SA0113.UPDT_USR_ID , SA0113.UPDT_DT , SA0116.CONTRACT_TP -- N: 일반판매, P:선출고판매, Q:집단판매 FROM SA_0113T SA0113 /* 프로모션 헤더 */ INNER JOIN SA_0115T SA_0115 /* 프로모션 모델 */ ON SA0113.DLR_CD = SA_0115.DLR_CD AND SA0113.PROMOTION_CD = SA_0115.PROMOTION_CD INNER JOIN SA_0116T SA0116 /* 프로모션 계약유형 */ ON SA0113.DLR_CD = SA0116.DLR_CD AND SA0113.PROMOTION_CD = SA0116.PROMOTION_CD INNER JOIN SA_0114T SA0114 ON SA0113.DLR_CD = SA0114.DLR_CD AND SA0113.PROMOTION_CD = SA0114.PROMOTION_CD Left OUTER JOIN SA_0102T SA_0102 ON SA_0115.MODEL_CD = SA_0102.MODEL_CD WHERE SA0113.USE_YN = 'Y' AND SA0113.APPROVE_CD = 'C' AND SA0116.CONTRACT_TP = #{sContractTp} AND SA_0115.MODEL_CD = #{sModelCd} AND #{sStartContractDt} BETWEEN SA0113.START_DT AND SA0113.END_DT -- 계약일 AND SA0113.DLR_CD = #{sDlrCd} AND SA0114.CUST_CD = #{sContractCustNo} AND SA0113.PROMOTION_OWN_CD = #{sPromotionOwnCd} -- 프로모션 주관 01:법인, 02:딜러 )

/* [chn.bhmc.dms.sal.cnt.service.dao.ContractPackageDAO.selectCntPackagePromotionSearch] */ SELECT SA0113.DLR_CD , SA0113.PROMOTION_CD , SA0113.PROMOTION_NM , SA0113.PROMOTION_TP /* SAL010 프로모션유형 */ , SA_0102.CARLINE_CD , SA_0115.MODEL_CD , SA0113.START_DT , SA0113.END_DT , SA0113.APPLY_TP -- 비율(R) , 금액(F) , SA0113.PROMOTION_AMT , SA0113.IMS_BUDN_RATE -- 제조사부담율 , SA0113.DLR_BUDN_RATE -- 딜러부담율 , SA0113.REG_USR_ID , SA0113.REG_DT , SA0113.UPDT_USR_ID , SA0113.UPDT_DT , SA0116.CONTRACT_TP -- N: 일반판매, P:선출고판매, Q:집단판매 FROM SA_0113T SA0113 /* 프로모션 헤더 */ INNER JOIN SA_0115T SA_0115 /* 프로모션 모델 */ ON SA0113.DLR_CD = SA_0115.DLR_CD AND SA0113.PROMOTION_CD = SA_0115.PROMOTION_CD INNER JOIN SA_0116T SA0116 /* 프로모션 계약유형 */ ON SA0113.DLR_CD = SA0116.DLR_CD AND SA0113.PROMOTION_CD = SA0116.PROMOTION_CD Left OUTER JOIN SA_0102T SA_0102 ON SA_0115.MODEL_CD = SA_0102.MODEL_CD WHERE SA0113.USE_YN = 'Y' AND SA0113.APPROVE_CD = 'C' AND SA0116.CONTRACT_TP = #{sContractTp} AND SA_0115.MODEL_CD = #{sModelCd} AND #{sStartContractDt} BETWEEN SA0113.START_DT AND SA0113.END_DT -- 계약일 AND SA0113.DLR_CD = #{sDlrCd} AND SA0113.PROMOTION_OWN_CD = #{sPromotionOwnCd} -- 프로모션 주관 01:법인, 02:딜러
