mapper.select:
SELECT * FROM (
    SELECT ROWNUM AS RNUM, T1.* FROM (
        SELECT SA0214.CTRL_SEQ
              ,SA0214.CTRL_START_YY_MM_DT
              ,SA0214.CTRL_END_YY_MM_DT
              ,SA0214.CTRL_GRP_CD
              ,SA0214.CTRL_TARG_CD
              ,SA0214.DLR_CTRL_RANGE_CD
              ,SA0214.CTRL_YN
              ,SA0214.REMARK
              ,SA0214.REG_USR_ID,SA0214.REG_DT,SA0214.UPDT_USR_ID,SA0214.UPDT_DT
          FROM SA_0214T SA0214
         WHERE 1=1

           AND SA0214.CTRL_GRP_CD = #{sCtrlGrpCd}


           AND SA0214.CTRL_TARG_CD = #{sCtrlTargCd}

         ORDER BY SA0214.UPDT_DT DESC
    ) T1


            ROWNUM <= #{lastIndex}


)


        RNUM > #{firstIndex}

SELECT COUNT(*) AS VALUE
  FROM SA_0214T SA0214
 WHERE 1=1

   AND SA0214.CTRL_GRP_CD = #{sCtrlGrpCd}


   AND SA0214.CTRL_TARG_CD = #{sCtrlTargCd}

SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT SA0215.CTRL_SEQ,SA0215.CTRL_DLR_CD,SA0214.CTRL_START_YY_MM_DT,SA0214.CTRL_END_YY_MM_DT , SA0214.CTRL_GRP_CD,SA0214.CTRL_TARG_CD,SA0214.DLR_CTRL_RANGE_CD,SA0215.REMARK , (SELECT DLR_NM FROM CM_0101T WHERE DLR_CD = SA0215.CTRL_DLR_CD) AS CTRL_DLR_NM , SA0215.REG_USR_ID,SA0215.REG_DT,SA0215.UPDT_USR_ID,SA0215.UPDT_DT FROM SA_0215T SA0215,SA_0214T SA0214 WHERE 1=1 AND SA0215.CTRL_SEQ = SA0214.CTRL_SEQ AND SA0215.CTRL_SEQ = #{sCtrlSeq} ) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}
ORDER BY
CTRL_SEQ ASCDESC CTRL_START_YY_MM_DT ASCDESC CTRL_END_YY_MM_DT ASCDESC CTRL_GRP_CD ASCDESC CTRL_TARG_CD ASCDESC CTRL_DLR_CD ASCDESC CTRL_DLR_NM ASCDESC REMARK ASCDESC UPDT_USR_ID ASCDESC UPDT_DT ASCDESC
CTRL_SEQ DESC
CTRL_SEQ DESC

SELECT * FROM SA_0214T
WHERE 1=1
  AND CTRL_SEQ = #{ctrlSeq}

mapper.insert:
INSERT INTO SA_0214T(
       CTRL_SEQ
      ,CTRL_START_YY_MM_DT
      ,CTRL_END_YY_MM_DT
      ,CTRL_GRP_CD
      ,CTRL_TARG_CD
      ,DLR_CTRL_RANGE_CD
      ,CTRL_YN
      ,REMARK
      ,REG_USR_ID
      ,REG_DT
      ,UPDT_USR_ID
      ,UPDT_DT
)VALUES(
       (SELECT NVL(MAX(CTRL_SEQ)+1,1) AS CTRL_SEQ FROM SA_0214T)
      ,#{ctrlStartYyMmDt}
      ,#{ctrlEndYyMmDt}
      ,#{ctrlGrpCd}
      ,#{ctrlTargCd}
      ,#{dlrCtrlRangeCd}
      ,#{ctrlYn}
      ,#{remark}
      ,#{regUsrId}
      ,SYSDATE
      ,#{regUsrId}
      ,SYSDATE
)

    SELECT MAX(CTRL_SEQ) AS ctrlSeq FROM SA_0214T

mapper.update:
UPDATE SA_0214T SET
       CTRL_YN = #{ctrlYn}
      ,REMARK = #{remark}
      ,UPDT_USR_ID = #{updtUsrId}
      ,UPDT_DT = SYSDATE
WHERE 1=1
  AND CTRL_SEQ = #{ctrlSeq}

mapper.select:
SELECT * FROM SA_0215T
WHERE 1=1
  AND CTRL_SEQ = #{ctrlSeq}
  AND CTRL_DLR_CD = #{ctrlDlrCd}

mapper.update:
UPDATE SA_0215T SET
       REMARK = #{remark}
      ,UPDT_USR_ID = #{updtUsrId}
      ,UPDT_DT = SYSDATE
WHERE 1=1
  AND CTRL_SEQ = #{ctrlSeq}
  AND CTRL_DLR_CD = #{ctrlDlrCd}

mapper.delete:
DELETE SA_0215T
 WHERE 1=1
   AND CTRL_SEQ = #{ctrlSeq}
   AND CTRL_DLR_CD = #{ctrlDlrCd}
