mapper.select:
/* [chn.bhmc.dms.sal.fim.service.dao.FincInfoMngDAO.selectFincNoSearch] */
    SELECT FINC_NO
      FROM SA_0233T
     WHERE DLR_CD = #{dlrCd}
       AND CONTRACT_NO = #{contractNo}

mapper.delete:
/* [chn.bhmc.dms.sal.fim.service.dao.FincInfoMngDAO.deleteFincMng] */
    DELETE
      FROM SA_0233T
     WHERE DLR_CD      = #{dlrCd}
       AND FINC_NO     = #{fincNo}

mapper.select:
/* [chn.bhmc.dms.sal.fim.service.dao.FincInfoMngDAO.selectFincInfoMngsByConditionCnt] */
    SELECT COUNT(*)
      FROM SA_0233T A                   /*금융관리*/
      LEFT OUTER JOIN SA_0121T B        /*차량마스터*/
        ON A.DLR_CD = B.DLR_CD
       AND A.VIN_NO = B.VIN_NO
       AND A.CONTRACT_NO = B.CONTRACT_NO
      LEFT OUTER JOIN CM_0801T C        /* 사용자 */
        ON A.DLR_CD = C.DLR_CD
       AND A.REG_USR_ID = C.USR_ID


          AND A.DLR_CD = #{sDlrCd}





             AND A.VIN_NO LIKE #{sVinNo}


             AND A.VIN_NO = #{sVinNo}




         AND A.FINC_NO  = #{sFincNo}


         AND A.FINC_STAT_CD = #{sFincStatCd}


         AND A.FINC_REQ_DT  >=  #{sStartFincReqDt}


         AND A.FINC_REQ_DT  <=  #{sEndFincReqDt}


         AND A.CONTRACT_NO = #{sContractNo}


         AND A.CARLINE_CD            = #{sCarlineCd}


         AND A.MODEL_CD              = #{sModelCd}


         AND A.OCN_CD                = #{sOcnCd}


         AND A.EXT_COLOR_CD          = #{sExtColorCd}


         AND A.INT_COLOR_CD          = #{sIntColorCd}

/* [chn.bhmc.dms.sal.fim.service.dao.FincInfoMngDAO.selectFincInfoMngsByCondition] */ SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT A.DLR_CD --딜러코드 , A.FINC_NO --금융번호 , A.FINC_REQ_DT --금융요청일자 , A.FINC_STAT_CD --금융상태코드 , A.CONTRACT_NO --계약번호 , A.VIN_NO --차대번호 , A.CARLINE_CD --차종코드 , FN_GET_CARLINE_NM(A.CARLINE_CD) AS CARLINE_NM , A.MODEL_CD --모델코드 , FN_GET_MODEL_NM(A.MODEL_CD) AS MODEL_NM , A.OCN_CD --OCN코드 , FN_GET_OCN_NM(A.MODEL_CD, A.OCN_CD) AS OCN_NM , A.EXT_COLOR_CD --외장색상코드 , FN_GET_EXT_COLOR_NM(A.EXT_COLOR_CD) AS EXT_COLOR_NM , A.INT_COLOR_CD --내장색상코드 , FN_GET_INT_COLOR_NM(A.INT_COLOR_CD) AS INT_COLOR_NM , B.CAR_REG_NO --차량등록번호 , B.ENGIN_SERIAL_NO AS ENGIN_NO -- 엔진번호 , B.cert_No --합격증번호 , A.PUBL_NM --발행명 , A.TEL_NO1 --전화번호1 , A.CUST_TP --고객유형 , A.MATH_DOC_TP --주요인증유형 , A.SSN_CRN_NO --주민사업자등록번호 , A.FINC_REQ_AMT --금융요청금액 , A.FINC_RPAY_PRID --금융상환기간 , A.FINC_LOAN_FEE_AMT --금융대출수수료금액 , A.FINC_CMP_CD --금융회사코드 , A.FINC_ITEM_CD --금융품목코드 , A.FINC_END_DT --금융만기일자 , A.FINC_LOAN_RATE --금융대출비율 , A.REQ_DATA_COLLC_END_YN --요청자료수집완료여부 , A.LOAN_AGREE_STAT_CD --대출동의상태코드 , A.LOAN_YN --대출여부 , A.CLTR_LOAN_STAT_CD --담보대출상태코드 , A.FILE_DOC_ID --파일문서ID , A.REG_USR_ID --등록자ID , C.USR_NM AS REG_USR_NM , A.REG_DT --등록일자 , A.UPDT_USR_ID --수정자ID , A.UPDT_DT --수정일자 FROM SA_0233T A /*금융관리*/ LEFT OUTER JOIN SA_0121T B /*차량마스터*/ ON A.DLR_CD = B.DLR_CD AND A.VIN_NO = B.VIN_NO AND A.CONTRACT_NO = B.CONTRACT_NO LEFT OUTER JOIN CM_0801T C /* 사용자 */ ON A.DLR_CD = C.DLR_CD AND A.REG_USR_ID = C.USR_ID
AND A.DLR_CD = #{sDlrCd}
AND A.VIN_NO LIKE #{sVinNo}
AND A.VIN_NO = #{sVinNo}
AND A.FINC_NO = #{sFincNo}
AND A.FINC_STAT_CD = #{sFincStatCd}
AND A.FINC_REQ_DT >= #{sStartFincReqDt}
AND A.FINC_REQ_DT <= #{sEndFincReqDt}
AND A.CONTRACT_NO = #{sContractNo}
AND A.CARLINE_CD = #{sCarlineCd}
AND A.MODEL_CD = #{sModelCd}
AND A.OCN_CD = #{sOcnCd}
AND A.EXT_COLOR_CD = #{sExtColorCd}
AND A.INT_COLOR_CD = #{sIntColorCd}
ORDER BY
A.DLR_CD ASCDESC C.USR_NM ASCDESC A.FINC_STAT_CD ASCDESC A.PUBL_NM ASCDESC A.VIN_NO ASCDESC A.FINC_CMP_CD ASCDESC A.FINC_LOAN_FEE_AMT ASCDESC A.FINC_RPAY_PRID ASCDESC A.FINC_NO ASCDESC A.FINC_REQ_DT ASCDESC A.UPDT_DT ASCDESC
A.DLR_CD ASC, A.FINC_NO DESC
A.DLR_CD ASC, A.FINC_NO DESC
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

mapper.update:
/* [chn.bhmc.dms.sal.fim.service.dao.FincInfoMngDAO.updateFincInfo] */
    UPDATE SA_0233T    --금융관리
       SET PUBL_NM = #{publNm}
         , TEL_NO1 = #{telNo1}
         , FILE_DOC_ID = #{fileDocId}
         , REQ_DATA_COLLC_END_YN = #{reqDataCollcEndYn}
         , LOAN_AGREE_STAT_CD = #{loanAgreeStatCd}
         , LOAN_YN = #{loanYn}
         , CLTR_LOAN_STAT_CD = #{cltrLoanStatCd}
         , UPDT_USR_ID           = #{updtUsrId}                   --수정자ID
         , UPDT_DT               = SYSDATE
     WHERE DLR_CD                = #{dlrCd}                       --딜러코드
       AND FINC_NO               = #{fincNo}                      --금융번호

mapper.select:
/* [chn.bhmc.dms.sal.fim.service.dao.FincInfoMngDAO.selectFincInfoPopupsByConditionCnt] */
    SELECT COUNT(*)
      FROM SA_0234T A
     WHERE A.DLR_CD             = #{sDlrCd}
       AND A.FINC_NO            = #{sFincNo}
 ORDER BY A.DLR_CD, A.FINC_NO, A.FINC_RPAY_SEQ

/* chn.bhmc.dms.sal.fim.service.dao.FincInfoMngDAO.selectCustFincInfosByCondition */ SELECT C.CUST_CD , C.Cust_Nm , A.* , FN_GET_CARLINE_NM(A.CARLINE_CD) AS CARLINE_NM , FN_GET_MODEL_NM(A.MODEL_CD) AS MODEL_NM , FN_GET_OCN_NM(A.MODEL_CD, A.OCN_CD) AS OCN_NM , FN_GET_EXT_COLOR_NM(A.EXT_COLOR_CD) AS EXT_COLOR_NM , FN_GET_INT_COLOR_NM(A.INT_COLOR_CD) AS INT_COLOR_NM , ( SELECT Z1.CMM_CD_NM FROM (SELECT A.CMM_CD , NVL(B.CMM_CD_TXT, A.CMM_CD_NM) AS CMM_CD_NM FROM CM_0303T A LEFT OUTER JOIN CM_0304T B ON A.CMM_GRP_CD = B.CMM_GRP_CD AND A.CMM_CD = B.CMM_CD AND B.LANG_CD = #{sLangCd} WHERE A.CMM_GRP_CD = 'SAL055' /*금융사*/ AND A.USE_YN = 'Y' ) Z1 WHERE A.FINC_CMP_CD = Z1.CMM_CD ) AS FINC_CMP_NM --금융사
, ( SELECT Z1.CMM_CD_NM FROM (SELECT A.CMM_CD , NVL(B.CMM_CD_TXT, A.CMM_CD_NM) AS CMM_CD_NM FROM CM_0303T A LEFT OUTER JOIN CM_0304T B ON A.CMM_GRP_CD = B.CMM_GRP_CD AND A.CMM_CD = B.CMM_CD AND B.LANG_CD = #{sLangCd} WHERE A.CMM_GRP_CD = 'SAL055' /*금융사*/ AND A.USE_YN = 'Y' ) Z1 WHERE A.FINC_CMP_CD = Z1.CMM_CD ) AS FINC_CMP_NM --금융사 , ( SELECT Z2.CMM_CD_NM FROM (SELECT A.CMM_CD , NVL(B.CMM_CD_TXT, A.CMM_CD_NM) AS CMM_CD_NM FROM CM_0303T A LEFT OUTER JOIN CM_0304T B ON A.CMM_GRP_CD = B.CMM_GRP_CD AND A.CMM_CD = B.CMM_CD AND B.LANG_CD = #{sLangCd} WHERE A.CMM_GRP_CD = 'SAL042' /*금융신청상태*/ AND A.USE_YN = 'Y' ) Z2 WHERE A.FINC_STAT_CD = Z2.CMM_CD ) AS FINC_STAT_NM --금융신청상태 FROM SA_0233T A INNER JOIN SA_0201T B ON A.DLR_CD = B.DLR_CD AND A.CONTRACT_NO = B.CONTRACT_NO INNER JOIN SA_0202T C ON B.DLR_CD = C.DLR_CD AND B.CONTRACT_NO = C.CONTRACT_NO AND C.PARTNER_TP = 'C' -- 계약자 WHERE B.CONTRACT_STAT_CD = '50' -- 고객인도 AND C.CUST_CD = #{sCustCd}
AND A.DLR_CD = #{sDlrCd}
