mapper.select:
/* [chn.bhmc.dms.sal.inv.service.dao.StockDueDAO.selectStockDueListsByConditionCnt] */
    SELECT COUNT(*)
      FROM (
            SELECT DLR_CD
                 , STOCK_DT
              FROM SA_0210T
             WHERE DLR_CD = #{sDlrCd}

               AND STOCK_DT = #{sStockDt}


               AND VIN_NO = #{sVinNo}



                  AND STOCK_AGING_CNT between 0 and 3


                  AND STOCK_AGING_CNT between 4 and 6


                  AND STOCK_AGING_CNT between 7 and 9


                  AND STOCK_AGING_CNT >= 10



               AND CONFIRM_YN = #{sConfirmYn}

             GROUP BY DLR_CD, STOCK_DT
           )

/* [chn.bhmc.dms.sal.inv.service.dao.StockDueDAO.selectStockDueListsByCondition] */
    SELECT *
      FROM (
        SELECT ROWNUM AS RNUM, T1.*
          FROM (
                    SELECT DLR_CD
                         , STOCK_DT
                         , COUNT(VIN_NO) AS CAR_TOT
                         , SUM(CASE WHEN NVL(CONFIRM_YN, 'N') = 'Y' THEN 1
                                    ELSE 0
                                END
                               )  AS CON_Y
                         , SUM(CASE WHEN NVL(CONFIRM_YN, 'N') = 'N' THEN 1
                                    ELSE 0
                                END
                               )  AS CON_N
                      FROM SA_0210T
                     WHERE DLR_CD = #{sDlrCd}

                       AND STOCK_DT = #{sStockDt}


                       AND VIN_NO = #{sVinNo}



                          AND STOCK_AGING_CNT between 0 and 3


                          AND STOCK_AGING_CNT between 4 and 6


                          AND STOCK_AGING_CNT between 7 and 9


                          AND STOCK_AGING_CNT >= 10



                       AND CONFIRM_YN = #{sConfirmYn}

                     GROUP BY DLR_CD, STOCK_DT

                  ) T1


                    ROWNUM <= #{lastIndex}


            )


                RNUM > #{firstIndex}

/* [chn.bhmc.dms.sal.inv.service.dao.StockDueDAO.selectStockDueDetailListsByConditionCnt] */
    SELECT COUNT(*)
      FROM SA_0210T A           /* 재고실사 */
     WHERE A.DLR_CD = #{sDlrCd}

       AND A.STOCK_DT = #{sStockDt}


       AND A.VIN_NO = #{sVinNo}



          AND A.STOCK_AGING_CNT between 0 and 3


          AND A.STOCK_AGING_CNT between 4 and 6


          AND A.STOCK_AGING_CNT between 7 and 9


          AND A.STOCK_AGING_CNT >= 10



       AND A.CONFIRM_YN = #{sConfirmYn}

/* [chn.bhmc.dms.sal.inv.service.dao.StockDueDAO.selectStockDueDetailListsByCondition] */ SELECT * FROM ( SELECT ROWNUM AS RNUM , FN_GET_CARLINE_NM(T1.CARLINE_CD) AS CARLINE_NM , FN_GET_MODEL_NM(T1.MODEL_CD) AS MODEL_NM , FN_GET_OCN_NM(T1.MODEL_CD, T1.OCN_CD) AS OCN_NM , FN_GET_EXT_COLOR_NM(T1.EXT_COLOR_CD) AS EXT_COLOR_NM , FN_GET_INT_COLOR_NM(T1.INT_COLOR_CD) AS INT_COLOR_NM , T1.* FROM ( SELECT A.DLR_CD --딜러코드 , A.STOCK_DT --재고일자 , A.CAR_ID --차량ID , A.VIN_NO --차대번호 , A.CARLINE_CD --차종코드 , A.MODEL_CD --모델코드 , A.OCN_CD --OCN코드 , A.EXT_COLOR_CD --외장색상코드 , A.INT_COLOR_CD --내장색상코드 , A.CAR_STAT_CD --차량상태코드 , B.CMM_CD_NM AS CAR_STAT_NM -- 차량상태 , A.STRGE_CD --창고코드 , C.STRGE_NM --창고명 , A.LOC_CD --위치코드 , D.LOC_NM --위치명 , A.MANUFACT_YY_MM_DT --제작년월일자 , A.GR_DT --입고일자 , A.STOCK_AGING_CNT --재고월령수 , A.CONFIRM_YN --확인여부 , A.STOCK_STAT_CD --재고상태코드 , A.PHOTO_FILE_PATH_NM --사진파일경로명 , A.FILE_DOC_NO --첨부파일문서번호 , A.FILE_NO --파일번호 , A.REMARK --비고 , A.REG_USR_ID --등록자ID , A.REG_DT --등록일자 , A.UPDT_USR_ID --수정자ID , A.UPDT_DT --수정일자 FROM SA_0210T A /* 재고실사 */ LEFT OUTER JOIN ( SELECT A.CMM_CD , NVL(B.CMM_CD_TXT, A.CMM_CD_NM) AS CMM_CD_NM FROM CM_0303T A LEFT OUTER JOIN CM_0304T B ON A.CMM_GRP_CD = B.CMM_GRP_CD AND A.CMM_CD = B.CMM_CD AND B.LANG_CD = #{sLangCd} WHERE A.CMM_GRP_CD = 'SAL001' /*차량상태*/ AND A.USE_YN = 'Y' ) B ON A.CAR_STAT_CD = B.CMM_CD LEFT OUTER JOIN ( SELECT A.DLR_CD , A.STRGE_CD , A.STRGE_NM FROM CM_0104T A /* 창고 */ INNER JOIN CM_0103T B ON A.PLT_CD = B.PLT_CD AND A.DLR_CD = B.DLR_CD ) C ON A.STRGE_CD = C.STRGE_CD AND A.DLR_CD = C.DLR_CD LEFT OUTER JOIN PT_0222T D ON A.DLR_CD = D.DLR_CD AND A.STRGE_CD = D.STRGE_CD AND A.LOC_CD = D.LOC_CD
WHERE A.DLR_CD = #{sDlrCd}
AND A.STOCK_DT = #{sStockDt}
AND A.VIN_NO = #{sVinNo}
AND A.STOCK_AGING_CNT between 0 and 3
AND A.STOCK_AGING_CNT between 4 and 6
AND A.STOCK_AGING_CNT between 7 and 9
AND A.STOCK_AGING_CNT >= 10
AND A.CONFIRM_YN = #{sConfirmYn}
ORDER BY
A.CAR_ID ASCDESC A.VIN_NO ASCDESC A.CARLINE_CD ASCDESC A.CARLINE_CD ASCDESC A.MODEL_CD ASCDESC A.MODEL_CD ASCDESC A.OCN_CD ASCDESC A.OCN_CD ASCDESC A.EXT_COLOR_CD ASCDESC A.EXT_COLOR_CD ASCDESC A.INT_COLOR_CD ASCDESC A.INT_COLOR_CD ASCDESC A.CAR_STAT_CD ASCDESC CAR_STAT_NM ASCDESC A.STRGE_CD ASCDESC C.STRGE_NM ASCDESC A.LOC_CD ASCDESC D.LOC_NM ASCDESC A.MANUFACT_YY_MM_DT ASCDESC A.GR_DT ASCDESC A.STOCK_AGING_CNT ASCDESC A.CONFIRM_YN ASCDESC A.STOCK_STAT_CD ASCDESC A.PHOTO_FILE_PATH_NM ASCDESC A.REMARK ASCDESC
A.DLR_CD ASC, A.STOCK_DT ASC, A.CONFIRM_YN ASC
A.DLR_CD ASC, A.STOCK_DT ASC, A.CONFIRM_YN ASC
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}
