mapper.select:

SELECT COUNT(*)
  FROM SA_0120T
 WHERE DLR_CD = #{sDlrCd}

   AND CARLINE_CD = #{sCarlineCd}


   AND MODEL_CD = #{sModelCd}


   AND OCN_CD = #{sOcnCd}


   AND START_DT  <=  #{sEndDt}
   AND END_DT  >=  #{sStartDt}

SELECT * FROM ( SELECT ROWNUM AS RNUM , FN_GET_CARLINE_NM(T1.CARLINE_CD) AS CARLINE_NM , DECODE(T1.MODEL_CD, 'ALL', 'ALL', FN_GET_MODEL_NM(T1.MODEL_CD)) AS MODEL_NM , DECODE(T1.OCN_CD, 'ALL', 'ALL', FN_GET_OCN_NM(T1.MODEL_CD, T1.OCN_CD)) AS OCN_NM , T1.* FROM ( SELECT DLR_CD , CARLINE_CD , MODEL_CD , OCN_CD , START_DT , END_DT , PRC_NREG_YN , DC_TOT_AMT1 , CAR_DC_AMT1 , GOODS_DC_AMT1 , DC_TOT_AMT2 , CAR_DC_AMT2 , GOODS_DC_AMT2 , DC_TOT_AMT3 , CAR_DC_AMT3 , GOODS_DC_AMT3 FROM SA_0120T WHERE DLR_CD = #{sDlrCd}
AND CARLINE_CD = #{sCarlineCd}
AND MODEL_CD = #{sModelCd}
AND OCN_CD = #{sOcnCd}
AND START_DT <= #{sEndDt} AND END_DT >= #{sStartDt}
ORDER BY
CARLINE_CD ASCDESC MODEL_CD ASCDESC OCN_CD ASCDESC START_DT ASCDESC END_DT ASCDESC DC_TOT_AMT1 ASCDESC CAR_DC_AMT1 ASCDESC GOODS_DC_AMT1 ASCDESC DC_TOT_AMT2 ASCDESC CAR_DC_AMT2 ASCDESC GOODS_DC_AMT2 ASCDESC DC_TOT_AMT3 ASCDESC CAR_DC_AMT3 ASCDESC GOODS_DC_AMT3 ASCDESC
START_DT DESC
START_DT DESC
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

mapper.update:
UPDATE SA_0120T
   SET START_DT = #{startDt}
     , PRC_NREG_YN = #{prcNregYn}
     , DC_TOT_AMT1 = #{dcTotAmt1}
     , CAR_DC_AMT1 = #{carDcAmt1}
     , GOODS_DC_AMT1 = #{goodsDcAmt1}
     , DC_TOT_AMT2 = #{dcTotAmt2}
     , CAR_DC_AMT2 = #{carDcAmt2}
     , GOODS_DC_AMT2 = #{goodsDcAmt2}
     , DC_TOT_AMT3 = #{dcTotAmt3}
     , CAR_DC_AMT3 = #{carDcAmt3}
     , GOODS_DC_AMT3 = #{goodsDcAmt3}
     , UPDT_USR_ID = #{updtUsrId}
     , UPDT_DT = SYSDATE
 WHERE DLR_CD = #{dlrCd}
   AND END_DT = #{endDt}
   AND CARLINE_CD = #{carlineCd}
   AND MODEL_CD = #{modelCd}
   AND OCN_CD = #{ocnCd}

mapper.insert:
INSERT INTO SA_0120T
     ( DLR_CD
     , END_DT
     , START_DT
     , CARLINE_CD
     , MODEL_CD
     , OCN_CD
     , PRC_NREG_YN
     , DC_TOT_AMT1
     , CAR_DC_AMT1
     , GOODS_DC_AMT1
     , DC_TOT_AMT2
     , CAR_DC_AMT2
     , GOODS_DC_AMT2
     , DC_TOT_AMT3
     , CAR_DC_AMT3
     , GOODS_DC_AMT3
     , REG_USR_ID
     , REG_DT
     , UPDT_USR_ID
     , UPDT_DT)
VALUES
     ( #{dlrCd}
     , #{endDt}
     , #{startDt}
     , #{carlineCd}
     , #{modelCd}
     , #{ocnCd}
     , #{prcNregYn}
     , #{dcTotAmt1}
     , #{carDcAmt1}
     , #{goodsDcAmt1}
     , #{dcTotAmt2}
     , #{carDcAmt2}
     , #{goodsDcAmt2}
     , #{dcTotAmt3}
     , #{carDcAmt3}
     , #{goodsDcAmt3}
     , #{regUsrId}
     , SYSDATE
     , #{updtUsrId}
     , SYSDATE)
