mapper.select:
SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT SA0212.OBT_NO,SA0212.DLR_CD,SA0212.REQ_DT,SA0212.REQ_STAT_CD ,SA0212.SALE_DLR_DIST_CD,SA0212.SALE_DLR_DIST_NM,SA0212.REQ_USR_ID,SA0212.REQ_USR_NM ,SA0212.TEL_NO ,SA0212.CARLINE_CD,FN_GET_CARLINE_NM(SA0212.CARLINE_CD) AS CARLINE_NM ,SA0212.MODEL_CD,FN_GET_MODEL_NM(SA0212.MODEL_CD) AS MODEL_NM ,SA0212.OCN_CD,FN_GET_OCN_NM(SA0212.MODEL_CD, SA0212.OCN_CD) AS OCN_NM ,SA0212.EXT_COLOR_CD,(SELECT EXT_COLOR_NM FROM SA_0107T WHERE EXT_COLOR_CD = SA0212.EXT_COLOR_CD) AS EXT_COLOR_NM ,SA0212.INT_COLOR_CD,(SELECT INT_COLOR_NM FROM SA_0108T WHERE INT_COLOR_CD = SA0212.INT_COLOR_CD) AS INT_COLOR_NM ,SA0212.EVAL_RSLT_YN,SA0212.PEVAL_ID,SA0212.PEVAL_NM,SA0212.TEL_NO1,SA0212.EVAL_DT ,SA0212.OBT_DLR_CD,SA0212.VIN_NO,SA0212.REMARK ,SA0212.REG_USR_ID,SA0212.REG_DT,SA0212.UPDT_USR_ID,SA0212.UPDT_DT ,'' AS DLR_NM FROM SA_0212T SA0212 WHERE 1=1
AND SA0212.SALE_DLR_DIST_CD = #{sSaleDlrDistCd}
AND SA0212.DLR_CD = #{sDlrCd}
AND SA0212.REQ_DT >= TRUNC(#{sStartReqDt},'DD')
AND SA0212.REQ_DT < TRUNC(#{sEndReqDt},'DD')+1
AND SA0212.REQ_STAT_CD = #{sReqStatCd}
AND SA0212.EVAL_RSLT_YN = #{sEvalRsltYn}
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}
ORDER BY
OBT_NO ASCDESC DLR_CD ASCDESC DLR_NM ASCDESC CARLINE_NM ASCDESC MODEL_NM ASCDESC EXT_COLOR_NM ASCDESC INT_COLOR_NM ASCDESC OBT_DLR_CD ASCDESC OBT_DLR_NM ASCDESC REQ_DT ASCDESC REQ_STAT_CD ASCDESC EVAL_RSLT_YN ASCDESC
OBT_NO DESC
OBT_NO DESC

SELECT COUNT(*) AS VALUE
  FROM SA_0212T SA0212
 WHERE 1=1

    AND SA0212.SALE_DLR_DIST_CD = #{sSaleDlrDistCd}


   AND SA0212.DLR_CD = #{sDlrCd}


     AND SA0212.REQ_DT >= TRUNC(#{sStartReqDt},'DD')


     AND SA0212.REQ_DT < TRUNC(#{sEndReqDt},'DD')+1


   AND SA0212.REQ_STAT_CD = #{sReqStatCd}


   AND SA0212.EVAL_RSLT_YN = #{sEvalRsltYn}

mapper.insert:
INSERT INTO SA_0212T(
       OBT_NO
     , DLR_CD
     , REQ_DT
     , REQ_STAT_CD
     , SALE_DLR_DIST_CD
     , SALE_DLR_DIST_NM
     , REQ_USR_ID
     , REQ_USR_NM
     , TEL_NO
     , CARLINE_CD
     , MODEL_CD
     , OCN_CD
     , EXT_COLOR_CD
     , INT_COLOR_CD
     , REG_USR_ID
     , REG_DT
)VALUES(
       'OB'||#{dlrCd}||(SELECT LPAD(NVL(MAX(SUBSTR(OBT_NO,-6)),0) + 1,'6','0') FROM SA_0212T WHERE DLR_CD = #{dlrCd})
     , #{dlrCd}
     , SYSDATE
     , 'A'
     , #{saleDlrDistCd}
     , #{saleDlrDistNm}
     , #{reqUsrId}
     , #{reqUsrNm}
     , #{telNo}
     , #{carlineCd}
     , #{modelCd}
     , #{ocnCd}
     , #{extColorCd}
     , #{intColorCd}
     , #{regUsrId}
     , SYSDATE
)

mapper.update:
UPDATE SA_0212T
   SET TEL_NO = #{telNo}
     , CARLINE_CD = #{carlineCd}
     , MODEL_CD = #{modelCd}
     , OCN_CD = #{ocnCd}
     , EXT_COLOR_CD = #{extColorCd}
     , INT_COLOR_CD = #{intColorCd}
     , REQ_STAT_CD = #{reqStatCd}
     , EVAL_RSLT_YN = #{evalRsltYn}
     , PEVAL_ID = #{pevalId}
     , PEVAL_NM = #{pevalNm}
     , EVAL_DT = #{evalDt}
     , TEL_NO1 = #{telNo1}
     , OBT_DLR_CD = #{obtDlrCd}
     , VIN_NO = #{vinNo}
     , REMARK = #{remark}
     , UPDT_USR_ID = #{updtUsrId}
     , UPDT_DT = SYSDATE
 WHERE 1=1
   AND OBT_NO = #{obtNo}

UPDATE SA_0121T
   SET DLR_CD = #{dlrCd}
      ,UPDT_USR_ID = #{updtUsrId}
      ,UPDT_DT = SYSDATE
 WHERE VIN_NO = #{vinNo}
