mapper.select:
SELECT COUNT(*) AS VALUE FROM ( SELECT ORD_YY_MM_DT --오더년월 ,WEEK --오더주차 ,YY_MM --오더년YYMM ,DLR_CD --딜러 ,ORD_TP --오더타입 ,CARLINE_CD --차종코드 ,CARLINE_NM --차종명 ,MODEL_CD --모델코드 ,FN_GET_CARLINE_COM_NM(A.MODEL_CD, A.OCN_CD) AS MODEL_NM --모델명 ,OCN_CD --OCN코드 ,OCN_NM --OCN명 ,EXT_COLOR_CD --외장색상코드 ,EXT_COLOR_NM --외장색상명 ,INT_COLOR_CD --내장색상코드 ,INT_COLOR_NM --내장색상명 ,SUM(DECODE(ORD_QTY,0,NULL,ORD_QTY)) AS ORD_QTY --요청수량 ,SUM(DECODE(FST_CONFIRM_QTY,0,NULL,FST_CONFIRM_QTY)) AS FST_CONFIRM_QTY --1차확정수량 ,SUM(DECODE(SCND_CONFIRM_QTY,0,NULL,SCND_CONFIRM_QTY)) AS SCND_CONFIRM_QTY --2차확정수량 ,SUM(DECODE(SCND_CONFIRM_QTY,0,NULL,SCND_CONFIRM_QTY)) AS FINAL_QTY --심사통과수량 ,SUM(DECODE(ORD_QTY,0,NULL,ORD_QTY)) - COUNT(DECODE(SCND_CONFIRM_QTY,0,NULL,SCND_CONFIRM_QTY)) - SUM(SCND_PAR_ISFF_ADJ_QTY) AS WT_ORD_QTY_CAL --승인대기수량 ,SUM(DLR_CNCL_QTY) AS DLR_CNCL_QTY --승인후취소수량 ,SUM(NON_ALOC_QTY) AS NON_ALOC_QTY --미배정수량 ,SUM(PDI_QTY) AS PDI_QTY --PDI배정수량 ,SUM(VIN_QTY) AS VIN_QTY --VIN배정 ,SUM(GATE_OUT_QTY) AS GATE_OUT_QTY --법인출고수량 ,SUM(DLR_IN_QTY) AS DLR_IN_QTY --딜러입고수량 ,SUM(DLR_OUT_QTY) AS DLR_OUT_QTY --딜러출고수량 ,SUM(SCND_PAR_ISFF_ADJ_QTY) AS SCND_PAR_ISFF_ADJ_QTY --심사미통과수량 ,ORD_GRADE_CD FROM(SELECT A.ORD_YY_MM_DT --오더년월 ,A.ORD_PRID AS WEEK --오더주차 ,SUBSTR(A.ORD_YY_MM_DT, 3, 6) AS YY_MM --오더년YYMM ,A.DLR_CD --딜러코드 ,A.CARLINE_CD ,FN_GET_CARLINE_NM_NC(A.CARLINE_CD) AS CARLINE_NM ,A.MODEL_CD ,FN_GET_MODEL_NM_NC(A.MODEL_CD) AS MODEL_NM ,A.OCN_CD ,FN_GET_OCN_NM_NC(A.MODEL_CD, A.OCN_CD) AS OCN_NM ,A.EXT_COLOR_CD ,FN_GET_EXT_COLOR_NM_NC(A.EXT_COLOR_CD) AS EXT_COLOR_NM ,A.INT_COLOR_CD ,FN_GET_INT_COLOR_NM_NC(A.INT_COLOR_CD) AS INT_COLOR_NM ,A.ORD_TP ,NVL(A.ORD_QTY,0) AS ORD_QTY --요청수량 O ,NVL(A.FST_CONFIRM_QTY, 0) AS FST_CONFIRM_QTY --1차확정수량합 O ,NVL(A.SCND_CONFIRM_QTY, 0) AS SCND_CONFIRM_QTY --2차확정수량합 O ,(CASE WHEN A.ORD_STAT_CD = '1K' THEN 1 ELSE 0 END) AS SCND_PAR_ISFF_ADJ_QTY --미통과수량 ,(CASE WHEN B.PRTY IS NULL THEN 1 ELSE 0 END) AS WT_ORD_QTY --승인대기 O ,(CASE WHEN B.PRTY = 'B' THEN 1 ELSE 0 END) AS DLR_CNCL_QTY --승인후취소수량 O ,(CASE WHEN B.PRTY = 'A' THEN 1 ELSE 0 END) AS NON_ALOC_QTY --미배정수량 O ,(CASE WHEN B.PRTY = 'P' THEN 1 ELSE 0 END) AS PDI_QTY --PDI배정 O ,(CASE WHEN B.PRTY = 'C' THEN 1 ELSE 0 END) AS VIN_QTY --VIN배정 O ,(CASE WHEN B.PRTY = 'F' THEN 1 ELSE 0 END) AS GATE_OUT_QTY --법인출고수량O ,(CASE WHEN B.PRTY = 'D' THEN 1 ELSE 0 END) AS DLR_IN_QTY --딜러입고수량0 ,(CASE WHEN B.PRTY = 'E' THEN 1 ELSE 0 END) AS DLR_OUT_QTY --딜러출고수량0 ,ORD_GRADE_CD FROM SA_0417T A, SA_0416T B WHERE A.DLR_CD = B.DLR_CD(+) AND A.ORD_TP = B.SORD(+) AND A.ORD_YY_MM_DT = B.ORD_YYMM(+) AND A.ORD_PRID = B.ORD_WEEK(+) AND TO_NUMBER(A.ORD_NO) = B.ORD_SEQ(+) AND NOT A.ORD_STAT_CD = '1A' AND NOT A.IFRESULT_RTN = 'E'
AND A.ORD_TP = 'N1'
AND A.ORD_TP = #{strOrdTp} -- 주문유형
AND NOT A.ORD_TP = 'N1'
AND A.ORD_DT >= TRUNC(#{sOrdStartDt},'DD')
AND A.ORD_DT < TRUNC(#{sOrdEndDt},'DD')+1
AND A.DLR_CD = #{sDlrCd}
AND A.ORD_YY_MM_DT = SUBSTR(#{sOrdPrid}, 1, 6) AND A.ORD_PRID = SUBSTR(#{sOrdPrid}, 7, 2)
AND A.CARLINE_CD = #{sCarlineCd}
AND A.MODEL_CD = #{sModelCd}
AND A.OCN_CD = #{sOcnCd}
AND A.EXT_COLOR_CD = #{sExtColorCd}
AND A.INT_COLOR_CD = #{sIntColorCd}
AND A.ORD_STAT_CD = #{sOrdStatCd}
AND (SELECT X.FSCP_CHM_MMM FROM SA_0105T X WHERE X.MODEL_CD = A.MODEL_CD AND X.OCN_CD = A.OCN_CD) = #{sFscNm}
AND (SELECT X.FSCP_CHM_MMM FROM SA_0105T X WHERE X.MODEL_CD = A.MODEL_CD AND X.OCN_CD = A.OCN_CD) IS NULL
) A GROUP BY ORD_YY_MM_DT , WEEK, YY_MM , DLR_CD , ORD_TP , CARLINE_CD , CARLINE_NM , MODEL_CD , MODEL_NM , OCN_CD , OCN_NM , EXT_COLOR_CD , EXT_COLOR_NM , INT_COLOR_CD , INT_COLOR_NM , ORD_GRADE_CD
)

SELECT * FROM ( SELECT ROWNUM AS RNUM ,DECODE(SIGN(WT_ORD_QTY_CAL),-1,0,WT_ORD_QTY_CAL) AS WT_ORD_QTY ,NON_ALOC_QTY+PDI_QTY+VIN_QTY+GATE_OUT_QTY+DLR_IN_QTY+DLR_OUT_QTY AS SUM_TOTAL_QTY ,SCND_CONFIRM_QTY - DLR_CNCL_QTY AS FINAL_CONF_QTY ,T1.* FROM ( SELECT ORD_YY_MM_DT --오더년월 ,WEEK --오더주차 ,YY_MM --오더년YYMM ,DLR_CD --딜러 ,ORD_TP --오더타입 ,CARLINE_CD --차종코드 ,CARLINE_NM --차종명 ,MODEL_CD --모델코드 ,(SELECT FSCP_CHM_MMM FROM SA_0105T Z WHERE Z.MODEL_CD = A.MODEL_CD AND Z.OCN_CD = A.OCN_CD) AS MODEL_NM --모델명 ,OCN_CD --OCN코드 ,OCN_NM --OCN명 ,EXT_COLOR_CD --외장색상코드 ,EXT_COLOR_NM --외장색상명 ,INT_COLOR_CD --내장색상코드 ,INT_COLOR_NM --내장색상명 ,SUM(DECODE(ORD_QTY,0,NULL,ORD_QTY)) AS ORD_QTY --요청수량 ,SUM(DECODE(FST_CONFIRM_QTY,0,NULL,FST_CONFIRM_QTY)) AS FST_CONFIRM_QTY --1차확정수량 ,SUM(DECODE(SCND_CONFIRM_QTY,0,NULL,SCND_CONFIRM_QTY)) AS SCND_CONFIRM_QTY --2차확정수량 ,SUM(DECODE(SCND_CONFIRM_QTY,0,NULL,SCND_CONFIRM_QTY)) AS FINAL_QTY --심사통과수량 ,SUM(DECODE(ORD_QTY,0,NULL,ORD_QTY)) - COUNT(DECODE(SCND_CONFIRM_QTY,0,NULL,SCND_CONFIRM_QTY)) - SUM(SCND_PAR_ISFF_ADJ_QTY) AS WT_ORD_QTY_CAL --승인대기수량 ,SUM(DLR_CNCL_QTY) AS DLR_CNCL_QTY --승인후취소수량 ,SUM(NON_ALOC_QTY) AS NON_ALOC_QTY --미배정수량 ,SUM(PDI_QTY) AS PDI_QTY --PDI배정수량 ,SUM(MISS_SET_QTY) AS MISS_SET_QTY --임무배정수량 ,SUM(TRAN_MISS_QTY) AS TRAN_MISS_QTY --배송임무배정수량 ,SUM(VIN_QTY) AS VIN_QTY --VIN배정 ,SUM(GATE_OUT_QTY) AS GATE_OUT_QTY --법인출고수량 ,SUM(DLR_IN_QTY) AS DLR_IN_QTY --딜러입고수량 ,SUM(DLR_OUT_QTY) AS DLR_OUT_QTY --딜러출고수량 ,SUM(SCND_PAR_ISFF_ADJ_QTY) AS SCND_PAR_ISFF_ADJ_QTY --심사미통과수량 ,ORD_GRADE_CD FROM(SELECT A.ORD_YY_MM_DT --오더년월 ,A.ORD_PRID AS WEEK --오더주차 ,SUBSTR(A.ORD_YY_MM_DT, 3, 6) AS YY_MM --오더년YYMM ,A.DLR_CD --딜러코드 ,A.CARLINE_CD ,FN_GET_CARLINE_NM_NC(A.CARLINE_CD) AS CARLINE_NM ,A.MODEL_CD ,FN_GET_MODEL_NM_NC(A.MODEL_CD) AS MODEL_NM ,A.OCN_CD ,FN_GET_OCN_NM_NC(A.MODEL_CD, A.OCN_CD) AS OCN_NM ,A.EXT_COLOR_CD ,FN_GET_EXT_COLOR_NM_NC(A.EXT_COLOR_CD) AS EXT_COLOR_NM ,A.INT_COLOR_CD ,FN_GET_INT_COLOR_NM_NC(A.INT_COLOR_CD) AS INT_COLOR_NM ,A.ORD_TP ,NVL(A.ORD_QTY,0) AS ORD_QTY --요청수량 O ,NVL(A.FST_CONFIRM_QTY, 0) AS FST_CONFIRM_QTY --1차확정수량합 O ,NVL(A.SCND_CONFIRM_QTY, 0) AS SCND_CONFIRM_QTY --2차확정수량합 O ,(CASE WHEN A.ORD_STAT_CD = '1K' THEN 1 ELSE 0 END) AS SCND_PAR_ISFF_ADJ_QTY --미통과수량 ,(CASE WHEN B.PRTY IS NULL THEN 1 ELSE 0 END) AS WT_ORD_QTY --승인대기 O ,(CASE WHEN B.PRTY = 'B' THEN 1 ELSE 0 END) AS DLR_CNCL_QTY --승인후취소수량 O ,(CASE WHEN B.PRTY = 'A' THEN 1 ELSE 0 END) AS NON_ALOC_QTY --미배정수량 O ,(CASE WHEN B.PRTY = 'P' THEN 1 ELSE 0 END) AS PDI_QTY --PDI배정 O ,(CASE WHEN B.PRTY = 'M' THEN 1 ELSE 0 END) AS MISS_SET_QTY --임무배정 O ,(CASE WHEN B.PRTY = 'N' THEN 1 ELSE 0 END) AS TRAN_MISS_QTY --운송배정 O ,(CASE WHEN B.PRTY = 'C' THEN 1 ELSE 0 END) AS VIN_QTY --VIN배정 O ,(CASE WHEN B.PRTY = 'F' THEN 1 ELSE 0 END) AS GATE_OUT_QTY --법인출고수량O ,(CASE WHEN B.PRTY = 'D' THEN 1 ELSE 0 END) AS DLR_IN_QTY --딜러입고수량O ,(CASE WHEN B.PRTY = 'E' THEN 1 ELSE 0 END) AS DLR_OUT_QTY --딜러출고수량O ,ORD_GRADE_CD FROM SA_0417T A, SA_0416T B WHERE A.DLR_CD = B.DLR_CD(+) AND A.ORD_TP = B.SORD(+) AND A.ORD_YY_MM_DT = B.ORD_YYMM(+) AND A.ORD_PRID = B.ORD_WEEK(+) AND TO_NUMBER(A.ORD_NO) = B.ORD_SEQ(+) AND NOT A.ORD_STAT_CD = '1A' AND NOT A.IFRESULT_RTN = 'E'
AND A.ORD_TP = 'N1'
AND A.ORD_TP = #{strOrdTp} -- 주문유형
AND NOT A.ORD_TP = 'N1'
AND A.ORD_DT >= TRUNC(#{sOrdStartDt},'DD')
AND A.ORD_DT < TRUNC(#{sOrdEndDt},'DD')+1
AND A.DLR_CD = #{sDlrCd}
AND A.ORD_YY_MM_DT = SUBSTR(#{sOrdPrid}, 1, 6) AND A.ORD_PRID = SUBSTR(#{sOrdPrid}, 7, 2)
AND A.CARLINE_CD = #{sCarlineCd}
AND A.MODEL_CD = #{sModelCd}
AND A.OCN_CD = #{sOcnCd}
AND A.EXT_COLOR_CD = #{sExtColorCd}
AND A.INT_COLOR_CD = #{sIntColorCd}
AND A.ORD_STAT_CD = #{sOrdStatCd}
AND (SELECT X.FSCP_CHM_MMM FROM SA_0105T X WHERE X.MODEL_CD = A.MODEL_CD AND X.OCN_CD = A.OCN_CD) = #{sFscNm}
AND (SELECT X.FSCP_CHM_MMM FROM SA_0105T X WHERE X.MODEL_CD = A.MODEL_CD AND X.OCN_CD = A.OCN_CD) IS NULL
) a GROUP BY ORD_YY_MM_DT , WEEK, YY_MM , DLR_CD , ORD_TP , CARLINE_CD , CARLINE_NM , MODEL_CD , MODEL_NM , OCN_CD , OCN_NM , EXT_COLOR_CD , EXT_COLOR_NM , INT_COLOR_CD , INT_COLOR_NM , ORD_GRADE_CD ORDER BY
CARLINE_CD ASCDESC CARLINE_NM ASCDESC MODEL_CD ASCDESC MODEL_NM ASCDESC OCN_CD ASCDESC OCN_NM ASCDESC EXT_COLOR_CD ASCDESC EXT_COLOR_NM ASCDESC INT_COLOR_CD ASCDESC INT_COLOR_NM ASCDESC ORD_TP ASCDESC ORD_QTY ASCDESC FST_CONFIRM_QTY ASCDESC SCND_CONFIRM_QTY ASCDESC FINAL_QTY ASCDESC WT_ORD_QTY ASCDESC DLR_CNCL_QTY ASCDESC CNCL_QTY ASCDESC N_ORD_QTY ASCDESC PDI_QTY ASCDESC VIN_QTY ASCDESC DLR_SALE_QTY ASCDESC
ORD_YY_MM_DT DESC, WEEK DESC, YY_MM DESC, CARLINE_CD ASC, MODEL_CD ASC, OCN_CD ASC, EXT_COLOR_CD ASC, INT_COLOR_CD ASC
ORD_YY_MM_DT DESC, WEEK DESC, YY_MM DESC, CARLINE_CD ASC, MODEL_CD ASC, OCN_CD ASC, EXT_COLOR_CD ASC, INT_COLOR_CD ASC
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

SELECT COUNT(*) ROWCNT
  FROM SA_0416T A, SA_0417T B, SA_0121T C
 WHERE A.DLR_CD = B.DLR_CD
   AND A.ORD_YYMM = B.ORD_YY_MM_DT
   AND A.ORD_WEEK = B.ORD_PRID
   AND A.CARLINE_CD = B.CARLINE_CD
   AND A.VIN_NO1 = C.VIN_NO1(+)
   AND A.VIN_NO2 = C.VIN_NO2(+)
   AND A.ORD_SEQ = TO_NUMBER(B.ORD_NO)

  AND A.DLR_CD       = #{sDlrCd}


  AND A.ORD_YYMM = #{sOrdYyMmDt}


  AND A.ORD_WEEK     = #{sOrdPrid}


  AND B.ORD_TP = #{sOrdsTp}


  AND A.CARLINE_CD   = #{sCarlineCd}


  AND A.MODEL_CD     = #{sModelCd}


  AND A.OCN_CD       = #{sOcnCd}


  AND A.EXT_COLOR_CD = #{sExtColorCd}


  AND A.INT_COLOR_CD = #{sIntColorCd}


  AND A.ORD_SEQ = TO_NUMBER(#{sOrdSeq})

SELECT COUNT(1) OVER() AS TOT_CNT , A.SORD AS ORD_TP -- 주문유형(공통코드SAL137) , A.PRTY AS ORD_STAT_CD --오더상태명(공통코드SAL150) , A.CNDY AS ORD_DATE --주문등록일자 , B.SALE_RGST_ID AS CHRG_CD --주문자 , A.SALES_NO AS ORD_SEQ --오더번호 , A.SALES_NO --영업번호 , A.ALOC AS VINM_VLOC --하치장명(공통코드SAL211) , SUBSTR(A.INNO,1,4)||'-'||SUBSTR(A.INNO,5,2)||'-'||SUBSTR(A.INNO,7) AS INNO , A.CARLINE_CD , FN_GET_CARLINE_NM_NC(A.CARLINE_CD) AS CARLINE_NM , A.MODEL_CD , FN_GET_MODEL_NM_NC(A.MODEL_CD) AS MODEL_NM , A.OCN_CD , FN_GET_OCN_NM_NC(A.MODEL_CD, A.OCN_CD) AS OCN_NM , A.EXT_COLOR_CD , FN_GET_EXT_COLOR_NM_NC(A.EXT_COLOR_CD) AS EXT_COLOR_NM , A.INT_COLOR_CD , FN_GET_INT_COLOR_NM_NC(A.INT_COLOR_CD) AS INT_COLOR_NM , CASE WHEN A.ALDY = '00000000' THEN NULL ELSE TO_DATE(A.ALDY, 'YYYYMMDD') END AS PDI_DT --PDI배정일 , C.CAR_VIN_ALLOC_DT AS VIN_DT --VIN배정일 , C.PLT_GI_DT AS RETAIL_DT , A.VIN_NO1 || A.VIN_NO2 AS VIN_NO --VIN NO , C.DLR_GR_DT --딜러입고일 , C.CUST_SALE_DT --딜러출고일 , B.REG_USR_ID , TO_CHAR(B.ORD_DT,'YYYY-MM-DD') AS ORD_DT FROM SA_0416T A, SA_0417T B, SA_0121T C WHERE A.DLR_CD = B.DLR_CD AND A.ORD_YYMM = B.ORD_YY_MM_DT AND A.ORD_WEEK = B.ORD_PRID AND A.SORD = B.ORD_TP AND A.CARLINE_CD = B.CARLINE_CD AND A.VIN_NO1 = C.VIN_NO1(+) AND A.VIN_NO2 = C.VIN_NO2(+) AND A.ORD_SEQ = TO_NUMBER(B.ORD_NO)
AND A.DLR_CD = #{sDlrCd}
AND A.ORD_YYMM = #{sOrdYyMmDt}
AND A.ORD_WEEK = #{sOrdPrid}
AND B.ORD_TP = #{sOrdsTp}
AND A.CARLINE_CD = #{sCarlineCd}
AND A.MODEL_CD = #{sModelCd}
AND A.OCN_CD = #{sOcnCd}
AND A.EXT_COLOR_CD = #{sExtColorCd}
AND A.INT_COLOR_CD = #{sIntColorCd}
AND A.ORD_SEQ = TO_NUMBER(#{sOrdSeq})
ORDER BY DECODE(ORD_STAT_CD,'B',1,'R',2,'O',3,'S',4,'A',5,'R',6,'P',7,'C',8,'F',9,'D',10,'E',11)
