mapper.select:
SELECT COUNT(*) AS VALUE FROM (SELECT AA.* FROM (SELECT A.SYS_DT , A.PTY_ORD_YY_MM_DT , A.PTY_ORD_SERIAL_NO , A.PTY_ORD_SEQ , NVL(A.PTY_ORD_STAT_CD, 'C') AS PTY_ORD_STAT_CD , A.DLR_CD , A.PTY_ORD_WKCNT_CD , DECODE(LENGTH(A.PTY_ORD_YY_MM_DT), 6, SUBSTR(A.PTY_ORD_YY_MM_DT, 3), A.PTY_ORD_YY_MM_DT) || A.PTY_ORD_WKCNT_CD || A.CARLINE_CD || LPAD(A.PTY_ORD_SEQ, 4, '0') AS ORD_NO , B.SALES_NO , A.CARLINE_CD , FN_GET_CARLINE_NM(A.CARLINE_CD) AS CARLINE_NM , A.MODEL_CD , FN_GET_MODEL_NM(A.MODEL_CD) AS MODEL_NM , A.OCN_CD , FN_GET_OCN_NM(A.MODEL_CD, A.OCN_CD) AS OCN_NM , A.EXT_COLOR_CD , FN_GET_EXT_COLOR_NM(A.EXT_COLOR_CD) AS EXT_COLOR_NM , A.INT_COLOR_CD , FN_GET_INT_COLOR_NM(A.INT_COLOR_CD) AS INT_COLOR_NM , A.PTY_ORD_TP , A.PTY_ORD_GRADE_TP , A.PFP_DT , A.ORD_DT , A.PTY_AMT , A.STOCK_CAU_CD /*재고원인*/ , A.ERP_PROC_DT , A.ERP_SEND_YN , A.ERP_REQ_DT , A.PFP_DT AS CHG_PFP_DT /*납기일자에 맵핑됨.*/ , ROW_NUMBER() OVER(PARTITION BY A.SYS_DT, A.DLR_CD, A.PTY_ORD_YY_MM_DT, A.PTY_ORD_WKCNT_CD, A.CARLINE_CD, A.PTY_ORD_SERIAL_NO ORDER BY A.PTY_ORD_SEQ DESC) AS RN FROM SA_0420T A, SA_0416T B WHERE A.DLR_CD = B.DLR_CD AND A.PTY_ORD_YY_MM_DT = B.ORD_YYMM AND A.PTY_ORD_WKCNT_CD = B.ORD_WEEK AND A.CARLINE_CD = B.CARLINE_CD AND A.PTY_ORD_SERIAL_NO = B.SALES_NO AND (A.PORD_STS <> 'D' OR A.PORD_STS IS NULL) AND (A.PTY_ORD_STAT_CD = 'A' OR A.PTY_ORD_STAT_CD IS NULL) AND A.DMS_ORD_CANC_PSB_YN = 'Y') AA WHERE 1 = 1 AND AA.RN = 1
AND AA.DLR_CD = #{sDlrCd}
AND AA.PTY_ORD_TP = #{sPtyOrdTp}
AND AA.SYS_DT = #{sSysDt}
AND AA.STOCK_CAU_CD = #{sStockCauCd}
AND AA.CARLINE_CD = #{sCarlineCd}
AND AA.MODEL_CD = #{sModelCd}
AND AA.OCN_CD = #{sOcnCd}
AND AA.INT_COLOR_CD = #{sIntColorCd}
AND AA.EXT_COLOR_CD = #{sExtColorCd}
AND AA.PTY_ORD_STAT_CD = #{sPtyOrdStatCd}
)

SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM (SELECT AA.* FROM (SELECT A.SYS_DT , TO_DATE(A.PTY_ORD_YY_MM_DT || '01', 'YYYY-MM-DD') AS PTY_ORD_YY_MM_DT , A.PTY_ORD_SERIAL_NO , A.PTY_ORD_SEQ , NVL(A.PTY_ORD_STAT_CD, 'C') AS PTY_ORD_STAT_CD , A.DLR_CD , A.PTY_ORD_WKCNT_CD , DECODE(LENGTH(A.PTY_ORD_YY_MM_DT), 6, SUBSTR(A.PTY_ORD_YY_MM_DT, 3), A.PTY_ORD_YY_MM_DT) || A.PTY_ORD_WKCNT_CD || A.CARLINE_CD || LPAD(A.PTY_ORD_SEQ, 4, '0') AS ORD_NO , B.SALES_NO , A.CARLINE_CD , FN_GET_CARLINE_NM(A.CARLINE_CD) AS CARLINE_NM , A.MODEL_CD , FN_GET_MODEL_NM(A.MODEL_CD) AS MODEL_NM , A.OCN_CD , FN_GET_OCN_NM(A.MODEL_CD, A.OCN_CD) AS OCN_NM , A.EXT_COLOR_CD , FN_GET_EXT_COLOR_NM(A.EXT_COLOR_CD) AS EXT_COLOR_NM , A.INT_COLOR_CD , FN_GET_INT_COLOR_NM(A.INT_COLOR_CD) AS INT_COLOR_NM , A.PTY_ORD_TP , A.PTY_ORD_GRADE_TP , A.PFP_DT AS PFP_DT , A.ORD_DT , A.PTY_AMT , A.STOCK_CAU_CD /*재고원인*/ , CASE WHEN A.STOCK_CAU_CD = 'B' THEN 'C' ELSE A.STOCK_CAU_CD END AS CAUSES_CD , A.ERP_PROC_DT , A.ERP_SEND_YN , A.ERP_REQ_DT , A.PFP_DT AS CHG_PFP_DT /*납기일자에 맵핑됨.*/ , ROW_NUMBER() OVER(PARTITION BY A.SYS_DT, A.DLR_CD, A.PTY_ORD_YY_MM_DT, A.PTY_ORD_WKCNT_CD, A.CARLINE_CD, A.PTY_ORD_SERIAL_NO ORDER BY A.PTY_ORD_SEQ DESC) AS RN FROM SA_0420T A, SA_0416T B WHERE A.DLR_CD = B.DLR_CD AND A.PTY_ORD_YY_MM_DT = B.ORD_YYMM AND A.PTY_ORD_WKCNT_CD = B.ORD_WEEK AND A.CARLINE_CD = B.CARLINE_CD AND A.PTY_ORD_SERIAL_NO = B.SALES_NO AND (A.PORD_STS <> 'D' OR A.PORD_STS IS NULL) AND (A.PTY_ORD_STAT_CD = 'A' OR A.PTY_ORD_STAT_CD IS NULL) AND A.DMS_ORD_CANC_PSB_YN = 'Y') AA WHERE 1 = 1 AND AA.RN = 1
AND AA.DLR_CD = #{sDlrCd}
AND AA.PTY_ORD_TP = #{sPtyOrdTp}
AND AA.SYS_DT = #{sSysDt}
AND AA.STOCK_CAU_CD = #{sStockCauCd}
AND AA.CARLINE_CD = #{sCarlineCd}
AND AA.MODEL_CD = #{sModelCd}
AND AA.OCN_CD = #{sOcnCd}
AND AA.INT_COLOR_CD = #{sIntColorCd}
AND AA.EXT_COLOR_CD = #{sExtColorCd}
AND AA.PTY_ORD_STAT_CD = #{sPtyOrdStatCd}
ORDER BY
AA.PTY_ORD_STAT_CD ASCDESC AA.DLR_CD ASCDESC AA.PTY_ORD_WKCNT_CD ASCDESC AA.ORD_NO ASCDESC AA.SALES_NO ASCDESC AA.CARLINE_NM ASCDESC AA.MODEL_NM ASCDESC AA.OCN_NM ASCDESC AA.EXT_COLOR_NM ASCDESC AA.INT_COLOR_NM ASCDESC AA.PTY_ORD_TP ASCDESC AA.PTY_ORD_GRADE_TP ASCDESC AA.PFP_DT ASCDESC AA.ORD_DT ASCDESC AA.PTY_AMT ASCDESC AA.STOCK_CAU_CD ASCDESC AA.CAUSES ASCDESC AA.ERP_PROC_DT ASCDESC
AA.DLR_CD, AA.ORD_NO DESC
AA.DLR_CD, AA.ORD_NO DESC
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}
