mapper.select:
SELECT COUNT(*) AS VALUE
  FROM SA_0417T
 WHERE DLR_CD = #{sDlrCd}
   AND ORD_STAT_CD = '1D'

   AND ORD_YY_MM_DT = TO_CHAR(#{sOrdYyMmDt}, 'YYYYMM')


   AND ORD_PRID = LPAD(#{sOrdPrid}, '2', '0')


   AND ORD_TP = #{sOrdTp}


   AND CARLINE_CD = #{sCarlineCd}


   AND MODEL_CD = #{sModelCd}


   AND MODEL_CD = #{sOcnCd}


   AND EXT_COLOR_CD = #{sExtColorCd}


   AND INT_COLOR_CD = #{sIntColorCd}

SELECT DLR_CD , ORD_TP , ORD_YY_MM_DT , ORD_PRID , ORD_NO , FN_GET_CARLINE_NM(CARLINE_CD) AS CARLINE_NM , FN_GET_MODEL_NM(MODEL_CD) AS MODEL_NM , FN_GET_OCN_NM(MODEL_CD, OCN_CD) AS OCN_NM , FN_GET_EXT_COLOR_NM(EXT_COLOR_CD) AS EXT_COLOR_NM , FN_GET_INT_COLOR_NM(INT_COLOR_CD) AS INT_COLOR_NM , TO_DATE(PFP_EXAM_DT, 'YYYY-MM-DD') AS PFP_EXAM_DT , ERP_SEND_YN , ORD_GRADE_CD FROM SA_0417T WHERE DLR_CD = #{sDlrCd} AND ORD_STAT_CD = '1D'
AND ORD_YY_MM_DT = TO_CHAR(#{sOrdYyMmDt}, 'YYYYMM')
AND ORD_PRID = LPAD(#{sOrdPrid}, '2', '0')
AND ORD_TP = #{sOrdTp}
AND CARLINE_CD = #{sCarlineCd}
AND MODEL_CD = #{sModelCd}
AND MODEL_CD = #{sOcnCd}
AND EXT_COLOR_CD = #{sExtColorCd}
AND INT_COLOR_CD = #{sIntColorCd}
ORDER BY
DLR_CD ASCDESC CARLINE_NM ASCDESC MODEL_Nm ASCDESC OCN_Nm ASCDESC EXT_COLOR_Nm ASCDESC INT_COLOR_Nm ASCDESC ORD_NO ASCDESC ORD_TP ASCDESC PFP_EXAM_DT ASCDESC ERP_SEND_YN ASCDESC
ORD_YY_MM_DT, ORD_PRID, ORD_NO, CARLINE_NM
ORD_YY_MM_DT, ORD_PRID, ORD_NO, CARLINE_NM

mapper.update:
UPDATE SA_0417T
   SET ORD_STAT_CD = '1A'
     , APPLY_STAT_CD = '1A'
     , ORD_QTY = 0
     , ERP_TARG_YN = 'Y'
     , ERP_SEND_YN = 'N'
 WHERE DLR_CD = #{dlrCd}
   AND ORD_TP = #{ordTp}
   AND ORD_YY_MM_DT = #{ordYyMmDt}
   AND ORD_PRID = #{ordPrid}
   AND ORD_NO = #{ordNo}

mapper.insert:
{CALL SP_SAL_DMS_P_0417T_IF(
      'SAL061'
    , #{dlrCd, mode=IN, jdbcType=VARCHAR}
    , #{ordTp, mode=IN, jdbcType=VARCHAR}
    , #{ordYyMmDt, mode=IN, jdbcType=VARCHAR}
    , #{ordPrid, mode=IN, jdbcType=VARCHAR}
    , #{ordNo, mode=IN, jdbcType=VARCHAR}
)}
