mapper.select:
SELECT SA0113.DLR_CD,SA0113.PROMOTION_CD,SA0113.PROMOTION_NM,SA0113.PROMOTION_TP ,SA0113.START_DT,SA0113.END_DT,SA0113.APPROVE_CD,SA0113.APPLY_TP,SA0113.PROMOTION_AMT ,SA0113.IMS_BUDN_RATE,SA0113.DLR_BUDN_RATE,SA0113.REMARK,SA0113.PROMOTION_OWN_CD ,SA0113.REG_USR_ID,SA0113.REG_DT,SA0113.UPDT_USR_ID,SA0113.UPDT_DT ,SA0116.CONTRACT_TP FROM SA_0113T SA0113 ,(SELECT DLR_CD,PROMOTION_CD,WM_CONCAT(CONTRACT_TP) AS CONTRACT_TP FROM SA_0116T GROUP BY DLR_CD,PROMOTION_CD ) SA0116 WHERE 1=1 AND SA0113.DLR_CD = SA0116.DLR_CD(+) AND SA0113.PROMOTION_CD = SA0116.PROMOTION_CD(+) AND SA0113.USE_YN = 'Y'
AND SA0113.PROMOTION_CD IN (SELECT PROMOTION_CD FROM SA_0115T WHERE MODEL_CD = #{sModelCd}) AND #{sContractDt} BETWEEN SA0113.START_DT AND SA0113.END_DT AND SA0113.APPROVE_CD = 'C' AND SA0113.DLR_CD = #{sDlrCd}
AND PROMOTION_TP = #{sPromotionTp}
AND APPROVE_CD = #{sApproveCd}
AND END_DT >= #{sStartDt} AND START_DT <= #{sEndDt}
ORDER BY
PROMOTION_TP ASCDESC PROMOTION_NM ASCDESC APPROVE_CD ASCDESC START_DT ASCDESC END_DT ASCDESC APPR_TP ASCDESC PROMOTION_AMT ASCDESC PROMOTION_OWN_CD ASCDESC
REG_DT DESC
REG_DT DESC

SELECT COUNT(*) VALUE
  FROM SA_0113T SA0113
      ,(SELECT DLR_CD,PROMOTION_CD,WM_CONCAT(CONTRACT_TP) AS CONTRACT_TP FROM SA_0116T
         GROUP BY DLR_CD,PROMOTION_CD
       ) SA0116
 WHERE 1=1
   AND SA0113.DLR_CD = SA0116.DLR_CD(+)
   AND SA0113.PROMOTION_CD = SA0116.PROMOTION_CD(+)
   AND SA0113.USE_YN = 'Y'


        AND SA0113.PROMOTION_CD IN (SELECT PROMOTION_CD FROM SA_0115T WHERE MODEL_CD = #{sModelCd})
        AND #{sContractDt} BETWEEN SA0113.START_DT AND SA0113.END_DT
        AND SA0113.APPROVE_CD = 'C'
        AND SA0113.DLR_CD = #{sDlrCd}



       AND PROMOTION_TP = #{sPromotionTp}


       AND APPROVE_CD = #{sApproveCd}



       AND END_DT >= #{sStartDt}
       AND START_DT <= #{sEndDt}

mapper.insert:
INSERT INTO SA_0113T
( DLR_CD
 ,PROMOTION_CD
 ,PROMOTION_NM
 ,PROMOTION_TP
 ,START_DT
 ,END_DT
 ,APPROVE_CD
 ,APPLY_TP
 ,PROMOTION_AMT
 ,IMS_BUDN_RATE
 ,DLR_BUDN_RATE
 ,REMARK
 ,USE_YN
 ,REG_USR_ID
 ,REG_DT
 ,UPDT_USR_ID
 ,UPDT_DT
 ,PROMOTION_OWN_CD
 ) VALUES
( #{dlrCd}
 ,(SELECT 'PRM' || DECODE(MAX(PROMOTION_CD),NULL,'PRM0000000001',LPAD(MAX(TO_NUMBER(SUBSTR(PROMOTION_CD,4)))+1,10,0)) FROM SA_0113T)
 ,#{promotionNm}
 ,#{promotionTp}
 ,#{startDt}
 ,#{endDt}
 ,'A'
 ,#{applyTp}
 ,#{promotionAmt}
 ,#{imsBudnRate}
 ,#{dlrBudnRate}
 ,#{remark}
 ,'Y'
 ,#{regUsrId}
 ,SYSDATE
 ,#{regUsrId}
 ,SYSDATE
 ,#{promotionOwnCd}
 )

mapper.update:
UPDATE SA_0113T
   SET APPROVE_CD = 'B'
 WHERE 1=1
   AND DLR_CD = #{dlrCd}
   AND PROMOTION_CD = #{promotionCd}

UPDATE SA_0113T
   SET APPROVE_CD = 'C'
 WHERE 1=1
   AND DLR_CD = #{dlrCd}
   AND PROMOTION_CD = #{promotionCd}

UPDATE SA_0113T
   SET USE_YN = 'N'
 WHERE 1=1
   AND DLR_CD = #{dlrCd}
   AND PROMOTION_CD = #{promotionCd}

UPDATE SA_0113T
   SET PROMOTION_NM = #{promotionNm}
      ,PROMOTION_TP = #{promotionTp}
      ,START_DT = #{startDt}
      ,END_DT = #{endDt}
      ,APPLY_TP = #{applyTp}
      ,PROMOTION_AMT = #{promotionAmt}
      ,IMS_BUDN_RATE = #{imsBudnRate}
      ,DLR_BUDN_RATE = #{dlrBudnRate}
      ,REMARK = #{remark}
      ,PROMOTION_OWN_CD = #{promotionOwnCd}
      ,UPDT_USR_ID = #{updtUsrId}
      ,UPDT_DT = SYSDATE
 WHERE 1=1
   AND DLR_CD = #{dlrCd}
   AND PROMOTION_CD = #{promotionCd}

mapper.select:
SELECT DLR_CD,PROMOTION_CD,CUST_CD,REG_USR_ID,REG_DT,UPDT_USR_ID,UPDT_DT
  FROM SA_0114T
 WHERE 1=1
   AND DLR_CD = #{dlrCd}
   AND PROMOTION_CD = #{promotionCd}
   AND CUST_CD = #{custCd}
