mapper.select:
/* [chn.bhmc.dms.sal.rcp.service.dao.SalesVhclCbinRcitDAO.selectSalesAddTaxReceiptSearchCnt] */ SELECT COUNT(T1.CAR_ID) AS VALUE FROM ( SELECT X.* FROM ( SELECT receipt.RECEIPT_ID, car.CAR_ID, car.VIN_NO, car.VIN_NO1, car.VIN_NO2, receipt.DLR_CD, receipt.RECEIPT_NO, receipt.RECEIPT_CD, receipt.RECEIPT_ISS_DT, receipt.SDLR_CD, receipt.REG_DT AS UPLOAD_DT, (SELECT SDPT_CD FROM CM_0101T WHERE DLR_CD = receipt.DLR_CD) AS SDPT_CD, (SELECT DIST_OFFICE_CD FROM CM_0101T WHERE DLR_CD = receipt.DLR_CD) AS DIST_OFFICE_CD, (SELECT SALE_DLR_SUNG_CD FROM CM_0101T WHERE DLR_CD = receipt.DLR_CD) AS SUNG_CD FROM SA_0502T receipt INNER JOIN SA_0508T receiptCar ON receiptCar.RECEIPT_ID = receipt.RECEIPT_ID INNER JOIN SA_0121T car ON car.CAR_ID = receiptCar.CAR_ID ) X
AND X.UPLOAD_DT >= #{sUploadStDt}
AND X.UPLOAD_DT < #{sUploadEdDt}+1
AND X.RECEIPT_ISS_DT >= #{sReceiptStDt}
AND X.RECEIPT_ISS_DT < #{sReceiptEdDt}+1
AND X.RECEIPT_NO = #{sReceiptNo}
AND X.VIN_NO LIKE '%'||#{sVinNo}||'%'
AND X.DLR_CD LIKE '%'||#{sDlrCd}||'%'
AND X.SDPT_CD = #{sSdptCd}
AND X.DIST_OFFICE_CD = #{sDistOfficeCd}
AND X.SUNG_CD = #{sSungCd}
) T1

/* [chn.bhmc.dms.sal.rcp.service.dao.SalesVhclCbinRcitDAO.selectSalesAddTaxReceiptSearchData] */ SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT X.* FROM ( SELECT receipt.RECEIPT_ID, car.CAR_ID, car.VIN_NO, car.VIN_NO1, car.VIN_NO2, receipt.DLR_CD, receipt.TAXPAYER_ID_CD, receipt.RECEIPT_NO, receipt.RECEIPT_CD, receipt.BUY_NM, receipt.RECEIPT_ISS_DT, receipt.SCAN_SUM_AMT, receipt.CARLINE_CD, FN_GET_CARLINE_NM(receipt.CARLINE_CD) AS CARLINE_NM, receipt.MODEL_CD, FN_GET_MODEL_TP_NM(car.MODEL_CD, car.OCN_CD) AS MODEL_NM, receipt.JEONGPAN_NO, receipt.EXT_COLOR_CD, FN_GET_EXT_COLOR_NM(receipt.EXT_COLOR_CD) AS EXT_COLOR_NM, receipt.WS_PRC, receipt.PLT_GI_DT, receipt.SUM_AMT, receipt.TAX_AMT, receipt.TMR_CD, receipt.EVAL_RSLT_CD, receipt.ERR_UP_CD, receipt.SDLR_CD, receipt.REG_DT AS UPLOAD_DT, receipt.SDLR_TP, receipt.SDLR_NM, (SELECT SDPT_CD FROM CM_0101T WHERE DLR_CD = receipt.DLR_CD) AS SDPT_CD, (SELECT DIST_OFFICE_CD FROM CM_0101T WHERE DLR_CD = receipt.DLR_CD) AS DIST_OFFICE_CD, (SELECT SALE_DLR_SUNG_CD FROM CM_0101T WHERE DLR_CD = receipt.DLR_CD) AS SUNG_CD FROM SA_0502T receipt INNER JOIN SA_0508T receiptCar ON receiptCar.RECEIPT_ID = receipt.RECEIPT_ID INNER JOIN SA_0121T car ON car.CAR_ID = receiptCar.CAR_ID ORDER BY receipt.RECEIPT_NO, receipt.TAXPAYER_ID_CD ) X
AND X.UPLOAD_DT >= #{sUploadStDt}
AND X.UPLOAD_DT < #{sUploadEdDt}+1
AND X.RECEIPT_ISS_DT >= #{sReceiptStDt}
AND X.RECEIPT_ISS_DT < #{sReceiptEdDt}+1
AND X.RECEIPT_NO = #{sReceiptNo}
AND X.VIN_NO LIKE '%'||#{sVinNo}||'%'
AND X.DLR_CD LIKE '%'||#{sDlrCd}||'%'
AND X.SDPT_CD = #{sSdptCd}
AND X.DIST_OFFICE_CD = #{sDistOfficeCd}
AND X.SUNG_CD = #{sSungCd}
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}
ORDER BY
PLT_GI_DT DESCASC RECEIPT_NO DESCASC RECEIPT_ISS_DT DESCASC UPLOAD_DT DESCASC
PLT_GI_DT DESC

/* [chn.bhmc.dms.sal.rcp.service.dao.SalesVhclCbinRcitDAO.selectAddTaxReceiptCnt] */
    SELECT COUNT(*) AS VALUE
      FROM SA_0502T
     WHERE RECEIPT_NO = #{sReceiptNo}

