/* [chn.bhmc.dms.sal.rcp.service.dao.SalesAddTaxRcitDAO.insertSalesAddTaxReceiptSave] */


    SELECT NVL(MAX(SEQ), 0) + 1 AS SEQ
      FROM SA_0502T
     WHERE CAR_ID = #{carId}


INSERT INTO SA_0502T(
                      CAR_ID
                     ,SEQ
                     ,VIN_NO
                     ,VIN_NO1
                     ,VIN_NO2
                     ,DLR_CD
                     ,TAXPAYER_ID_CD
                     ,RECEIPT_NO
                     ,RECEIPT_CD
                     ,BUY_NM
                     ,RECEIPT_ISS_DT
                     ,SCAN_SUM_AMT
                     ,CARLINE_CD
                     ,MODEL_CD
                     ,JEONGPAN_NO
                     ,EXT_COLOR_CD
                     ,WS_PRC
                     ,PLT_GI_DT
                     ,SUM_AMT
                     ,TAX_AMT
                     ,FILE_NO
                     ,FILE_SEQ
                     ,FILE_SIZE_VAL
                     ,RECEIPT_IMG
                     ,TMR_CD
                     ,EVAL_RSLT_CD
                     ,ERR_UP_CD
                     ,SDLR_CD
                     ,SDLR_TP
                     ,SDLR_NM
                     ,REMARK
                     ,REG_USR_ID
                     ,REG_DT
                     ,UPDT_USR_ID
                     ,UPDT_DT
    )VALUES(
             #{carId}
            ,(SELECT NVL(MAX(SEQ)+1, 1) FROM SA_0502T WHERE CAR_ID = #{carId})
            ,#{vinNo}
            ,SUBSTR(#{vinNo}, 1, 11)
            ,SUBSTR(#{vinNo}, 12, 6)
            ,#{dlrCd}
            ,#{taxpayerIdCd}
            ,#{receiptNo}
            ,#{receiptCd}
            ,#{buyNm}
            ,#{receiptIssDt}
            ,#{scanSumAmt}
            ,#{carlineCd}
            ,#{modelCd}
            ,#{jeongpanNo}
            ,#{extColorCd}
            ,#{wsPrc}
            ,#{pltGiDt}
            ,#{sumAmt}
            ,#{taxAmt}
            ,'' -- fileNo
            ,0 --fileSeq
            ,0 --fileSizeVal
            ,#{receiptImg}
            ,'' --tmrCd
            ,'' --evalRsltCd
            ,'' --errUpCd
            ,#{secDlrCd}
            ,#{sdlrTp}
            ,#{sdlrNm}
            ,'' -- remark
            ,#{regUsrId}
            ,#{regDt}
            ,#{regUsrId}
            ,#{updtDt}
    )

