mapper.select:
SELECT COUNT(*) FROM SA_0411T SA0411 /* 집단판매업무권한신청정보 */ , (SELECT SA0443.DLR_CD,SA0443.USR_ID,SA0443.REQ_SEQ,SA0443.CARLINE_CD,SA0443.MODEL_CD,SA0443.APP_QTY,SA0443.REQ_RC FROM SA_0443T SA0443 , (SELECT DLR_CD,USR_ID,REQ_SEQ,MIN(CARLINE_CD) AS CARLINE_CD
FROM (SELECT * FROM SA_0443T WHERE CARLINE_CD = #{sCarlineCd}
AND MODEL_CD = #{sModelCd}
)
FROM SA_0443T
GROUP BY DLR_CD,USR_ID,REQ_SEQ ) A WHERE 1=1 AND SA0443.DLR_CD = A.DLR_CD AND SA0443.USR_ID = A.USR_ID AND SA0443.REQ_SEQ = A.REQ_SEQ AND SA0443.CARLINE_CD = A.CARLINE_CD ) SA0443 /* 집단판매업무권한신청정보 - 차량내역테이블 */ , (SELECT DLR_CD,USR_ID,REQ_SEQ,COUNT(CARLINE_CD) AS CAR_CNT,SUM(APP_QTY) AS SUM_QTY FROM SA_0443T GROUP BY DLR_CD,USR_ID,REQ_SEQ) B WHERE 1=1 AND SA0411.DLR_CD = SA0443.DLR_CD(+) AND SA0411.USR_ID = SA0443.USR_ID(+) AND SA0411.REQ_SEQ = SA0443.REQ_SEQ(+) AND SA0443.DLR_CD = B.DLR_CD AND SA0443.USR_ID = B.USR_ID AND SA0443.REQ_SEQ = B.REQ_SEQ AND SA0411.DEL_YN != 'Y'
and SA0411.DLR_CD = #{sDlrCd} -- 딜러코드
and SA0411.USR_ID = #{sUsrId} -- 사용자코드
AND SA0411.APPROVE_CD = #{sApproveCd} -- 상태
AND SA0411.REQ_DT >= #{sReqStartDt}
AND SA0411.REQ_DT < #{sReqEndDt} +1

SELECT COUNT(*)
  FROM SA_0411T A               /* 집단판매업무권한신청정보 */
  LEFT OUTER JOIN CM_0101T B
    ON A.DLR_CD = B.DLR_CD
 WHERE 1 = 1

   AND A.DLR_CD = #{sDlrCd}


   AND A.USR_ID = #{sUsrId}


   AND A.REQ_SEQ = #{sReqSeq}

SELECT A.DLR_CD --딜러코드 , B.DLR_NM , A.USR_ID --사용자ID , A.REQ_SEQ --신청번호 , A.REQ_SAL_NO , A.REQ_START_DT -- 요청시작일 , A.REQ_END_DT -- 요청종료일 , A.REQ_DT -- 요청일 , A.CUST_NM -- 고객명 , A.ATC_DSTIN_CD --항목 , A.ATC_INTRO_CONT --항목소개 , A.FEE_POLICY_CONT --수수료상관정책 , A.DMS_OPEN_YN --DMS 공시여부 , A.POA_OFFER_YN --위임장 제공여부 , A.APPROVE_CD --상태 , A.DEL_YN --삭제여부 , A.APPLY_START_DT --임명시작일자 , A.APPLY_END_DT --임명종료일자 FROM SA_0411T A /* 집단판매업무권한신청정보 */ LEFT OUTER JOIN CM_0101T B ON A.DLR_CD = B.DLR_CD WHERE 1 = 1
AND A.DLR_CD = #{sDlrCd}
AND A.USR_ID = #{sUsrId}
AND A.REQ_SEQ = #{sReqSeq}

SELECT A.DLR_CD --딜러코드 , B.DLR_NM , A.USR_ID --사용자ID , A.REQ_SEQ --신청번호 , A.REQ_SAL_NO , A.REQ_START_DT -- 요청시작일 , A.REQ_END_DT -- 요청종료일 , A.REQ_DT -- 요청일 , A.CUST_NM -- 고객명 , A.ATC_DSTIN_CD --항목 , A.ATC_INTRO_CONT --항목소개 , A.FEE_POLICY_CONT --수수료상관정책 , A.DMS_OPEN_YN --DMS 공시여부 , A.POA_OFFER_YN --위임장 제공여부 , A.APPROVE_CD --상태 , A.DEL_YN --삭제여부 , A.APPLY_START_DT --임명시작일자 , A.APPLY_END_DT --임명종료일자 FROM SA_0411T A /* 집단판매업무권한신청정보 */ LEFT OUTER JOIN CM_0101T B ON A.DLR_CD = B.DLR_CD WHERE 1 = 1
AND A.DLR_CD = #{sDlrCd}
AND A.USR_ID = #{sUsrId}
AND A.REQ_SEQ = #{sReqSeq}

mapper.update:
