   SET ATC_DSTIN_CD    = #{atcDstinCd}
     , CUST_NM         = #{custNm}
     , ATC_INTRO_CONT  = #{atcIntroCont}
     , FEE_POLICY_CONT = #{feePolicyCont}
     , POA_OFFER_YN    = #{poaOfferYn}
     , UPDT_USR_ID     = #{updtUsrId}
     , UPDT_DT         = SYSDATE
 WHERE DLR_CD = #{dlrCd}
   AND USR_ID = #{usrId}
   AND REQ_SEQ = #{reqSeq}

UPDATE SA_0411T
   SET DEL_YN = 'Y'
     , APPROVE_CD = 'D'
     , UPDT_USR_ID = #{updtUsrId}
     , UPDT_DT = SYSDATE
 WHERE DLR_CD = #{dlrCd}
   AND USR_ID = #{usrId}
   AND REQ_SEQ = #{reqSeq}

UPDATE SA_0411T
   SET APPROVE_CD = 'B'
     , UPDT_USR_ID = #{updtUsrId}
     , UPDT_DT = SYSDATE
 WHERE DLR_CD = #{dlrCd}
   AND USR_ID = #{usrId}
   AND REQ_SEQ = #{reqSeq}

mapper.select:
SELECT COUNT(T2.REQ_SEQ) AS VALUE FROM ( SELECT T1.* ,CASE WHEN T1.LAST_APPROVE_CNT < T1.EVAL_USR_GRDE AND T1.APPROVE_CD ='B' THEN '02' WHEN T1.LAST_APPROVE_CNT = T1.EVAL_USR_GRDE AND T1.APPROVE_CD ='C' THEN '01' WHEN T1.LAST_APPROVE_CNT < T1.EVAL_USR_GRDE AND T1.APPROVE_CD ='C' THEN '02' ELSE '01' END AS EVAL_YN -- 01 : 기심사 , 02 : 미심사 ,CASE WHEN T1.EVAL_USR_GRDE IS NULL THEN 'N' WHEN (T1.EVAL_USR_GRDE <= T1.LAST_APPROVE_CNT AND T1.APPROVE_CD = 'D' ) THEN 'Y' WHEN (T1.EVAL_USR_GRDE > T1.LAST_APPROVE_CNT AND T1.APPROVE_CD != 'D') THEN 'Y' WHEN T1.APPROVE_CD != 'D' THEN 'Y' ELSE 'N' END SRC_YN FROM ( SELECT A.DLR_CD, --딜러코드 B.DLR_NM, --딜러명 B.DIST_OFFICE_CD, (SELECT OFF_NM FROM CM_2601T Y WHERE Y.DIVI_CD = B.SDPT_CD AND Y.OFF_CD = B.DIST_OFFICE_CD ) AS OFFICE_NM, A.USR_ID, --사용자ID A.CUST_NM, --고객명 B.SDPT_CD, A.REQ_SEQ, A.ATC_DSTIN_CD, --사부소 A.REQ_DT, --요청일자 A.APPROVE_CD, --상태 ( SELECT NVL(SUM(APP_QTY),0) FROM SA_0443T WHERE DLR_CD = A.DLR_CD AND USR_ID = A.USR_ID AND REQ_SEQ = A.REQ_SEQ )AS SUM_QTY, ( SELECT NVL(MAX(EVAL_RSLT_YN), 'N') FROM SA_0410T WHERE DLR_CD = A.DLR_CD AND USR_ID = A.USR_ID AND REQ_SEQ = A.REQ_SEQ )AS RSLT_YN, ( SELECT NVL(MAX(REQ_NO), 0) FROM SA_0410T WHERE DLR_CD = A.DLR_CD AND USR_ID = A.USR_ID AND REQ_SEQ = A.REQ_SEQ )AS REQ_NO, A.EVAL_GRADE_CD, NVL(A.LAST_APPROVE_CNT,0) AS LAST_APPROVE_CNT, ( SELECT EVAL_GRADE_CD FROM SA_0156T WHERE 1=1 AND DLR_CD = #{sApporveDlrCd} AND USR_ID = #{sApporveUsrId} AND WRK_DSTIN_CD = #{sWrkDstinCd} )AS EVAL_USR_GRDE, A.REQ_SAL_NO --신청번호 FROM SA_0411T A , CM_0101T B WHERE B.DLR_CD = A.DLR_CD AND A.APPROVE_CD != 'A' AND A.DEL_YN != 'Y'
AND A.DLR_CD = #{sDlrCd} -- 딜러코드
AND A.APPROVE_CD = #{sApproveCd} -- 상태
AND A.REQ_DT >= #{sReqStartDt}
AND A.REQ_DT < #{sReqEndDt} +1
AND A.REQ_SAL_NO LIKE '%'||#{sReqSeq}||'%'
AND B.SDPT_CD = #{sDiviCd}
AND B.DIST_OFFICE_CD = #{sOffCd}
AND EXISTS ( SELECT MNG_DLR_CD FROM SA_0439T WHERE DLR_CD = 'A07AA' AND USR_ID = #{sApporveUsrId} AND MNG_DLR_CD = A.DLR_CD ) AND EXISTS ( SELECT DLR_CD FROM CM_0101T WHERE DLR_CD = A.DLR_CD AND DMS_USE_YN = 'Y' AND OPEN_DT <= TO_CHAR(SYSDATE, 'YYYYMMDD') ) )T1 WHERE LAST_APPROVE_CNT >= #{sUsrEvalGrade}-1 ) T2 WHERE SRC_YN ='Y'
AND T2.EVAL_YN = #{sEvalRsltYn}

SELECT * FROM ( SELECT ROWNUM AS RNUM, T2.* FROM ( SELECT T1.* ,CASE WHEN T1.LAST_APPROVE_CNT < T1.EVAL_USR_GRDE AND T1.APPROVE_CD ='B' THEN '02' WHEN T1.LAST_APPROVE_CNT = T1.EVAL_USR_GRDE AND T1.APPROVE_CD ='C' THEN '01' WHEN T1.LAST_APPROVE_CNT < T1.EVAL_USR_GRDE AND T1.APPROVE_CD ='C' THEN '02' ELSE '01' END AS EVAL_YN -- 01 : 기심사 , 02 : 미심사 ,CASE WHEN T1.EVAL_USR_GRDE IS NULL THEN 'N' WHEN (T1.EVAL_USR_GRDE <= T1.LAST_APPROVE_CNT AND T1.APPROVE_CD = 'D' ) THEN 'Y' WHEN (T1.EVAL_USR_GRDE > T1.LAST_APPROVE_CNT AND T1.APPROVE_CD != 'D') THEN 'Y' WHEN T1.APPROVE_CD != 'D' THEN 'Y' ELSE 'N' END SRC_YN FROM ( SELECT A.DLR_CD, --딜러코드 B.DLR_NM, --딜러명 B.DIST_OFFICE_CD, (SELECT OFF_NM FROM CM_2601T Y WHERE Y.DIVI_CD = B.SDPT_CD AND Y.OFF_CD = B.DIST_OFFICE_CD ) AS OFFICE_NM, A.USR_ID, --사용자ID A.CUST_NM, --고객명 B.SDPT_CD, (SELECT DIVI_NM FROM CM_2501T WHERE DIVI_CD = B.SDPT_CD) AS SDPT_NM, A.REQ_SEQ, A.ATC_DSTIN_CD, --사부소 A.REQ_DT, --요청일자 A.APPROVE_CD, --상태 ( SELECT NVL(SUM(APP_QTY),0) FROM SA_0443T WHERE DLR_CD = A.DLR_CD AND USR_ID = A.USR_ID AND REQ_SEQ = A.REQ_SEQ )AS SUM_QTY, ( SELECT NVL(MAX(EVAL_RSLT_YN), 'N') FROM SA_0410T WHERE DLR_CD = A.DLR_CD AND USR_ID = A.USR_ID AND REQ_SEQ = A.REQ_SEQ )AS RSLT_YN, ( SELECT NVL(MAX(REQ_NO), 0) FROM SA_0410T WHERE DLR_CD = A.DLR_CD AND USR_ID = A.USR_ID AND REQ_SEQ = A.REQ_SEQ )AS REQ_NO, A.EVAL_GRADE_CD, NVL(A.LAST_APPROVE_CNT,0) AS LAST_APPROVE_CNT, ( SELECT EVAL_GRADE_CD FROM SA_0156T WHERE 1=1 AND DLR_CD = #{sApporveDlrCd} AND USR_ID = #{sApporveUsrId} AND WRK_DSTIN_CD = #{sWrkDstinCd} )AS EVAL_USR_GRDE, A.REQ_SAL_NO, --신청번호 A.APPROVE_END_DT FROM SA_0411T A , CM_0101T B WHERE B.DLR_CD = A.DLR_CD AND A.APPROVE_CD != 'A' AND A.DEL_YN != 'Y'
AND A.DLR_CD = #{sDlrCd} -- 딜러코드
AND A.APPROVE_CD = #{sApproveCd} -- 상태
AND A.REQ_DT >= #{sReqStartDt}
AND A.REQ_DT < #{sReqEndDt} +1
AND A.REQ_SAL_NO LIKE '%'||#{sReqSeq}||'%'
AND B.SDPT_CD = #{sDiviCd}
AND B.DIST_OFFICE_CD = #{sOffCd}
AND EXISTS ( SELECT MNG_DLR_CD FROM SA_0439T WHERE DLR_CD = 'A07AA' AND USR_ID = #{sApporveUsrId} AND MNG_DLR_CD = A.DLR_CD ) AND EXISTS ( SELECT DLR_CD FROM CM_0101T WHERE DLR_CD = A.DLR_CD AND DMS_USE_YN = 'Y' AND OPEN_DT <= TO_CHAR(SYSDATE, 'YYYYMMDD') ) )T1 WHERE LAST_APPROVE_CNT >= #{sUsrEvalGrade}-1 ORDER BY
T1.DIST_CD ASCDESC T1.DLR_NM ASCDESC T1.DLR_CD ASCDESC T1.USR_ID ASCDESC T1.REQ_SEQ ASCDESC T1.ATC_DSTIN_CD ASCDESC T1.CUST_NM ASCDESC T1.REQ_DT ASCDESC T1.APPROVE_CD ASCDESC T1.SUM_QTY ASCDESC
T1.DLR_NM ASC ,T1.DLR_CD ASC ,T1.REQ_SEQ DESC
T1.DLR_NM ASC ,T1.DLR_CD ASC ,T1.REQ_SEQ DESC
) T2 WHERE SRC_YN ='Y'
AND ROWNUM <= #{lastIndex}
AND T2.EVAL_YN = #{sEvalRsltYn}
)
RNUM > #{firstIndex}

mapper.update:
UPDATE SA_0411T
   SET APPROVE_CD       = #{approveCd}
     , APPROVE_END_DT   = SYSDATE
     , APPLY_START_DT   = #{applyStartDt}
     , APPLY_END_DT     = #{applyEndDt}
     , CONFIRM_DT       = #{confirmDt}
     , LAST_APPROVE_CNT = #{usrEvalGrade}
     , LAST_APPROVE_DT  = SYSDATE
     , UPDT_USR_ID      = #{usrId}
     , UPDT_DT          = SYSDATE
 WHERE DLR_CD           = #{dlrCd}
   AND USR_ID           = #{usrId}
   AND REQ_SEQ          = #{reqSeq}

