  FROM SA_0424T A
LEFT OUTER JOIN SA_0423T B
   ON A.DLR_CD = B.DLR_CD
  AND A.USR_ID = B.USR_ID
  AND A.REQ_NO = B.REQ_NO
  AND B.PEVAL_ID = #{sUserId}
WHERE A.DLR_CD = #{sDlrCd}
  AND A.USR_ID = #{sUsrId}
  AND A.REQ_NO = #{sReqNo}
GROUP BY A.DLR_CD, A.USR_ID, A.REQ_NO

