OIN ( SELECT USR_ID, MNG_DLR_CD FROM SA_0439T GROUP BY USR_ID, MNG_DLR_CD ) X ON A.DLR_CD = X.MNG_DLR_CD LEFT OUTER JOIN ( SELECT A.SDPT_CD -- 사업부코드 , (SELECT DIVI_NM FROM CM_2501T Z WHERE Z.DIVI_CD = A.SDPT_CD) AS SDPT_NM , A.DIST_OFFICE_CD -- 사무소코드 , (SELECT OFF_NM FROM CM_2601T Z WHERE Z.DIVI_CD = A.SDPT_CD AND Z.OFF_CD = A.DIST_OFFICE_CD ) AS DIST_OFFICE_NM , A.SALE_DLR_SUNG_CD ,(SELECT CSTL_NM FROM CM_2701T WHERE CSTL_CD = A.SALE_DLR_SUNG_CD) AS SALE_DLR_SUNG_NM , A.SALE_DLR_CITY_CD , (SELECT CITY_NM FROM CM_3201T WHERE CSTL_CD = A.SALE_DLR_SUNG_CD AND CITY_CD = A.SALE_DLR_CITY_CD ) AS SALE_DLR_CITY_NM , A.DLR_CD , A.DLR_NM FROM CM_0101T A -- 딜러정보 ) T1 ON A.DLR_CD = T1.DLR_CD LEFT OUTER JOIN ( SELECT DLR_CD , USR_ID , REQ_NO , MAX(CARLINE_CD) AS CARLINE_CD , MAX(MODEL_CD) AS MODEL_CD , MAX(OCN_CD) AS OCN_CD , MAX(EXT_COLOR_CD) AS EXT_COLOR_CD , MAX(INT_COLOR_CD) AS INT_COLOR_CD , SUM(SPC_CAR_QTY) AS SPC_CAR_QTY -- 수량 합계 FROM SA_0422T GROUP BY DLR_CD, USR_ID, REQ_NO ) B /* 특수차판매신청 DETAIL */ ON A.DLR_CD = B.DLR_CD AND A.USR_ID = B.USR_ID AND A.REQ_NO = B.REQ_NO LEFT OUTER JOIN CM_0101T C ON A.DLR_CD = C.DLR_CD LEFT OUTER JOIN ( SELECT DLR_CD , USR_ID , REQ_NO , MAX(SPC_ORD_SEQ) AS SPC_ORD_SEQ -- 최종 심사 차수 , MAX(EVAL_DT) AS EVAL_DT -- 심사일자 , MAX(EVAL_RSLT_YN) as EVAL_RSLT_YN -- 심사결과여부 FROM SA_0423T GROUP BY DLR_CD, USR_ID, REQ_NO ) D ON A.DLR_CD = D.DLR_CD AND A.USR_ID = D.USR_ID AND A.REQ_NO = D.REQ_NO LEFT OUTER JOIN ( SELECT DLR_CD , USR_ID , REQ_NO , MAX(EVAL_RSLT_YN) AS USE_EVAL_YN -- 사용자의 심사 여부 FROM SA_0423T WHERE 1=1 GROUP BY DLR_CD, USR_ID, REQ_NO ) E ON A.DLR_CD = E.DLR_CD AND A.USR_ID = E.USR_ID AND A.REQ_NO = E.REQ_NO WHERE A.TP_CD = 'N7' AND A.DEL_YN != 'Y' AND A.APPROVE_CD IN ('B', 'C', 'D') /* 승인신청, 승인, 불승인*/ AND X.usr_id = #{sUserId} and EXISTS ( SELECT dlr_cd FROM cm_0101t WHERE dlr_cd = a.dlr_cd AND dms_use_yn = 'Y' )
AND A.DLR_CD = #{sDlrCd} -- 딜러코드
AND A.USR_ID = #{sUsrId}
AND A.REQ_NO = #{sReqNo}
AND A.REQ_CAR_NO = #{sReqCarNo}
AND A.APPROVE_CD = #{sApproveCd} -- 상태
AND A.TP_CD = #{sTpCd} -- 차량유형
AND A.REQ_DT >= TRUNC(#{sStartReqDt},'DD')
AND A.REQ_DT < TRUNC(#{sEndReqDt},'DD')+1
AND A.REQ_CAR_NO LIKE #{sReqCarNo}
AND A.REQ_CAR_NO = #{sReqCarNo}
AND D.EVAL_DT >= TRUNC(#{sStartEvalDt},'DD') AND D.EVAL_DT < TRUNC(#{sEndEvalDt},'DD')+1
AND D.EVAL_RSLT_YN = #{sEvalRsltYn}
AND A.FSDM_AGR_YN = #{sFsdmAgrYn}
AND DECODE(NVL(A.UPLOAD_SEQ, 'N'), 'N', 'N', 'Y') = #{sFileYn}
) K WHERE K.USE_EVAL <= K.SPC_ORD_SEQ+1 AND K.USE_EVAL <= K.MAX_EVAL ) Z WHERE 1 = 1
AND Z.USE_APPROVE_CD = #{sUseApproveCd}
AND Z.APPROVES_CD = #{sApprovesCd}
ORDER BY Z.REQ_NO DESC
