mapper.select:
 /* [chn.bhmc.dms.ser.svi.service.dao.LtsModelMappingDAO.selectLtsModelByCondition] */




SELECT LTS_MODEL_CD

           , LTS_EN_NM  AS LTS_MODEL_NM 
            , LTS_CHN_NM AS LTS_MODEL_NM

  FROM SE_1064T


   AND LTS_MODEL_CD LIKE #{sLtsModelCd}

         OR LTS_EN_NM LIKE #{sLtsModelCd} 
         OR LTS_CHN_NM LIKE #{sLtsModelCd}




 ORDER BY REPLACE(LTS_MODEL_CD,'ALL','1')

        /* [chn.bhmc.dms.ser.svi.service.dao.LtsModelMappingDAO.selectLtsModelByCondition] */




       SELECT LTS_MODEL_CD

                    , LTS_EN_NM  AS LTS_MODEL_NM 
                    , LTS_CHN_NM AS LTS_MODEL_NM

         FROM SE_1064T


          AND LTS_MODEL_CD LIKE #{sLtsModelCd}

               OR LTS_EN_NM LIKE #{sLtsModelCd} 
                OR LTS_CHN_NM LIKE #{sLtsModelCd} 






		ORDER BY REPLACE(LTS_MODEL_CD,'ALL','1')
