mapper.select:
/* [chn.bhmc.dms.ser.wac.service.dao.SanbaoTechApproveDAO.selectSanbaoTechApproveByCondition] */
SELECT * FROM ( SELECT ROWNUM AS RNUM , T1.* FROM ( SELECT DLR_CD --딜러코드 ,VIN_NO --차대번호 ,DOC_NO --문서번호 ,STAT_CD --상태코드 ,ENGIN_NO --엔진번호 ,CAR_OWNER_ID --차량소유자ID ,CAR_OWNER_NM --차량소유자명 ,JUDG_DT --판정일자 ,JUDG_NM --판정명 ,JUDG_ID --판정ID ,RUN_DIST_VAL --주행거리값 ,TEL_NO --전화번호 ,WART_START_DT --보증시작일자 ,CRE_DT --생산일자 ,PBLM_ERR_CD --문제고장코드 ,EMGC_ALRT_CD --긴급경계코드 ,OPN_TP --의견유형 ,EMGC_REASON_CONT --긴급사유내용 ,DLR_PROC_OPN_CONT --딜러처리의견내용 ,FILE_KEY_NM --첨부파일키명 ,DLR_SIGN_REQ_DT --딜러결재요청일자 ,APPROVE_NO --승인번호 ,BMP_PROC_CONT --BMP처리내용 ,BRAND_CD --브랜드코드 ,NVL(TRUNC( NVL(JUDG_DT,SYSDATE) -DLR_SIGN_REQ_DT),0) AS DELAY_DAY -- 지연일자 FROM SE_0770T WHERE STAT_CD != 'O'
AND VIN_NO LIKE '%'||#{sVinNo} ||'%'
AND STAT_CD = #{sStatCd} --상태코드
AND REG_DT >= #{sFromDt} --작성일자
AND REG_DT <= #{sToDt}+1 --작성일자
AND USE_YN = #{sUseYn}
ORDER BY DOC_NO DESC )T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}
