mapper.insert:
/* [chn.bhmc.dms.par.pmm.service.dao.BinLocationMasterDAO.insertBinLocationMaster] */
INSERT INTO PT_0222T(
    DLR_CD
    ,LOC_ID
    ,PLT_CD
    ,STRGE_CD
    ,LVL_ID
    ,LOC_CD
    ,LOC_NM
    ,UPPER_LOC_ID
    ,MAX_QTY
    ,LMT_MNG_YN
    ,ITEM_CD
    ,REMARK
    ,MAIN_LOC_YN
    ,BASE_LVL_ID
    ,UPPER_LVL_ID
    ,AVLB_STOCK_QTY
    ,RESV_STOCK_QTY
    ,CLAM_STOCK_QTY
    ,REG_USR_ID
    ,REG_DT
) VALUES (
    #{dlrCd}
    ,#{locId}
    ,#{pltCd}
    ,#{strgeCd}
    ,#{lvlId}
    ,#{locCd}
    ,#{locNm}
    ,#{upperLocId}
    ,#{maxQty}
    ,#{lmtMngYn}
    ,#{itemCd}
    ,#{remark}
    ,#{mainLocYn}
    ,( select count(0)+1 from PT_0222T A WHERE A.DLR_CD = #{dlrCd} AND A.ITEM_CD IS NULL AND A.MAIN_LOC_YN = 'N' )
    ,#{upperLvlId}
    ,#{avlbStockQty}
    ,#{resvStockQty}
    ,#{clamStockQty}
    ,#{regUsrId}
    ,SYSDATE
)

