/* [chn.bhmc.dms.par.pmm.service.dao.BinLocationMasterDAO.deleteBinLocationMaster] */
DELETE FROM PT_0222T
WHERE DLR_CD = #{dlrCd}
AND LOC_CD = #{locCd}

mapper.select:
/* [chn.bhmc.dms.par.pmm.service.dao.BinLocationMasterDAO.selectBinLocationMasterByKey] */
SELECT * FROM PT_0222T
WHERE DLR_CD = #{dlrCd}
AND LOC_CD = #{locCd}

/* [chn.bhmc.dms.par.pmm.service.dao.BinLocationMasterDAO.selectBinLocationMasterTreeListByCondition] */
SELECT
    ROW_NUMBER() OVER(ORDER BY A.LVL_ID, A.LOC_CD ASC) AS ID
    ,A.LVL_ID
    ,A.LOC_CD
    ,A.LOC_NM
    ,(
      SELECT
          C.PARENT_ID
        FROM
        (
        SELECT
            ROW_NUMBER() OVER(ORDER BY B.LVL_ID, B.LOC_CD ASC) AS PARENT_ID
            ,B.LOC_CD
        FROM PT_0222T B
        WHERE B.ITEM_CD IS null
        AND B.STRGE_CD = #{sStrgeCd}
        )C WHERE C.LOC_CD = A.UPPER_LOC_ID
    )AS PARENT_ID
    ,A.ITEM_CD
    ,A.MAIN_LOC_YN
    ,A.STRGE_CD
    ,A.UPPER_LOC_ID
    ,B.LAST_LVL_YN
FROM PT_0222T A
LEFT JOIN PT_0221T B ON A.DLR_CD = B.DLR_CD AND A.LVL_ID = B.LVL_ID
WHERE A.ITEM_CD IS null




             AND A.DLR_CD LIKE #{sDlrCd}


             AND A.DLR_CD = #{sDlrCd}








             AND A.LOC_ID LIKE #{sLocId}


             AND A.LOC_ID = #{sLocId}








             AND A.STRGE_CD LIKE #{sStrgeCd}


             AND A.STRGE_CD = #{sStrgeCd}





    AND A.LVL_ID = #{sLvlId}



    AND A.LOC_CD = #{sLocCd}



    AND B.LAST_LVL_YN = #{sLastLvlYn}


ORDER BY




                A.LOC_ID ASCDESC

                    A.DLR_CD ,A.LVL_ID, A.LOC_CD ASC





        A.DLR_CD ,A.LVL_ID, A.LOC_CD ASC

/* [chn.bhmc.dms.par.pmm.service.dao.BinLocationMasterDAO.selectBinLocationMasterChildByCondition] */ SELECT A.DLR_CD ,A.LOC_ID ,A.LOC_ID AS DB_LOC_ID ,A.PLT_CD ,A.STRGE_CD ,A.LVL_ID ,A.LOC_CD AS DB_LOC_CD ,A.LOC_CD ,A.LOC_NM ,A.UPPER_LOC_ID ,A.MAX_QTY ,A.LMT_MNG_YN ,A.ITEM_CD ,A.REMARK ,A.REG_USR_ID ,A.REG_DT ,A.UPDT_USR_ID ,A.UPDT_DT ,A.MAIN_LOC_YN ,A.UPPER_LVL_ID ,A.AVLB_STOCK_QTY ,A.RESV_STOCK_QTY ,A.CLAM_STOCK_QTY ,'Y' AS DB_YN ,B.LGTH_CNT ,B.CD_TP FROM PT_0222T A LEFT JOIN PT_0221T B ON A.DLR_CD = B.DLR_CD AND A.LVL_ID = B.LVL_ID
AND A.DLR_CD LIKE #{sDlrCd}
AND A.DLR_CD = #{sDlrCd}
AND A.LOC_ID LIKE #{sLocId}
AND A.LOC_ID = #{sLocId}
AND A.STRGE_CD LIKE #{sStrgeCd}
AND A.STRGE_CD = #{sStrgeCd}
AND A.LVL_ID = #{sLvlId}
AND A.LVL_ID > #{sNotLvlId}
AND A.LOC_CD LIKE #{sLocCd}
AND A.LOC_CD = #{sLocCd}
AND A.UPPER_LOC_ID LIKE #{sUpperLocId}
AND A.UPPER_LOC_ID = #{sUpperLocId}
AND A.MAIN_LOC_YN = #{sMainLocYn}
AND B.LAST_LVL_YN = #{sLastLvlYn}
AND A.ITEM_CD IS NULL
AND A.ITEM_CD IS NOT NULL
AND A.LOC_CD IS NULL
AND A.ITEM_CD IS NOT NULL
AND A.AVLB_STOCK_QTY = 0
AND A.AVLB_STOCK_QTY > 0
ORDER BY
A.LOC_ID ASCDESC A.LOC_CD ASCDESC A.ITEM_CD ASCDESC
A.DLR_CD, A.LOC_CD ASC
A.DLR_CD, A.LOC_CD ASC
A.DLR_CD, A.ITEM_CD ASC
A.DLR_CD ASC

/* [chn.bhmc.dms.par.pmm.service.dao.BinLocationMasterDAO.selectBinLocationMasterByCondition] */
SELECT * FROM (
    SELECT ROWNUM AS RNUM, T1.* FROM (
        SELECT
           A.DLR_CD
           ,A.LOC_ID
           ,A.LOC_ID AS DB_LOC_ID
           ,A.PLT_CD
           ,A.STRGE_CD
           ,A.LVL_ID
           ,A.LOC_CD AS DB_LOC_CD
           ,A.LOC_CD
           ,A.LOC_NM
           ,A.UPPER_LOC_ID
           ,A.MAX_QTY
           ,A.LMT_MNG_YN
           ,A.ITEM_CD
           ,A.REMARK
           ,A.REG_USR_ID
           ,A.REG_DT
           ,A.UPDT_USR_ID
           ,A.UPDT_DT
           ,A.MAIN_LOC_YN
           ,A.UPPER_LVL_ID
           ,A.AVLB_STOCK_QTY
           ,A.RESV_STOCK_QTY
           ,A.CLAM_STOCK_QTY
           ,'Y' AS DB_YN
           ,B.LGTH_CNT
           ,B.CD_TP
        FROM PT_0222T A
        LEFT JOIN PT_0221T B ON A.DLR_CD = B.DLR_CD AND A.LVL_ID = B.LVL_ID





                         AND A.DLR_CD LIKE #{sDlrCd}


                         AND A.DLR_CD = #{sDlrCd}








                         AND A.LOC_ID LIKE #{sLocId}


                         AND A.LOC_ID = #{sLocId}








                         AND A.STRGE_CD LIKE #{sStrgeCd}


                         AND A.STRGE_CD = #{sStrgeCd}








                         AND A.ITEM_CD LIKE #{sItemCd}


                         AND A.ITEM_CD = #{sItemCd}





                AND A.LVL_ID = #{sLvlId}



                AND A.LVL_ID <> #{sNotLvlId}



                AND A.LVL_ID > #{sLvlId}



                AND A.LOC_CD = #{sLocCd}






                         AND A.UPPER_LOC_ID LIKE #{sUpperLocId}


                         AND A.UPPER_LOC_ID = #{sUpperLocId}





                AND A.MAIN_LOC_YN = #{sMainLocYn}



                AND B.LAST_LVL_YN = #{sLastLvlYn}



                AND A.ITEM_CD IS NULL



                AND A.ITEM_CD IS NOT NULL



        ORDER BY




                        A.LOC_ID ASCDESC

                            A.DLR_CD, A.LOC_CD ASC





                A.DLR_CD, A.LOC_CD ASC


    ) T1


            ROWNUM <= #{lastIndex}


)


        RNUM > #{firstIndex}

/* [chn.bhmc.dms.par.pmm.service.dao.BinLocationMasterDAO.selectBinLocationItemsByCondition] */
SELECT * FROM (
    SELECT ROWNUM AS RNUM, T1.* FROM (
        SELECT
           A.DLR_CD
           ,A.LOC_ID
           ,A.LOC_ID AS DB_LOC_ID
           ,A.PLT_CD
           ,A.STRGE_CD
           ,A.LVL_ID
           ,A.LOC_CD AS DB_LOC_CD
           ,A.LOC_CD
           ,A.LOC_NM
           ,A.UPPER_LOC_ID
           ,A.MAX_QTY
           ,A.LMT_MNG_YN
           ,A.ITEM_CD
           ,D.ITEM_NM
           ,D.STOCK_UNIT_CD AS UNIT_CD
           ,A.REMARK
           ,A.REG_USR_ID
           ,A.REG_DT
           ,A.UPDT_USR_ID
           ,A.UPDT_DT
           ,A.MAIN_LOC_YN
           ,A.UPPER_LVL_ID
           ,C.AVLB_STOCK_QTY AS BASE_AVLB_STOCK_QTY
           ,A.AVLB_STOCK_QTY
           ,A.RESV_STOCK_QTY
           ,A.CLAM_STOCK_QTY
           ,'Y' AS DB_YN
           ,B.LGTH_CNT
           ,B.CD_TP
        FROM PT_0222T A
        LEFT JOIN PT_0221T B ON A.DLR_CD = B.DLR_CD AND A.LVL_ID = B.LVL_ID
        LEFT JOIN PT_0301T C ON A.DLR_CD = C.DLR_CD AND A.PLT_CD = C.PLT_CD AND A.STRGE_CD = C.STRGE_CD AND A.ITEM_CD = C.ITEM_CD
        LEFT JOIN PT_0201T D ON A.DLR_CD = D.DLR_CD AND A.ITEM_CD = D.ITEM_CD





                         AND A.DLR_CD LIKE #{sDlrCd}


                         AND A.DLR_CD = #{sDlrCd}








                         AND A.LOC_ID LIKE #{sLocId}


                         AND A.LOC_ID = #{sLocId}








                         AND A.STRGE_CD LIKE #{sStrgeCd}


                         AND A.STRGE_CD = #{sStrgeCd}








                         AND A.ITEM_CD LIKE #{sItemCd}


                         AND A.ITEM_CD = #{sItemCd}





                AND A.LVL_ID = #{sLvlId}



                AND A.LVL_ID <> #{sNotLvlId}



                AND A.LVL_ID > #{sLvlId}



                AND A.LOC_CD = #{sLocCd}






                         AND A.UPPER_LOC_ID LIKE #{sUpperLocId}


                         AND A.UPPER_LOC_ID = #{sUpperLocId}





                AND A.MAIN_LOC_YN = #{sMainLocYn}



                AND B.LAST_LVL_YN = #{sLastLvlYn}



                AND A.ITEM_CD IS NULL



                AND A.ITEM_CD IS NOT NULL



        ORDER BY




                        A.LOC_ID ASCDESC

                            A.DLR_CD, A.LOC_CD ASC





                A.DLR_CD, A.LOC_CD ASC


    ) T1


            ROWNUM <= #{lastIndex}


)


        RNUM > #{firstIndex}

/* [chn.bhmc.dms.par.pmm.service.dao.BinLocationMasterDAO.selectBinLocationMasterByConditionCnt] */
SELECT count(*) FROM PT_0222T





                 AND DLR_CD LIKE #{sDlrCd}


                 AND DLR_CD = #{sDlrCd}








                 AND LOC_ID LIKE #{sLocId}


                 AND LOC_ID = #{sLocId}








                 AND STRGE_CD LIKE #{sStrgeCd}


                 AND STRGE_CD = #{sStrgeCd}








                 AND ITEM_CD LIKE #{sItemCd}


                 AND ITEM_CD = #{sItemCd}





        AND LVL_ID = #{sLvlId}



        AND LOC_CD = #{sLocCd}



        AND UPPER_LOC_ID = #{sUpperLocId}



        AND MAIN_LOC_YN = #{sMainLocYn}



        AND ITEM_CD IS NULL



        AND ITEM_CD IS NOT NULL

mapper.update:
/* [chn.bhmc.dms.par.pmm.service.dao.BinLocationMasterDAO.updateBinLocationItemStock] */
UPDATE PT_0222T SET
      MAX_QTY = #{maxQty}
    , AVLB_STOCK_QTY = #{ avlbStockQty }
    , RESV_STOCK_QTY = #{ resvStockQty }
    , CLAM_STOCK_QTY = #{ clamStockQty }
    , UPDT_USR_ID = #{ updtUsrId }
    , UPDT_DT = sysdate
WHERE DLR_CD = #{ dlrCd }
  AND PLT_CD = #{ pltCd }
  AND STRGE_CD = #{ strgeCd }
  AND LOC_CD = #{ locCd }
  AND ITEM_CD = #{ itemCd }

mapper.select:
/* [chn.bhmc.dms.par.pmm.service.dao.BinLocationMasterDAO.selectBinLocationMasterByItemCd] */ SELECT A.DLR_CD ,A.LOC_ID ,A.PLT_CD ,A.STRGE_CD ,A.LVL_ID ,A.LOC_CD ,A.LOC_NM ,A.UPPER_LOC_ID ,A.MAX_QTY ,A.LMT_MNG_YN ,A.ITEM_CD ,A.REMARK ,A.REG_USR_ID ,A.REG_DT ,A.UPDT_USR_ID ,A.UPDT_DT ,A.MAIN_LOC_YN ,A.UPPER_LVL_ID ,A.AVLB_STOCK_QTY ,A.RESV_STOCK_QTY ,A.CLAM_STOCK_QTY ,B.LAST_LVL_YN FROM PT_0222T A INNER JOIN PT_0221T B ON A.DLR_CD = B.DLR_CD AND A.LVL_ID = B.LVL_ID WHERE B.LAST_LVL_YN = 'Y' --최하위 레벨여부 AND A.ITEM_CD IN
