mapper.select:
SELECT OCN_CD, '[' || OCN_CD || ']' || OCN_NM AS OCN_NM, MODEL_CD, MODEL_CD || OCN_CD AS FSC_CD FROM SA_0105T X WHERE SUBSTR(X.MODEL_CD, 1, 2) = #{sCarlineCd}
AND X.FSCP_CHM_MMM IN (SELECT A.FSCP_CHM_MMM FROM SA_0105T A, SA_0102T B WHERE A.MODEL_CD = B.MODEL_CD AND SUBSTR(A.MODEL_CD, 1, 2) = #{sCarlineCd} AND FSCP_MODTP_CD = #{sFscCd} GROUP BY A.FSCP_CHM_MMM)
ORDER BY OCN_CD ASC
