mapper.select:
/* [chn.bhmc.dms.ser.cmm.service.dao.ServiceCommonDAO.selectEtcIssuesByCondition] */ SELECT PREFIX_ID --PREFIX ID ,DLR_CD --딜러코드 ,JOB_NO --업무번호 ,LINE_NO --라인번호 ,ETC_TP_CD --기타유형코드 ,ETC_TP_NM --기타유형명 ,ETC_REQ_CONT --기타요청내용 ,ETC_REQ_PRC --기타요청가격 ,PAYM_TP --결제유형 ,PAYM_USR_NM --결제자명 ,REG_USR_ID --등록자ID ,REG_DT --등록일자 ,UPDT_USR_ID --수정자ID ,UPDT_DT --수정일자 FROM SE_0140T --정비기타항목
AND PREFIX_ID = #{sPreFixId} --PREFIX ID AND DLR_CD = #{sDlrCd} --딜러코드 AND JOB_NO = #{sJobNo} --업무번호
ORDER BY REG_DT DESC
