mapper.delete:
/* [chn.bhmc.dms.ser.svi.service.dao.RateManageDAO.deleteRateManage] */
DELETE FROM SE_1065T
 WHERE DSTIN_CD         = #{dstinCd}        --임률코드
   AND DLR_CD           = #{dlrCd}          --딜러코드
   AND RTE_DOC_NO       = #{rteDocNo}

mapper.select:
/* [chn.bhmc.dms.ser.svi.service.dao.RateManageDAO.selectRateManageByCondition] */ SELECT ROWNUM AS RNUM , T1.* FROM ( SELECT ROWNUM AS RNUM ,DSTIN_CD --구분코드 ,DLR_CD --딜러코드 ,RTE_DOC_NO --임률코드 ,RPIR_TP --수리유형 ,GNL_RTE_RATE --일반임률비율 ,WART_RTE_RATE --보증임률비율 ,INC_RTE_RATE --보험임률비율 ,INT_RTE_RATE --내부임률비율 ,RWRK_RTE_RATE --재작업임률비율 ,BRAND_CD --브랜드코드 ,APPLY_START_DT --적용시작일자 ,APPLY_END_DT --적용종료일자 ,USE_YN FROM SE_1065T WHERE DSTIN_CD = #{sDstinCd}
AND DLR_CD = #{sDlrCd} --딜러코드
AND BRAND_CD = #{sBrandCd} --브랜드코드
AND RPIR_TP = #{sRpirTp} --수리유형
ORDER BY RPIR_TP )T1

/* [chn.bhmc.dms.ser.svi.service.dao.RateManageDAO.selectRateManageByKey] */ SELECT ROWNUM AS RNUM ,DSTIN_CD --구분코드 ,DLR_CD --딜러코드 ,RTE_DOC_NO --임률코드 ,RPIR_TP --수리유형 ,GNL_RTE_RATE --일반임률비율 ,WART_RTE_RATE --보증임률비율 ,INC_RTE_RATE --보험임률비율 ,INT_RTE_RATE --내부임률비율 ,RWRK_RTE_RATE --재작업임률비율 ,BRAND_CD --브랜드코드 ,APPLY_START_DT --적용시작일자 ,APPLY_END_DT --적용종료일자 ,USE_YN FROM SE_1065T WHERE 1=1
AND DSTIN_CD = #{sDstinCd}
AND DLR_CD = #{sDlrCd} --딜러코드
AND BRAND_CD = #{sBrandCd} --브랜드코드
AND RPIR_TP = #{sRpirTp} --수리유형
AND USE_YN = 'Y'
AND RTE_DOC_NO != #{sRteDocNo}
AND APPLY_START_DT <= #{sApplyStartDt}
AND APPLY_END_DT <= #{sApplyEndDt}+1
