mapper.sql:
INNER JOIN CR_0211T T2 ON T1.DLR_CD = T2.DLR_CD AND T1.SALE_OPPT_SEQ = T2.SEQ
LEFT OUTER JOIN CM_0801T SCT ON T1.REG_USR_ID = SCT.USR_ID

   AND T1.DEL_YN = 'N'

    AND T1.DLR_CD            = #{sDlrCd}


    AND TO_CHAR(T1.NEXT_TRACE_DT , 'YYYYMMDD') = TO_CHAR(SYSDATE, 'YYYYMMDD')

    AND T1.SEQ               = #{sSeq}            
    AND T1.SALE_OPPT_SEQ     = #{sSaleOpptSeq}    
    AND T1.TRACE_CONT        LIKE '%'||#{sTraceCont}||'%'      
    AND T1.TRACE_GRADE_CD    = #{sTraceGradeCd}   
    AND T1.TRACE_DT          = #{sTraceDt}        
    AND T1.TRACE_MTH_CD      = #{sTraceMthCd}     
    AND T1.NEXT_TRACE_DT     = #{sNextTraceDt}    
    AND T1.NEXT_TRACE_MTH_CD = #{sNextTraceMthCd} 
    AND T1.VALID_GRADE_CD    = #{sValidGradeCd}   
    AND T1.INVALID_CAU_CD    = #{sInvalidCauCd}   
    AND T1.REG_USR_ID        = #{sRegUsrId}       
    AND T1.REG_DT            = #{sRegDt}          
    AND T1.UPDT_USR_ID       = #{sUpdtUsrId}      
    AND T1.UPDT_DT           = #{sUpdtDt}

mapper.select:
/* [chn.bhmc.dms.bat.crm.cso.service.dao.BatSalesOpptTraceMgmtDAO.selectSalesOpptTraceMgmtsByConditionCnt] */
SELECT COUNT(T1.SEQ) AS VALUE
FROM CR_0213T T1

/* [chn.bhmc.dms.bat.crm.cso.service.dao.BatSalesOpptTraceMgmtDAO.selectSalesOpptTraceMgmtsByCondition] */ SELECT T2.* FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT T1.DLR_CD --딜러코드 ,T1.SEQ --일련번호 ,T2.CUST_NO ,T2.LEAD_STAT_CD ,T1.SALE_OPPT_SEQ --판매기회일련번호 ,T1.TRACE_CONT --추적내용 ,T1.TRACE_GRADE_CD --추적등급코드 ,T1.TRACE_DT --추적일자 ,T1.TRACE_MTH_CD --추적방법코드 ,T1.NEXT_TRACE_DT --다음추적일자 ,T1.NEXT_TRACE_MTH_CD --다음추적방법코드 ,T1.VALID_GRADE_CD --유효등급코드 ,T1.INVALID_CAU_CD --무효원인코드 ,T1.DEL_YN --삭제여부 , SCT.USR_NM AS MNG_SC_NM --MNG_SC_NM , ( SELECT Z1.CMM_CD_NM FROM (SELECT A.CMM_CD, NVL(B.CMM_CD_TXT, A.CMM_CD_NM) AS CMM_CD_NM FROM CM_0303T A LEFT OUTER JOIN CM_0304T B ON A.CMM_GRP_CD = B.CMM_GRP_CD AND A.CMM_CD = B.CMM_CD AND B.LANG_CD = #{sLangCd} WHERE A.CMM_GRP_CD = 'CRM377' AND A.USE_YN = 'Y') Z1 WHERE T1.NEXT_TRACE_MTH_CD = Z1.CMM_CD ) AS NEXT_TRACE_MTH_NM , ( SELECT Z1.CMM_CD_NM FROM (SELECT A.CMM_CD, NVL(B.CMM_CD_TXT, A.CMM_CD_NM) AS CMM_CD_NM FROM CM_0303T A LEFT OUTER JOIN CM_0304T B ON A.CMM_GRP_CD = B.CMM_GRP_CD AND A.CMM_CD = B.CMM_CD AND B.LANG_CD = #{sLangCd} WHERE A.CMM_GRP_CD = 'CRM363' AND A.USE_YN = 'Y') Z1 WHERE T1.TRACE_GRADE_CD = Z1.CMM_CD ) AS TRACE_GRADE_NM , ( SELECT Z1.CMM_CD_NM FROM (SELECT A.CMM_CD, NVL(B.CMM_CD_TXT, A.CMM_CD_NM) AS CMM_CD_NM FROM CM_0303T A LEFT OUTER JOIN CM_0304T B ON A.CMM_GRP_CD = B.CMM_GRP_CD AND A.CMM_CD = B.CMM_CD AND B.LANG_CD = #{sLangCd} WHERE A.CMM_GRP_CD = 'CRM377' AND A.USE_YN = 'Y') Z1 WHERE T1.TRACE_MTH_CD = Z1.CMM_CD ) AS TRACE_MTH_NM , ( SELECT Z1.CMM_CD_NM FROM (SELECT A.CMM_CD, NVL(B.CMM_CD_TXT, A.CMM_CD_NM) AS CMM_CD_NM FROM CM_0303T A LEFT OUTER JOIN CM_0304T B ON A.CMM_GRP_CD = B.CMM_GRP_CD AND A.CMM_CD = B.CMM_CD AND B.LANG_CD = #{sLangCd} WHERE A.CMM_GRP_CD = 'CRM364' AND A.USE_YN = 'Y') Z1 WHERE T1.VALID_GRADE_CD = Z1.CMM_CD ) AS VALID_GRADE_NM , ( SELECT Z1.CMM_CD_NM FROM (SELECT A.CMM_CD, NVL(B.CMM_CD_TXT, A.CMM_CD_NM) AS CMM_CD_NM FROM CM_0303T A LEFT OUTER JOIN CM_0304T B ON A.CMM_GRP_CD = B.CMM_GRP_CD AND A.CMM_CD = B.CMM_CD AND B.LANG_CD = #{sLangCd} WHERE A.CMM_GRP_CD = 'CRM365' AND A.USE_YN = 'Y') Z1 WHERE T1.INVALID_CAU_CD = Z1.CMM_CD ) AS INVALID_CAU_NM , (SELECT A1.CUST_NM FROM CR_0101T A1 WHERE A1.DLR_CD = T2.DLR_CD AND A1.CUST_NO = T2.CUST_NO ) AS CUST_NM ,T1.REG_USR_ID --등록자ID ,T1.REG_DT --등록일자 ,T1.UPDT_USR_ID --수정자ID ,T1.UPDT_DT --수정일자 FROM CR_0213T T1
ORDER BY
T1.DLR_CD ASCDESC T1.SEQ ASCDESC T1.SALE_OPPT_SEQ ASCDESC T1.TRACE_CONT ASCDESC T1.TRACE_DT ASCDESC T1.NEXT_TRACE_DT ASCDESC T1.DEL_YN ASCDESC T1.NEXT_TRACE_MTH_CD ASCDESC T1.TRACE_GRADE_CD ASCDESC T1.TRACE_MTH_CD ASCDESC T1.VALID_GRADE_CD ASCDESC T1.INVALID_CAU_CD ASCDESC T1.REG_USR_ID ASCDESC T1.REG_DT ASCDESC T1.UPDT_USR_ID ASCDESC T1.UPDT_DT ASCDESC NEXT_TRACE_MTH_NM ASCDESC TRACE_GRADE_NM ASCDESC TRACE_MTH_NM ASCDESC VALID_GRADE_NM ASCDESC INVALID_CAU_NM ASCDESC MNG_SC_NM ASCDESC
T1.UPDT_DT DESC
T1.UPDT_DT DESC
) T1
ROWNUM <= #{lastIndex}
) T2
RNUM > #{firstIndex}

mapper.insert:
/* [chn.bhmc.dms.bat.crm.cso.service.dao.BatSalesOpptTraceMgmtDAO.insertSalesOpptTraceMgmt] */
SELECT SEQ_CR_0213T.currval AS SEQ FROM DUAL
INSERT INTO CR_0213T --판매기회관리추적 ( DLR_CD --딜러코드 ,SEQ --일련번호 ,SALE_OPPT_SEQ --판매기회일련번호 ,TRACE_CONT --추적내용 ,TRACE_GRADE_CD --추적등급코드 ,TRACE_DT --추적일자 ,TRACE_MTH_CD --추적방법코드 ,NEXT_TRACE_DT --다음추적일자 ,NEXT_TRACE_MTH_CD --다음추적방법코드 ,VALID_GRADE_CD --유효등급코드 ,INVALID_CAU_CD --무효원인코드 ,DEL_YN --삭제여부 ,REG_USR_ID --등록자ID ,REG_DT --등록일자 ,UPDT_USR_ID --수정자ID ,UPDT_DT --수정일자 ) VALUES ( #{dlrCd} --딜러코드 ,SEQ_CR_0213T.NEXTVAL --일련번호 ,#{saleOpptSeq} --판매기회일련번호 ,#{traceCont} --추적내용 ,#{traceGradeCd} --추적등급코드 ,#{traceDt} --추적일자 ,#{traceMthCd} --추적방법코드 ,#{nextTraceDt} --다음추적일자 ,#{nextTraceMthCd} --다음추적방법코드 ,#{validGradeCd} --유효등급코드 ,#{invalidCauCd} --무효원인코드 ,#{delYn} --삭제여부 ,#{regUsrId} --등록자ID ,SYSDATE --등록일자 ,#{updtUsrId} --수정자ID ,SYSDATE --수정일자 )
