mapper.sql:
LEFT OUTER JOIN CR_0217T T2 ON T1.DLR_CD=T2.DLR_CD AND T1.USR_ID = T2.USR_ID

    AND T1.DLR_CD = #{sDlrCd}

        AND T1.USR_ID = #{sUsrId}


        AND T1.USR_NM LIKE '%'||#{sUsrNmRemark}||'%'

mapper.select:
/* [chn.bhmc.dms.crm.cso.service.dao.IdccRoleMgmtDAO.selectIdccRoleAssigedMgmtsByConditionCnt] */
SELECT COUNT(T1.USR_ID) AS VALUE
FROM CR_0217T T1
WHERE   T1.DLR_CD = #{sDlrCd}
AND     T1.USR_ID = #{sUsrId}

/* [chn.bhmc.dms.crm.cso.service.dao.IdccRoleMgmtDAO.selectIdccRoleMgmtsByCondition] */ SELECT T2.* FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT T1.DLR_CD ,T1.USR_ID ,T2.REMARK ,DECODE(T2.REMARK,'',T1.USR_NM, T1.USR_NM||' ('||T2.REMARK||')' ) AS USR_NM ,T2.HOLD_MNG_TP FROM ( SELECT T2.USR_ID ,T2.USR_NM ,T2.EMP_NO ,T2.DLR_CD FROM CM_0810T T1 INNER JOIN CM_0801T T2 ON T1.REF_ID = T2.USR_ID WHERE T1.VIEW_ID = 'VIEW-D-10322' AND T1.SYS_CD = 'D' AND T1.REF_TP = 'U' AND T1.USE_YN = 'Y' AND T2.DLR_CD = #{sDlrCd} AND T2.DEL_YN = 'N' ) T1
ORDER BY
T1.DLR_CD ASCDESC T1.USR_ID ASCDESC T1.USR_NM ASCDESC
T1.USR_NM ASC
T1.USR_NM ASC
) T1
ROWNUM <= #{lastIndex}
) T2
RNUM > #{firstIndex}

mapper.update:
/* [chn.bhmc.dms.crm.cso.service.dao.IdccRoleMgmtDAO.updateIdccRole] */

    UPDATE CR_0217T    --고객(판매기획)확보관리
      SET
        REMARK           = #{remark}
        ,HOLD_MNG_TP     = #{holdMngTp}
        ,UPDT_USR_ID     = #{updtUsrId}
        ,UPDT_DT         = SYSDATE
     WHERE 1 = 1
       AND DLR_CD          = #{dlrCd}                 --딜러코드
       AND USR_ID          = #{usrId}
