/* [chn.bhmc.dms.par.pcm.service.dao.InvcDAO.selectInvcsByCondition] */ SELECT A.* FROM ( SELECT ROWNUM AS RNUM , T1.* FROM ( SELECT A.DLR_CD --딜러코드 ,A.INVC_DOC_NO --송장문서번호 ,A.INVC_TP --송장유형 ,A.INVC_DSTIN_CD --송장구분코드 ,A.INVC_STAT_CD --송장상태코드 ,A.INVC_DT --송장일자 ,A.ARRV_DT --도착일자 ,TO_CHAR(A.ARRV_DT, 'YYYYMMDD') AS ARRV_DT_CHAR --등록일자 ,TO_CHAR(A.ARRV_DT, 'HH24MISS') AS ARRV_TM_CHAR --등록일자 ,A.CONFIRM_DT --확정일자 ,A.PLT_CD --센터코드 ,D.PLT_NM --센터명 ,A.BP_CD --거래처코드 ,C.BP_NM --거래처명 ,A.MOBIS_INVC_NO --모비스송장번호 ,A.CURR_CD --통화코드 ,E.INVC_TOT_AMT AS PURC_INC_TAX_AMT --송장총금액 ,E.INVC_TOT_AMT --송장총금액 ,A.VAT_CD --부가세코드 ,A.VAT_AMT --부가세금액 ,A.REMARK --비고 ,A.CANC_YN --취소여부 ,A.REG_USR_ID --등록자ID ,A.REG_DT --등록일자 ,TO_CHAR(A.REG_DT, 'YYYYMMDD') AS REG_DT_CHAR --등록일자 ,A.UPDT_USR_ID --수정자ID ,A.UPDT_DT --수정일자 ,A.IF_YN --인터페이스여부 ,NVL(A.INVC_TOTAL_LINE, B.ITEM_CNT) AS INVC_TOTAL_LINE ,NVL(A.INVC_SUM_QT, B.ITEM_QTY) AS INVC_SUM_QT ,A.INVC_AMT_CUR ,A.INVC_TSP_CODE ,A.INVC_CSE_CNT ,TO_CHAR(NVL(A.UPDT_DT,A.REG_DT), 'YYYYMMDDHH24MISS') AS UPDT_DT_STR FROM DMSDB.PT_0431T A --송장문서헤더정보 INNER JOIN ( SELECT A.DLR_CD ,A.INVC_DOC_NO ,COUNT(*) AS ITEM_CNT ,SUM(A.INVC_QTY) AS ITEM_QTY ,SUM(B.PURC_AMT + (B.PURC_AMT * 0.12) ) AS PURC_INC_TAX_AMT ,SUM(B.PURC_AMT) AS PURC_AMT FROM DMSDB.PT_0432T A INNER JOIN DMSDB.PT_0422T B ON A.DLR_CD = B.DLR_CD AND A.PURC_ORD_NO = B.PURC_ORD_NO AND A.PURC_ORD_LINE_NO = B.PURC_ORD_LINE_NO INNER JOIN DMSDB.PT_0201T C --품목정보 ON A.DLR_CD = C.DLR_CD AND A.ITEM_CD = C.ITEM_CD WHERE (1 = 1)
AND A.ITEM_CD = #{sItemCd} --부품번호
AND C.ITEM_NM LIKE '%'|| #{sItemNm} ||'%'
AND C.ITEM_DSTIN_CD = #{sItemDstinCd}
GROUP BY A.DLR_CD ,A.INVC_DOC_NO ) B ON A.DLR_CD = B.DLR_CD AND A.INVC_DOC_NO = B.INVC_DOC_NO INNER JOIN DMSDB.PT_0211T C ON A.DLR_CD = C.DLR_CD AND A.BP_CD = C.BP_CD INNER JOIN DMSDB.CM_0103T D ON A.DLR_CD = D.DLR_CD AND A.PLT_CD = D.PLT_CD INNER JOIN ( SELECT A.DLR_CD ,A.INVC_DOC_NO ,SUM(A.INVC_TOT_AMT) AS INVC_TOT_AMT FROM DMSDB.PT_0432T A WHERE A.DLR_CD = #{sDlrCd}
AND A.MOBIS_INVC_NO LIKE '%'||#{sMobisInvcNo}||'%' --모비스송장번호 GROUP BY A.DLR_CD ,A.INVC_DOC_NO ) E ON A.DLR_CD = E.DLR_CD AND A.INVC_DOC_NO = E.INVC_DOC_NO WHERE A.DLR_CD = #{sDlrCd}
AND A.INVC_TP = #{sInvcTp} --송장유형
AND A.INVC_STAT_CD = #{sInvcStatCd}
AND A.INVC_STAT_CD in ('00','02')
AND A.INVC_DT >= #{sInvcDtFr}
AND A.INVC_DT <= #{sInvcDtTo} + 1
AND A.CONFIRM_DT >= #{sConfirmDtFr}
AND A.CONFIRM_DT <= #{sConfirmDtTo} + 1
AND A.BP_CD = #{sBpCd} --거래처코드
AND A.MOBIS_INVC_NO LIKE '%'||#{sMobisInvcNo}||'%' --모비스송장번호
AND A.REG_DT >= #{sRegDtFr}
AND A.REG_DT <= #{sRegDtTo} + 1
AND A.CANC_YN != 'Y' ORDER BY
A.INVC_TP ASCDESC A.INVC_STAT_CD ASCDESC A.INVC_DT ASCDESC A.ARRV_DT ASCDESC A.PLT_CD ASCDESC D.PLT_NM ASCDESC C.BP_NM ASCDESC A.MOBIS_INVC_NO ASCDESC A.INVC_TOT_AMT ASCDESC A.VAT_CD ASCDESC A.VAT_AMT ASCDESC A.REMARK ASCDESC A.REG_DT ASCDESC
A.DLR_CD ASC ,A.INVC_DT DESC
A.DLR_CD ASC ,A.INVC_DT DESC ,A.CONFIRM_DT DESC
) T1 ) A
RNUM <= #{lastIndex} AND RNUM > #{firstIndex}

/* [chn.bhmc.dms.par.pcm.service.dao.InvcDAO.selectInvcsByConditionCnt] */ SELECT count(*) FROM DMSDB.PT_0431T A --송장문서헤더정보 INNER JOIN ( SELECT A.DLR_CD ,A.INVC_DOC_NO ,COUNT(*) AS ITEM_CNT ,SUM(A.INVC_QTY) AS ITEM_QTY ,SUM(B.PURC_AMT + (B.PURC_AMT * 0.12) ) AS PURC_INC_TAX_AMT ,SUM(B.PURC_AMT) AS PURC_AMT FROM DMSDB.PT_0432T A INNER JOIN DMSDB.PT_0422T B ON A.DLR_CD = B.DLR_CD AND A.PURC_ORD_NO = B.PURC_ORD_NO AND A.PURC_ORD_LINE_NO = B.PURC_ORD_LINE_NO INNER JOIN DMSDB.PT_0201T C --품목정보 ON A.DLR_CD = C.DLR_CD AND A.ITEM_CD = C.ITEM_CD INNER JOIN ( SELECT A.DLR_CD ,A.INVC_DOC_NO ,SUM(A.INVC_TOT_AMT) AS INVC_TOT_AMT FROM DMSDB.PT_0432T A WHERE A.DLR_CD = #{sDlrCd}
AND A.MOBIS_INVC_NO LIKE '%'||#{sMobisInvcNo}||'%' --모비스송장번호 GROUP BY A.DLR_CD ,A.INVC_DOC_NO ) E ON A.DLR_CD = E.DLR_CD AND A.INVC_DOC_NO = E.INVC_DOC_NO WHERE (1 = 1)
AND A.ITEM_CD = #{sItemCd} --부품번호
AND C.ITEM_NM LIKE '%'|| #{sItemNm} ||'%'
AND C.ITEM_DSTIN_CD = #{sItemDstinCd}
GROUP BY A.DLR_CD ,A.INVC_DOC_NO ) B ON A.DLR_CD = B.DLR_CD AND A.INVC_DOC_NO = B.INVC_DOC_NO INNER JOIN DMSDB.PT_0211T C ON A.DLR_CD = C.DLR_CD AND A.BP_CD = C.BP_CD INNER JOIN DMSDB.CM_0103T D ON A.DLR_CD = D.DLR_CD AND A.PLT_CD = D.PLT_CD WHERE A.DLR_CD = #{sDlrCd}
AND A.INVC_TP = #{sInvcTp} --송장유형
AND A.INVC_STAT_CD in ('00','02')
AND A.INVC_DT >= #{sInvcDtFr}
AND A.INVC_DT <= #{sInvcDtTo} + 1
AND A.BP_CD = #{sBpCd} --거래처코드
AND A.MOBIS_INVC_NO LIKE '%'||#{sMobisInvcNo}||'%' --모비스송장번호
AND A.REG_DT >= #{sRegDtFr}
AND A.REG_DT <= #{sRegDtTo} + 1
AND A.CANC_YN != 'Y'

/* [chn.bhmc.dms.par.pcm.service.dao.InvcDAO.selectReceiveByMobile] */ SELECT A.DLR_CD --딜러코드 ,A.INVC_DT --INVOICE일자 ,A.CONFIRM_DT --확정일자 ,A.BP_CD --공급업체코드 ,E.BP_NM --공급업체명 ,B.PURC_ORD_NO --구매오더번호 ,A.INVC_DOC_NO --송장문서번호 ,A.MOBIS_INVC_NO --모비스송장번호 FROM DMSDB.PT_0431T A --송장헤더정보 INNER JOIN ( SELECT DLR_CD, INVC_DOC_NO,PURC_ORD_NO, COUNT(*) AS CNT FROM DMSDB.PT_0432T WHERE DLR_CD = #{sDlrCd} AND GR_END_YN != 'Y' GROUP BY DLR_CD, INVC_DOC_NO, PURC_ORD_NO ) B ON A.DLR_CD = B.DLR_CD AND A.INVC_DOC_NO = B.INVC_DOC_NO INNER JOIN DMSDB.PT_0211T E ON A.DLR_CD = E.DLR_CD AND A.BP_CD = E.BP_CD WHERE A.DLR_CD = #{sDlrCd} --딜러코드 --AND A.INVC_STAT_CD = '02' AND A.INVC_STAT_CD = '00' AND A.BP_CD = #{sBpCd} --거래처코드 AND B.CNT > 0
AND A.MOBIS_INVC_NO = #{sMobisInvcNo} --모비스송장번호
AND A.INVC_DT = #{sInvcDt}
ORDER BY A.INVC_DT DESC ,A.MOBIS_INVC_NO ASC

/* [chn.bhmc.dms.par.pcm.service.dao.InvcDAO.selectReceiveByMobileCnt] */ SELECT COUNT(*) FROM DMSDB.PT_0431T A --송장헤더정보 INNER JOIN ( SELECT DLR_CD, INVC_DOC_NO,PURC_ORD_NO, COUNT(*) AS CNT FROM DMSDB.PT_0432T WHERE DLR_CD = #{sDlrCd} AND GR_END_YN != 'Y' GROUP BY DLR_CD, INVC_DOC_NO, PURC_ORD_NO ) B ON A.DLR_CD = B.DLR_CD AND A.INVC_DOC_NO = B.INVC_DOC_NO INNER JOIN DMSDB.PT_0211T E ON A.DLR_CD = E.DLR_CD AND A.BP_CD = E.BP_CD WHERE A.DLR_CD = #{sDlrCd} --딜러코드 --AND A.INVC_STAT_CD = '02' AND A.INVC_STAT_CD = '00' AND A.BP_CD = #{sBpCd} --거래처코드 AND B.CNT > 0
AND A.MOBIS_INVC_NO = #{sMobisInvcNo} --모비스송장번호
AND A.INVC_DT = #{sInvcDt}
