/* [chn.bhmc.dms.par.pcm.service.dao.PurcOrdDAO.selectPurcOrdsByCondition] */ SELECT A.* FROM( SELECT ROWNUM AS RNUM ,T1.* FROM( SELECT F.* FROM( SELECT A.DLR_CD ,A.PURC_ORD_NO ,A.PURC_ORD_TP ,A.TRSF_TP ,A.PURC_ORD_STAT_CD ,A.PLT_CD ,A.BP_CD ,B.BP_NM ,A.PURC_REG_DT ,CASE WHEN A.PURC_ORD_STAT_CD IN ('01','02') THEN C.TOT_PURC_AMT WHEN A.PURC_ORD_STAT_CD IN ('05') THEN 0 -- 취소 일때 : 0 WHEN A.PURC_ORD_STAT_CD = '03' AND A.TOT_PURC_AMT = 0 THEN A.CONFIRM_PAR_AMT*1.12 ELSE A.TOT_PURC_AMT*1.12 END CALC_PURC_AMT ,CASE WHEN A.PURC_ORD_STAT_CD IN ('01','02') THEN C.TOT_TAX_DDCT_AMT WHEN A.PURC_ORD_STAT_CD IN ('05') THEN 0 -- 취소 일때 : 0 WHEN A.PURC_ORD_STAT_CD = '03' AND A.TOT_PURC_AMT = 0 THEN A.CONFIRM_PAR_AMT ELSE A.TOT_PURC_AMT END CALC_TAX_DDCT_AMT ,CASE WHEN A.PURC_ORD_STAT_CD IN ('01','02') THEN C.TOT_PURC_QTY WHEN A.PURC_ORD_STAT_CD IN ('05') THEN 0 -- 취소 일때 : 0 WHEN A.PURC_ORD_STAT_CD = '03' AND A.TOT_PURC_AMT = 0 THEN A.CONFIRM_PAR_QTY ELSE A.PURC_PAR_QTY END CALC_CONFIRM_QTY ,A.BMP_ORD_NO ,CASE WHEN A.PURC_ORD_STAT_CD IN ('01','02') THEN C.ITEM_CNT WHEN A.PURC_ORD_STAT_CD IN ('05') THEN 0 -- 취소 일때 : 0 WHEN A.PURC_ORD_STAT_CD = '03' AND A.TOT_PURC_AMT = 0 THEN A.CONFIRM_PAR_CNT ELSE A.PURC_PAR_CNT END ITEM_CNT ,A.SEND_DT ,A.CONFIRM_DT ,TO_DATE(A.ARRV_DT) AS ARRV_DATE ,A.END_DT ,A.CANC_DT ,A.REMARK ,A.REG_USR_ID ,(SELECT USR_NM FROM CM_0801T WHERE USR_ID = A.REG_USR_ID) AS REG_USR_NM ,A.REG_DT ,A.UPDT_USR_ID ,A.UPDT_DT ,'' as MOBIS_INVC_NO ,C.TOT_PURC_QTY ,C.TOT_PURC_AMT ,C.TOT_TAX_DDCT_AMT FROM PT_0421T A INNER JOIN PT_0211T B ON A.DLR_CD = B.DLR_CD AND A.BP_CD = B.BP_CD INNER JOIN ( SELECT A.DLR_CD ,A.PURC_ORD_NO --,SUM(PURC_QTY) AS TOT_PURC_QTY ,COUNT(*) AS ITEM_CNT --,SUM(PURC_AMT) AS TOT_PURC_AMT ,SUM(CALC_CONFIRM_QTY) TOT_PURC_QTY ,SUM(CALC_PURC_AMT) TOT_PURC_AMT ,SUM(CALC_TAX_DDCT_AMT) AS TOT_TAX_DDCT_AMT FROM (SELECT A.DLR_CD ,A.PURC_ORD_NO ,A.ITEM_CD ,PURC_QTY ,PURC_AMT ,CASE WHEN B.PURC_ORD_STAT_CD IN ('01','02') THEN A.PURC_QTY -- 신규생성 일때 : 오더수량을 그대로 보여줌 WHE