AND A.TOT_PURC_AMT >= #{sTotPurcAmt}
AND A.TOT_PURC_AMT >= #{sTotPurcAmtFr}
AND A.TOT_PURC_AMT <= #{sTotPurcAmtTo}
AND A.REG_USR_ID = #{sRegUsrId}
) F WHERE 1=1
AND F.REG_USR_NM LIKE '%'||#{sRegUsrNm}||'%'
ORDER BY
F.PURC_ORD_NO ASCDESC F.BMP_ORD_NO ASCDESC F.PURC_ORD_TP ASCDESC F.TRSF_TP ASCDESC F.PURC_ORD_STAT_CD ASCDESC F.BP_NM ASCDESC F.ITEM_CNT ASCDESC F.PURC_REG_DT ASCDESC F.TOT_PURC_QTY ASCDESC F.TOT_PURC_AMT ASCDESC F.TOT_TAX_DDCT_AMT ASCDESC F.ARRV_DATE ASCDESC F.CONFIRM_DT ASCDESC F.REG_USR_NM ASCDESC F.MOBIS_INVC_NO ASCDESC F.REMARK ASCDESC
F.DLR_CD ASC ,F.PURC_REG_DT DESC
) T1 ) A
RNUM <= #{lastIndex} AND RNUM > #{firstIndex}

/* [chn.bhmc.dms.par.pcm.service.dao.PurcOrdDAO.selectPurcOrdsByConditionCnt] */ SELECT COUNT(*) FROM( SELECT F.* FROM( SELECT A.DLR_CD ,A.PURC_ORD_NO ,A.PURC_ORD_TP ,A.TRSF_TP ,A.PURC_ORD_STAT_CD ,A.PLT_CD ,A.BP_CD ,B.BP_NM ,A.PURC_REG_DT ,CASE WHEN A.PURC_ORD_STAT_CD IN ('01','02') THEN C.TOT_PURC_AMT WHEN A.PURC_ORD_STAT_CD = '03' AND A.TOT_PURC_AMT = 0 THEN A.CONFIRM_PAR_AMT*1.12 ELSE A.TOT_PURC_AMT*1.12 END CALC_PURC_AMT ,CASE WHEN A.PURC_ORD_STAT_CD IN ('01','02') THEN C.TOT_TAX_DDCT_AMT WHEN A.PURC_ORD_STAT_CD = '03' AND A.TOT_PURC_AMT = 0 THEN A.CONFIRM_PAR_AMT ELSE A.TOT_PURC_AMT END TOT_TAX_DDCT_AMT ,CASE WHEN A.PURC_ORD_STAT_CD IN ('01','02') THEN C.TOT_PURC_QTY WHEN A.PURC_ORD_STAT_CD = '03' AND A.TOT_PURC_AMT = 0 THEN A.CONFIRM_PAR_QTY ELSE A.PURC_PAR_QTY END CALC_CONFIRM_QTY ,A.BMP_ORD_NO ,CASE WHEN A.PURC_ORD_STAT_CD IN ('01','02') THEN C.ITEM_CNT WHEN A.PURC_ORD_STAT_CD = '03' AND A.TOT_PURC_AMT = 0 THEN A.CONFIRM_PAR_CNT ELSE A.PURC_PAR_CNT END ITEM_CNT ,A.SEND_DT ,A.CONFIRM_DT ,TO_DATE(A.ARRV_DT) AS ARRV_DATE ,A.END_DT ,A.CANC_DT ,A.REMARK ,A.REG_USR_ID ,(SELECT USR_NM FROM CM_0801T WHERE USR_ID = A.REG_USR_ID) AS REG_USR_NM ,A.REG_DT ,A.UPDT_USR_ID ,A.UPDT_DT ,'' as MOBIS_INVC_NO ,C.TOT_PURC_QTY ,C.TOT_PURC_AMT FROM PT_0421T A INNER JOIN PT_0211T B ON A.DLR_CD = B.DLR_CD AND A.BP_CD = B.BP_CD INNER JOIN ( SELECT A.DLR_CD ,A.PURC_ORD_NO --,SUM(PURC_QTY) AS TOT_PURC_QTY ,COUNT(*) AS ITEM_CNT --,SUM(PURC_AMT) AS TOT_PURC_AMT ,SUM(CALC_CONFIRM_QTY) TOT_PURC_QTY ,SUM(CALC_PURC_AMT) TOT_PURC_AMT ,SUM(TAX_DDCT_AMT) AS TOT_TAX_DDCT_AMT FROM (SELECT A.DLR_CD ,A.PURC_ORD_NO ,A.ITEM_CD ,PURC_QTY ,PURC_AMT ,CASE WHEN B.PURC_ORD_STAT_CD IN ('01','02') THEN A.PURC_QTY -- 신규생성 일때 : 오더수량을 그대로 보여줌 ELSE CASE WHEN A.PURC_PGSS_CD IN ('C','D','Y') THEN CASE WHEN A.CONFIRM_QTY > 0 THEN A.CONFIRM_QTY - NVL(CASE WHEN NVL(A.CANC_QTY,0) = 0 THEN CASE WHEN B.PURC_ORD_STAT_CD = '05' THEN A.PURC_QTY ELSE 0 END ELSE NVL(A.CANC_QTY,0) END,0) ELSE 0 END WHEN A.PURC_PGSS_CD IN ('X','Z') THEN NVL(NVL(A.SHIP_QTY,0) + NVL(A.ODR_ALC_QT,0) + NVL(A.ODR_OPIC_QT,0) + NVL(A.ODR_PAKD_QT,0) + NVL(A.ODR_INV_QT,0),0) ELSE A.CONFIRM_QTY END END CALC_CONFIRM_QTY ,CASE WHEN b.PURC_ORD_STAT_CD IN ('01','02') THEN A.PURC_AMT ELSE CASE WHEN A.PURC_PGSS_CD IN ('C','D','Y') THEN CASE WHEN A.CONFIRM_QTY - NVL(CASE WHEN NVL(A.CANC_QTY,0) = 0 THEN CASE WHEN B.PURC_ORD_STAT_CD = '05' THEN A.PURC_QTY ELSE 0 END ELSE NVL(A.CANC_QTY,0) END,0) > 0 THEN ROUND((A.CONFIRM_QTY - NVL(CASE WHEN NVL(A.CANC_QTY,0) = 0 THEN CASE WHEN B.PURC_ORD_STAT_CD = '05' THEN A.PURC_QTY ELSE 0 END ELSE NVL(A.CANC_QTY,0) END,0)) * A.PURC_PRC,2) ELSE 0 END WHEN A.PURC_PGSS_CD IN ('X','Z') THEN CASE WHEN NVL(A.SHIP_QTY,0) + NVL(A.ODR_ALC_QT,0) + NVL(A.ODR_OPIC_QT,0) + NVL(A.ODR_PAKD_QT,0) + NVL(A.ODR_INV_QT,0) > 0 THEN ROUND(A.PURC_PRC * (NVL(A.SHIP_QTY,0) + NVL(A.ODR_ALC_QT,0) + NVL(A.ODR_OPIC_QT,0) + NVL(A.ODR_PAKD_QT,0) + NVL(A.ODR_INV_QT,0)),2) ELSE 0 END ELSE A.PURC_AMT END END CALC_PURC_AMT ,TAX_DDCT_AMT FROM PT_0422T A INNER JOIN PT_0421T B ON A.DLR_CD = B.DLR_CD AND A.PURC_ORD_NO = B.PURC_ORD_NO WHERE A.DEL_YN != 'Y') A INNER JOIN PT_0201T B ON A.DLR_CD = B.DLR_CD AND A.ITEM_CD = B.ITEM_CD WHERE 1=1
AND A.ITEM_CD LIKE '%'||#{sItemCd}||'%'
AND B.ITEM_NM LIKE '%'||#{sItemNm}||'%'
AND B.ITEM_DSTIN_CD = #{sItemDstinCd}
GROUP BY A.DLR_CD ,A.PURC_ORD_NO ) C ON A.DLR_CD = C.DLR_CD AND A.PURC_ORD_NO = C.PURC_ORD_NO WHERE A.DLR_CD = #{sDlrCd}
AND A.PURC_ORD_NO LIKE '%'||#{sPurcOrdNo}||'%'
AND A.PURC_ORD_TP = #{sPurcOrdTp}
AND A.TRSF_TP = #{sTrsfTp}
AND B.BP_CD = #{sBpCd}
AND A.PURC_ORD_STAT_CD = #{sPurcOrdStatCd}
AND A.PURC_ORD_STAT_CD IN
#{item}
