#{item}
AND A.PURC_REG_DT >= #{sPurcRegDtFr}
AND A.PURC_REG_DT <= #{sPurcRegDtTo} + 1
AND TO_DATE(A.ARRV_DT) >= to_date(to_char(#{sArrvDtFr},'yyyymmdd'))
AND TO_DATE(A.ARRV_DT) <= to_date(to_char(#{sArrvDtTo},'yyyymmdd'))
AND A.UPDT_USR_ID = #{sCancelImplYn}
AND A.UPDT_DT = #{sGrImplYn}
