AND A.TOT_PURC_AMT <= #{sTotPurcAmtTo}
AND A.REG_USR_ID = #{sRegUsrId}
) F WHERE 1=1
AND F.REG_USR_NM LIKE '%'||#{sRegUsrNm}||'%'
) T1
