mapper.select:
	SELECT COUNT(*)
    FROM SA_0417T A
    	,SA_0416T B
    	,SA_0121T C
 	WHERE A.DLR_CD = B.DLR_CD(+)
        AND A.ORD_TP = B.SORD(+)
        AND A.ORD_YY_MM_DT = B.ORD_YYMM(+)
        AND A.ORD_PRID = B.ORD_WEEK(+)
        AND TO_NUMBER(A.ORD_NO) = B.ORD_SEQ(+)
        AND B.VIN_NO1 = C.VIN_NO1(+)
        AND B.VIN_NO2 = C.VIN_NO2(+)
        AND A.ORD_STAT_CD NOT IN ('1A')
        AND B.PRTY NOT IN ('A','B','R')
        AND A.IFRESULT_RTN != 'E'

        	AND A.ORD_TP = #{sOrdTp}


        	AND A.ORD_TP IN

            	#{ordTp}



         	AND B.ALDY  >=  TO_CHAR(#{sOrdStartDt},'YYYYMMDD')


         	AND B.ALDY  <  TO_CHAR(#{sOrdEndDt} +1,'YYYYMMDD')


         	AND A.DLR_CD = #{sDlrCd}


         	AND ORD_YY_MM_DT = SUBSTR(#{sOrdPrid}, 1, 6)
         	AND ORD_PRID = SUBSTR(#{sOrdPrid}, 7, 2)


         	AND A.CARLINE_CD = #{sCarlineCd}


         	AND A.MODEL_CD = #{sModelCd}


         	AND A.OCN_CD = #{sOcnCd}


         	AND A.EXT_COLOR_CD = #{sExtColorCd}


         	AND A.INT_COLOR_CD = #{sIntColorCd}


         	AND A.ORD_GRADE_CD = #{sordGradeCd}


         	AND B.PRTY = #{sOrdStatCd}


         	AND B.EMONT = #{sConfirmDt}


       		AND B.ALDY >= #{sOrdStartDtstr}


       		AND B.ALDY <= #{sOrdEndDtstr}


           AND (SELECT X.FSCP_MODTP_CD FROM SA_0105T X WHERE X.MODEL_CD = A.MODEL_CD AND X.OCN_CD = A.OCN_CD)  = #{sFscCd}


           AND (SELECT X.FSCP_MODTP_CD FROM SA_0105T X WHERE X.MODEL_CD = A.MODEL_CD AND X.OCN_CD = A.OCN_CD) IS NULL

SELECT * FROM ( SELECT ROWNUM AS RNUM ,FN_GET_CARLINE_NM_NC(T1.CARLINE_CD) AS CARLINE_NM ,FN_GET_MODEL_TP_NM(T1.MODEL_CD, T1.OCN_CD) AS MODEL_NM ,FN_GET_OCN_NM_NC(T1.MODEL_CD, T1.OCN_CD) AS OCN_NM ,FN_GET_EXT_COLOR_NM_NC(T1.EXT_COLOR_CD) AS EXT_COLOR_NM ,FN_GET_INT_COLOR_NM_NC(T1.INT_COLOR_CD) AS INT_COLOR_NM ,SALE_RGST_ID AS CHRG_CD ,DC_RATE ,MDPR + DAMT AS DC_AMT ,T1.* ,AAMT FROM ( SELECT A.ORD_YY_MM_DT --오더년월 ,A.ORD_PRID AS WEEK --오더주차 ,SUBSTR(A.ORD_YY_MM_DT, 3, 6) AS YY_MM --오더년YYMM ,A.DLR_CD --딜러코드 ,A.CARLINE_CD ,A.MODEL_CD ,A.OCN_CD ,A.ORD_GRADE_CD ,A.EXT_COLOR_CD ,A.INT_COLOR_CD ,CASE WHEN NVL(A.ORD_QTY, 0) > 1 THEN 1 ELSE NVL(A.ORD_QTY, 0) END AS ORD_QTY --주문수량 ,CASE WHEN NVL(A.FST_CONFIRM_QTY, 0) > 1 THEN 1 ELSE NVL(A.FST_CONFIRM_QTY, 0) END AS FST_CONFIRM_QTY --1차확정수량합 ,CASE WHEN NVL(A.SCND_CONFIRM_QTY, 0) > 1 THEN 1 ELSE NVL(A.SCND_CONFIRM_QTY, 0) END AS SCND_CONFIRM_QTY --2차확정수량합 ,(CASE WHEN B.PRTY IS NULL THEN 1 ELSE 0 END) AS WT_ORD_QTY --승인대기 ,(CASE WHEN B.PRTY = 'B' THEN 1 ELSE 0 END) AS DLR_CNCL_QTY --승인후취소수량 O ,(CASE WHEN B.PRTY = 'A' THEN 1 ELSE 0 END) AS NON_ALOC_QTY --미배정수량 ,(CASE WHEN B.PRTY = 'P' THEN 1 ELSE 0 END) AS PDI_QTY --PDI배정 ,(CASE WHEN B.PRTY = 'C' THEN 1 ELSE 0 END) AS VIN_QTY --VIN배정 ,(CASE WHEN B.PRTY = 'F' THEN 1 ELSE 0 END) AS GATE_OUT_QTY --법인출고수량 ,B.EXNO AS FNDL_DT --오더분배예정일 ,B.REG_USR_ID ,B.SALES_NO AS ORD_SEQ --오더번호 ,B.PRTY AS ORD_STAT_CD --오더상태명(공통코드SAL150) ,B.CNDY AS ORD_DATE --오더등록일자 ,B.ALOC AS VINM_VLOC --하치장명(공통코드SAL152) ,B.VIN_NO1 || B.VIN_NO2 AS VIN_NO--VIN NO ,A.ORD_TP --주문유형(공통코드SAL137) ,CASE WHEN B.ALDY = '00000000' THEN NULL ELSE TO_DATE(B.ALDY, 'YYYYMMDD') END AS PDI_DT --PDI배정일 ,C.CAR_VIN_ALLOC_DT AS VIN_DT --VIN배정일 ,C.PLT_GI_DT AS RETAIL_DT ,C.DLR_GR_DT --입고일자 ,C.CUST_SALE_DT --고객판매일자 ,DECODE(B.EMONT, NULL, NULL, '000000', NULL, TO_CHAR(TO_DATE(B.EMONT,'YYYYMM'),'YYYY-MM')) AS EMONT ,( SELECT X.MS_PRC FROM SA_0110T X WHERE X.MODEL_CD = A.MODEL_CD AND X.OCN_CD = A.OCN_CD AND TO_CHAR(X.END_DT,'YYYYMMDD') >= TO_CHAR(SYSDATE,'YYYYMMDD') AND X.USE_YN = 'Y' ) AS MDPR ,B.DAMT ,( SELECT DECODE(X.DATE2,NULL,NULL,'00000000',NULL,X.DATE2) FROM SA_0447T X WHERE X.KUNNR = B.DLR_CD AND X.VBELN = B.SALES_NO AND X.TSTMP = ( SELECT MAX(Y.TSTMP) FROM SA_0447T Y WHERE Y.KUNNR = B.DLR_CD AND Y.VBELN = B.SALES_NO AND NOT Y.ZACTION = 'D' ) ) AS MIS_SET_DT ,( SELECT DECODE(X.DATE3,NULL,NULL,'00000000',NULL,X.DATE3) FROM SA_0448T X WHERE X.KUNNR = B.DLR_CD AND X.VBELN = B.SALES_NO AND X.TSTMP = ( SELECT MAX(Y.TSTMP) FROM SA_0448T Y WHERE Y.KUNNR = B.DLR_CD AND Y.VBELN = B.SALES_NO AND NOT Y.ZACTION = 'D' ) ) AS TRAN_SET_DT ,B.AAMT ,A.SALE_RGST_ID ,A.ORD_DT ,CASE WHEN B.AAMT+(B.DAMT)*-1 > 0 THEN ROUND(((B.DAMT)*-1)/(B.AAMT+(B.DAMT)*-1)*100,2) ELSE 0 END AS DC_RATE FROM SA_0417T A ,SA_0416T B ,SA_0121T C WHERE A.DLR_CD = B.DLR_CD(+) AND A.ORD_TP = B.SORD(+) AND A.ORD_YY_MM_DT = B.ORD_YYMM(+) AND A.ORD_PRID = B.ORD_WEEK(+) AND TO_NUMBER(A.ORD_NO) = B.ORD_SEQ(+) AND B.VIN_NO1 = C.VIN_NO1(+) AND B.VIN_NO2 = C.VIN_NO2(+) AND A.ORD_STAT_CD NOT IN ('1A') AND B.PRTY NOT IN ('A','B','R') AND A.IFRESULT_RTN != 'E'
AND A.ORD_TP = #{sOrdTp}
AND A.ORD_TP IN
#{ordTp}
AND A.DLR_CD = #{sDlrCd}
AND ORD_YY_MM_DT = SUBSTR(#{sOrdPrid}, 1, 6) AND ORD_PRID = SUBSTR(#{sOrdPrid}, 7, 2)
AND A.CARLINE_CD = #{sCarlineCd}
AND A.MODEL_CD = #{sModelCd}
AND A.OCN_CD = #{sOcnCd}
AND A.EXT_COLOR_CD = #{sExtColorCd}
AND A.INT_COLOR_CD = #{sIntColorCd}
AND A.ORD_GRADE_CD = #{sordGradeCd}
AND B.PRTY = #{sOrdStatCd}
AND B.EMONT = #{sConfirmDt}
AND B.ALDY >= #{sOrdStartDtstr}
AND B.ALDY <= #{sOrdEndDtstr}
AND (SELECT X.FSCP_MODTP_CD FROM SA_0105T X WHERE X.MODEL_CD = A.MODEL_CD AND X.OCN_CD = A.OCN_CD) = #{sFscCd}
AND (SELECT X.FSCP_MODTP_CD FROM SA_0105T X WHERE X.MODEL_CD = A.MODEL_CD AND X.OCN_CD = A.OCN_CD) IS NULL
ORDER BY
A.CARLINE_CD ASCDESC A.CARLINE_CD ASCDESC A.MODEL_CD ASCDESC A.MODEL_CD ASCDESC A.OCN_CD ASCDESC A.OCN_CD ASCDESC A.EXT_COLOR_CD ASCDESC A.EXT_COLOR_CD ASCDESC A.INT_COLOR_CD ASCDESC A.INT_COLOR_CD ASCDESC A.ORD_TP ASCDESC ORD_QTY ASCDESC FST_CONFIRM_QTY ASCDESC SCND_CONFIRM_QTY ASCDESC WT_ORD_QTY ASCDESC DLR_CNCL_QTY ASCDESC NON_ALOC_QTY ASCDESC PDI_QTY ASCDESC VIN_QTY ASCDESC GATE_OUT_QTY ASCDESC FNDL_DT ASCDESC B.REG_USR_ID ASCDESC ORD_SEQ ASCDESC ORD_DATE ASCDESC VINM_VLOC ASCDESC VIN_NO ASCDESC A.ORD_TP ASCDESC PDI_DT ASCDESC VIN_DT ASCDESC RETAIL_DT ASCDESC C.DLR_GR_DT ASCDESC C.CUST_SALE_DT ASCDESC
A.ORD_YY_MM_DT DESC, A.ORD_PRID DESC, A.ORD_NO, A.ORD_TP, A.CARLINE_CD, A.MODEL_CD, A.OCN_CD, A.EXT_COLOR_CD, A.INT_COLOR_CD
A.ORD_YY_MM_DT DESC, A.ORD_PRID DESC, A.ORD_NO, A.ORD_TP, A.CARLINE_CD, A.MODEL_CD, A.OCN_CD, A.EXT_COLOR_CD, A.INT_COLOR_CD
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

SELECT * FROM ( SELECT ROWNUM AS RNUM , FN_GET_CARLINE_NM_NC(T1.CARLINE_CD) AS CARLINE_NM , FN_GET_MODEL_TP_NM(T1.MODEL_CD, T1.OCN_CD) AS MODEL_NM , FN_GET_OCN_NM_NC(T1.MODEL_CD, T1.OCN_CD) AS OCN_NM , FN_GET_EXT_COLOR_NM_NC(T1.EXT_COLOR_CD) AS EXT_COLOR_NM , FN_GET_INT_COLOR_NM_NC(T1.INT_COLOR_CD) AS INT_COLOR_NM , SALE_RGST_ID AS CHRG_CD , DC_RATE AS DC_RATE_XLS , MDPR + DAMT || '.00' AS DC_AMT_XLS , MDPR || '.00' AS MDPR_XLS , DAMT || '.00' AS DAMT_XLS , T1.* FROM (SELECT A.ORD_YY_MM_DT --오더년월 , A.ORD_PRID AS WEEK --오더주차 , SUBSTR(A.ORD_YY_MM_DT, 3, 6) AS YY_MM --오더년YYMM , A.DLR_CD --딜러코드 , A.CARLINE_CD , A.MODEL_CD , A.OCN_CD , A.EXT_COLOR_CD , A.INT_COLOR_CD , CASE WHEN NVL(A.ORD_QTY, 0) > 1 THEN 1 ELSE NVL(A.ORD_QTY, 0) END AS ORD_QTY --주문수량 , CASE WHEN NVL(A.FST_CONFIRM_QTY, 0) > 1 THEN 1 ELSE NVL(A.FST_CONFIRM_QTY, 0) END AS FST_CONFIRM_QTY --1차확정수량합 , CASE WHEN NVL(A.SCND_CONFIRM_QTY, 0) > 1 THEN 1 ELSE NVL(A.SCND_CONFIRM_QTY, 0) END AS SCND_CONFIRM_QTY --2차확정수량합 , (CASE WHEN B.PRTY IS NULL THEN 1 ELSE 0 END) AS WT_ORD_QTY --승인대기 , (CASE WHEN B.PRTY = 'B' THEN 1 ELSE 0 END) AS DLR_CNCL_QTY --승인후취소수량 O , (CASE WHEN B.PRTY = 'A' THEN 1 ELSE 0 END) AS NON_ALOC_QTY --미배정수량 , (CASE WHEN B.PRTY = 'P' THEN 1 ELSE 0 END) AS PDI_QTY --PDI배정 , (CASE WHEN B.PRTY = 'C' THEN 1 ELSE 0 END) AS VIN_QTY --VIN배정 , (CASE WHEN B.PRTY = 'F' THEN 1 ELSE 0 END) AS GATE_OUT_QTY --법인출고수량 , TO_CHAR(B.EXNO, 'YYYY-MM-DD') AS FNDL_DT_XLS --오더분배예정일 , NVL(A.SALE_RGST_ID, B.REG_USR_ID) AS REG_USR_ID , B.SALES_NO AS ORD_SEQ --오더번호 , (SELECT NVL(B.CMM_CD_TXT, A.CMM_CD_NM) AS CMM_CD_NM FROM CM_0303T A LEFT OUTER JOIN CM_0304T B ON A.CMM_GRP_CD = B.CMM_GRP_CD AND A.CMM_CD = B.CMM_CD AND B.LANG_CD = 'zh' WHERE A.CMM_GRP_CD = 'SAL150' AND A.USE_YN = 'Y' AND A.CMM_CD = B.PRTY) AS ORD_STAT_CD --오더상태명(공통코드SAL150) , TO_CHAR(B.CNDY,'YYYY-MM-DD') AS ORD_DATE_XLS --오더등록일자 , (SELECT NVL(B.CMM_CD_TXT, A.CMM_CD_NM) AS CMM_CD_NM FROM CM_0303T A LEFT OUTER JOIN CM_0304T B ON A.CMM_GRP_CD = B.CMM_GRP_CD AND A.CMM_CD = B.CMM_CD AND B.LANG_CD = 'zh' WHERE A.CMM_GRP_CD = 'SAL211' AND A.USE_YN = 'Y' AND A.CMM_CD = B.ALOC) AS VINM_VLOC --하치장명(공통코드SAL211) , B.VIN_NO1 || B.VIN_NO2 AS VIN_NO --VIN NO , '['|| A.ORD_TP || ']' || (SELECT NVL(B.CMM_CD_TXT, A.CMM_CD_NM) AS CMM_CD_NM FROM CM_0303T A LEFT OUTER JOIN CM_0304T B ON A.CMM_GRP_CD = B.CMM_GRP_CD AND A.CMM_CD = B.CMM_CD AND B.LANG_CD = 'zh' WHERE A.CMM_GRP_CD = 'SAL137' AND A.USE_YN = 'Y' AND A.CMM_CD = A.ORD_TP) AS ORD_TP --주문유형(공통코드SAL137) , CASE WHEN B.ALDY = '00000000' THEN NULL ELSE TO_CHAR(TO_DATE(B.ALDY, 'YYYYMMDD'),'YYYY-MM-DD') END AS PDI_DT_XLS --PDI배정일 , TO_CHAR(C.CAR_VIN_ALLOC_DT, 'YYYY-MM-DD') AS VIN_DT_XLS --VIN배정일 , TO_CHAR(C.PLT_GI_DT, 'YYYY-MM-DD') AS RETAIL_DT_XLS , TO_CHAR(C.DLR_GR_DT, 'YYYY-MM-DD') AS DLR_GR_DT_XLS --입고일자 , TO_CHAR(C.CUST_SALE_DT, 'YYYY-MM-DD') AS CUST_SALE_DT_XLS --고객판매일자 , DECODE(B.EMONT, NULL, NULL, '000000', NULL, TO_CHAR(TO_DATE(B.EMONT,'YYYYMM'),'YYYY-MM')) AS EMONT , (SELECT X.MS_PRC FROM SA_0110T X WHERE X.MODEL_CD = A.MODEL_CD AND X.OCN_CD = A.OCN_CD AND TO_CHAR(X.END_DT,'YYYYMMDD') >= TO_CHAR(SYSDATE,'YYYYMMDD') AND X.USE_YN = 'Y') AS MDPR ,B.DAMT ,(SELECT DECODE(X.DATE2,NULL,NULL,'00000000',NULL,X.DATE2) FROM SA_0447T X WHERE X.KUNNR = B.DLR_CD AND X.VBELN = B.SALES_NO AND X.TSTMP = (SELECT MAX(Y.TSTMP) FROM SA_0447T Y WHERE Y.KUNNR = B.DLR_CD AND Y.VBELN = B.SALES_NO AND NOT Y.ZACTION = 'D')) AS MIS_SET_DT_XLS ,(SELECT DECODE(X.DATE3,NULL,NULL,'00000000',NULL,X.DATE3) FROM SA_0448T X WHERE X.KUNNR = B.DLR_CD AND X.VBELN = B.SALES_NO AND X.TSTMP = (SELECT MAX(Y.TSTMP) FROM SA_0448T Y WHERE Y.KUNNR = B.DLR_CD AND Y.VBELN = B.SALES_NO AND NOT Y.ZACTION = 'D')) AS TRAN_SET_DT_XLS ,A.SALE_RGST_ID ,CASE WHEN NOT B.DAMT = 0 AND NOT B.AAMT = 0 THEN ROUND(((B.DAMT)*-1)/(B.AAMT+(B.DAMT)*-1)*100,2) ELSE 0 END AS DC_RATE FROM SA_0417T A , SA_0416T B , SA_0121T C WHERE A.DLR_CD = B.DLR_CD(+) AND A.ORD_TP = B.SORD(+) AND A.ORD_YY_MM_DT = B.ORD_YYMM(+) AND A.ORD_PRID = B.ORD_WEEK(+) AND TO_NUMBER(A.ORD_NO) = B.ORD_SEQ(+) AND B.VIN_NO1 = C.VIN_NO1(+) AND B.VIN_NO2 = C.VIN_NO2(+) AND A.ORD_STAT_CD NOT IN ('1A') AND B.PRTY NOT IN ('A','B','R') AND A.IFRESULT_RTN != 'E'
AND A.ORD_TP = #{sOrdTp}
AND A.ORD_TP IN
#{ordTp}
AND A.ORD_DT >= #{sOrdStartDt}
AND A.ORD_DT <= #{sOrdEndDt} +1
AND A.DLR_CD = #{sDlrCd}
AND ORD_YY_MM_DT = SUBSTR(#{sOrdPrid}, 1, 6) AND ORD_PRID = SUBSTR(#{sOrdPrid}, 7, 2)
AND A.CARLINE_CD = #{sCarlineCd}
AND A.MODEL_CD = #{sModelCd}
AND A.OCN_CD = #{sOcnCd}
AND A.EXT_COLOR_CD = #{sExtColorCd}
AND A.INT_COLOR_CD = #{sIntColorCd}
AND B.PRTY = #{sOrdStatCd}
AND B.EMONT = #{sConfirmDt}
AND B.ALDY >= #{sOrdStartDtstr}
AND B.ALDY <= #{sOrdEndDtstr}
AND (SELECT X.FSCP_MODTP_CD FROM SA_0105T X WHERE X.MODEL_CD = A.MODEL_CD AND X.OCN_CD = A.OCN_CD) = #{sFscCd}
AND (SELECT X.FSCP_MODTP_CD FROM SA_0105T X WHERE X.MODEL_CD = A.MODEL_CD AND X.OCN_CD = A.OCN_CD) IS NULL
ORDER BY A.ORD_YY_MM_DT DESC, A.ORD_PRID DESC, A.ORD_NO, A.ORD_TP, A.CARLINE_CD, A.MODEL_CD, A.OCN_CD, A.EXT_COLOR_CD, A.INT_COLOR_CD ) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

SELECT SUM(T1.ORD_QTY) FROM ( SELECT D.DLR_CD ,D.CARLINE_CD ,D.MODEL_CD ,D.OCN_CD ,D.EXT_COLOR_CD ,D.INT_COLOR_CD
,D.MDPR ,SUM(CASE WHEN D.ORD_QTY>=1 THEN 1 ELSE 0 END) ORD_QTY ,SUM(D.DC_AMT) DC_AMT FROM ( SELECT A.DLR_CD ,A.CARLINE_CD ,A.MODEL_CD ,A.OCN_CD ,A.EXT_COLOR_CD ,A.INT_COLOR_CD
,( SELECT NVL(X.MS_PRC,0) FROM SA_0110T X WHERE X.MODEL_CD = A.MODEL_CD AND X.OCN_CD = A.OCN_CD AND TO_CHAR(X.END_DT,'YYYYMMDD') >= TO_CHAR(SYSDATE,'YYYYMMDD') AND X.USE_YN = 'Y' ) AS MDPR ,NVL(A.ORD_QTY,0) ORD_QTY ,NVL(B.DAMT,0) DAMT ,( SELECT NVL(X.MS_PRC,0) FROM SA_0110T X WHERE X.MODEL_CD = A.MODEL_CD AND X.OCN_CD = A.OCN_CD AND TO_CHAR(X.END_DT,'YYYYMMDD') >= TO_CHAR(SYSDATE,'YYYYMMDD') AND X.USE_YN = 'Y' )+NVL(B.DAMT,0) DC_AMT FROM SA_0417T A ,SA_0416T B ,SA_0121T C WHERE A.DLR_CD = B.DLR_CD(+) AND A.ORD_TP = B.SORD(+) AND A.ORD_YY_MM_DT = B.ORD_YYMM(+) AND A.ORD_PRID = B.ORD_WEEK(+) AND TO_NUMBER(A.ORD_NO) = B.ORD_SEQ(+) AND B.VIN_NO1 = C.VIN_NO1(+) AND B.VIN_NO2 = C.VIN_NO2(+) AND A.ORD_STAT_CD NOT IN ('1A') AND B.PRTY NOT IN ('A','B','R') AND A.IFRESULT_RTN != 'E'
AND A.ORD_TP = #{sOrdTp}
AND A.ORD_TP IN
#{ordTp}
AND A.DLR_CD = #{sDlrCd}
AND ORD_YY_MM_DT = SUBSTR(#{sOrdPrid}, 1, 6) AND ORD_PRID = SUBSTR(#{sOrdPrid}, 7, 2)
AND A.CARLINE_CD = #{sCarlineCd}
AND A.MODEL_CD = #{sModelCd}
AND A.OCN_CD = #{sOcnCd}
AND A.EXT_COLOR_CD = #{sExtColorCd}
AND A.INT_COLOR_CD = #{sIntColorCd}
AND B.PRTY = #{sOrdStatCd}
AND A.ORD_GRADE_CD = #{sordGradeCd}
AND B.EMONT = #{sConfirmDt}
AND B.ALDY >= #{sOrdStartDtstr}
AND B.ALDY <= #{sOrdEndDtstr}
AND (SELECT X.FSCP_MODTP_CD FROM SA_0105T X WHERE X.MODEL_CD = A.MODEL_CD AND X.OCN_CD = A.OCN_CD) = #{sFscCd}
AND (SELECT X.FSCP_MODTP_CD FROM SA_0105T X WHERE X.MODEL_CD = A.MODEL_CD AND X.OCN_CD = A.OCN_CD) IS NULL
) D GROUP BY D.DLR_CD ,D.CARLINE_CD ,D.MODEL_CD ,D.OCN_CD ,D.EXT_COLOR_CD ,D.INT_COLOR_CD
,D.MDPR ORDER BY D.CARLINE_CD,D.MODEL_CD,D.OCN_CD,D.EXT_COLOR_CD,D.INT_COLOR_CD,D.MDPR ) T1

SELECT * FROM ( SELECT ROWNUM AS RNUM ,T1.DLR_CD ,T1.CARLINE_CD ,T1.MODEL_CD ,T1.OCN_CD ,T1.EXT_COLOR_CD ,T1.INT_COLOR_CD ,FN_GET_CARLINE_NM_NC(T1.CARLINE_CD) AS CARLINE_NM ,FN_GET_MODEL_TP_NM(T1.MODEL_CD, T1.OCN_CD) AS MODEL_NM ,FN_GET_OCN_NM_NC(T1.MODEL_CD, T1.OCN_CD) AS OCN_NM ,FN_GET_EXT_COLOR_NM_NC(T1.EXT_COLOR_CD) AS EXT_COLOR_NM ,FN_GET_INT_COLOR_NM_NC(T1.INT_COLOR_CD) AS INT_COLOR_NM ,T1.MDPR ,T1.ORD_QTY ,T1.DC_AMT ,T1.AAMT FROM ( SELECT D.DLR_CD ,D.CARLINE_CD ,D.MODEL_CD ,D.OCN_CD ,D.EXT_COLOR_CD ,D.INT_COLOR_CD ,D.MDPR ,SUM(CASE WHEN D.ORD_QTY>=1 THEN 1 ELSE 0 END) ORD_QTY ,SUM(D.DC_AMT) DC_AMT ,SUM(D.AAMT) AAMT FROM ( SELECT A.DLR_CD ,A.CARLINE_CD ,A.MODEL_CD ,A.OCN_CD ,A.EXT_COLOR_CD ,A.INT_COLOR_CD ,( SELECT NVL(X.MS_PRC,0) FROM SA_0110T X WHERE X.MODEL_CD = A.MODEL_CD AND X.OCN_CD = A.OCN_CD AND TO_CHAR(X.END_DT,'YYYYMMDD') >= TO_CHAR(SYSDATE,'YYYYMMDD') AND X.USE_YN = 'Y' ) AS MDPR ,NVL(A.ORD_QTY,0) ORD_QTY ,NVL(B.DAMT,0) DAMT ,NVL(B.AAMT,0) AAMT ,( SELECT NVL(X.MS_PRC,0) FROM SA_0110T X WHERE X.MODEL_CD = A.MODEL_CD AND X.OCN_CD = A.OCN_CD AND TO_CHAR(X.END_DT,'YYYYMMDD') >= TO_CHAR(SYSDATE,'YYYYMMDD') AND X.USE_YN = 'Y' )+NVL(B.DAMT,0) DC_AMT FROM SA_0417T A ,SA_0416T B ,SA_0121T C WHERE A.DLR_CD = B.DLR_CD(+) AND A.ORD_TP = B.SORD(+) AND A.ORD_YY_MM_DT = B.ORD_YYMM(+) AND A.ORD_PRID = B.ORD_WEEK(+) AND TO_NUMBER(A.ORD_NO) = B.ORD_SEQ(+) AND B.VIN_NO1 = C.VIN_NO1(+) AND B.VIN_NO2 = C.VIN_NO2(+) AND A.ORD_STAT_CD NOT IN ('1A') AND B.PRTY NOT IN ('A','B','R') AND A.IFRESULT_RTN != 'E' AND A.ORD_GRADE_CD IN ('P','M','N','C','F','D','E')
AND A.ORD_TP = #{sOrdTp}
AND A.ORD_TP IN
#{ordTp}
AND A.DLR_CD = #{sDlrCd}
AND ORD_YY_MM_DT = SUBSTR(#{sOrdPrid}, 1, 6) AND ORD_PRID = SUBSTR(#{sOrdPrid}, 7, 2)
AND A.CARLINE_CD = #{sCarlineCd}
AND A.MODEL_CD = #{sModelCd}
AND A.OCN_CD = #{sOcnCd}
AND A.EXT_COLOR_CD = #{sExtColorCd}
AND A.INT_COLOR_CD = #{sIntColorCd}
AND B.PRTY = #{sOrdStatCd}
AND A.ORD_GRADE_CD = #{sordGradeCd}
AND B.EMONT = #{sConfirmDt}
AND B.ALDY >= #{sOrdStartDtstr}
AND B.ALDY <= #{sOrdEndDtstr}
AND (SELECT X.FSCP_MODTP_CD FROM SA_0105T X WHERE X.MODEL_CD = A.MODEL_CD AND X.OCN_CD = A.OCN_CD) = #{sFscCd}
AND (SELECT X.FSCP_MODTP_CD FROM SA_0105T X WHERE X.MODEL_CD = A.MODEL_CD AND X.OCN_CD = A.OCN_CD) IS NULL
) D GROUP BY D.DLR_CD ,D.CARLINE_CD ,D.MODEL_CD ,D.OCN_CD ,D.EXT_COLOR_CD ,D.INT_COLOR_CD ,D.MDPR ORDER BY D.CARLINE_CD,D.MODEL_CD,D.OCN_CD,D.EXT_COLOR_CD,D.INT_COLOR_CD,D.MDPR ) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}
