/* [chn.bhmc.dms.sal.inv.service.dao.StockMntDAO.selectStockMntsByConditionCnt] */ SELECT COUNT(1) FROM ( SELECT X1.* FROM ( SELECT A.CAR_ID , A.STOCK_TP -- 재고유형 , ( SELECT Z4.CMM_CD_NM FROM (SELECT A.CMM_CD , NVL(B.CMM_CD_TXT, A.CMM_CD_NM) AS CMM_CD_NM FROM CM_0303T A LEFT OUTER JOIN CM_0304T B ON A.CMM_GRP_CD = B.CMM_GRP_CD AND A.CMM_CD = B.CMM_CD AND B.LANG_CD = #{sLangCd} WHERE A.CMM_GRP_CD = 'SAL001' /*차량상태코드*/ AND A.USE_YN = 'Y' ) Z4 WHERE A.CAR_STAT_CD = Z4.CMM_CD ) AS CAR_STAT_NM -- 차량상태(재고유형) , A.VIN_NO -- 차대번호 , A.NCAR_DSTIN_CD -- 신차량구분코드 , A.CARLINE_CD , A.MODEL_CD , A.OCN_CD , A.EXT_COLOR_CD , A.INT_COLOR_CD , A.ORD_TP , ( SELECT Z1.CMM_CD_NM FROM (SELECT A.CMM_CD , NVL(B.CMM_CD_TXT, A.CMM_CD_NM) AS CMM_CD_NM FROM CM_0303T A LEFT OUTER JOIN CM_0304T B ON A.CMM_GRP_CD = B.CMM_GRP_CD AND A.CMM_CD = B.CMM_CD AND B.LANG_CD = #{sLangCd} WHERE A.CMM_GRP_CD = 'SAL138' /*오더유형*/ AND A.USE_YN = 'Y' ) Z1 WHERE A.ORD_TP = Z1.CMM_CD ) AS ORD_TP_NM -- 주문유형 , A.STR_YN -- 전략여부 , A.STR_NO -- 전략신청번호 , ( SELECT CASE WHEN MAX(Z1.STR_STAT) = 'A' THEN 'A' WHEN MAX(Z1.STR_STAT) = 'B' THEN 'Y' ELSE 'N' END AS STR_STAT_CD FROM SA_0256T Z1 --전략출고 INNER JOIN ( SELECT DLR_CD , CAR_ID , MAX(CAST(NVL(STR_NO,0) AS INTEGER)) AS STR_NO FROM SA_0256T GROUP BY DLR_CD, CAR_ID ) Z2 ON Z1.DLR_CD = Z2.DLR_CD AND Z1.CAR_ID = Z2.CAR_ID AND Z1.STR_NO = Z2.STR_NO WHERE Z1.DLR_CD = A.ORD_DLR_CD AND Z1.CAR_ID = A.CAR_ID AND Z1.STR_STAT <> 'X' ) AS STR_STAT_CD , NVL(A.DAMAGE_CD, '02') AS DAMAGE_CD -- 손상여부 : SAL049 , CASE WHEN A.BLOCK_SALE_ADV_NO IS NOT NULL THEN 'Y' WHEN NVL(A.OWN_STAT_CD, 'O') = 'A' THEN 'Y' WHEN NVL(A.DAMAGE_CD, '02') = '01' THEN 'Y' /* ELSE NVL(A.BLOCK_YN, 'N') */ ELSE 'N' END AS BLOCK_YN , NVL(A.BLOCK_SALE_ADV_NO, ' ') AS BLOCK_SALE_ADV_NO , NVL(A.STRGE_CD, ' ') AS STRGE_CD -- 창고 , NVL(A.LOC_CD, ' ') AS LOC_CD -- 위치 , ( SELECT NVL(Z3.LOC_NM, ' ') AS LOC_NM FROM ( SELECT DLR_CD , STRGE_CD , LOC_CD , LOC_NM FROM PT_0222T GROUP BY DLR_CD, STRGE_CD, LOC_CD, LOC_NM ) Z3 WHERE Z3.DLR_CD = A.ORD_DLR_CD AND Z3.STRGE_CD = A.STRGE_CD AND Z3.LOC_CD = A.LOC_CD ) AS LOC_NM , NVL(A.CMP_CAR_YN, 'N') AS CMP_CAR_YN -- 회사차여부 , NVL(A.CMP_CAR_DSTIN_CD, ' ') AS CMP_CAR_DSTIN_CD -- 회사차구분
, NVL(A.DAMAGE_CD, '02') AS BF_DAMAGE_CD , NVL(A.BLOCK_YN, 'N') AS BF_BLOCK_YN , A.BLOCK_SALE_ADV_NO AS BF_BLOCK_SALE_ADV_NO , A.STRGE_CD AS BF_STRGE_CD , A.LOC_CD AS BF_LOC_CD , A.CMP_CAR_YN AS BF_CMP_CAR_YN , A.CMP_CAR_DSTIN_CD AS BF_CMP_CAR_DSTIN_CD , A.OPEN_YN -- 공개여부 , X.WH_PRC AS RETL_PRC -- 사용자금 , DECODE(A.CERT_SECU_CD, NULL, 'N', A.CERT_SECU_CD) AS USED_AMT_TP , ( SELECT Z1.CMM_CD_NM FROM (SELECT A.CMM_CD , NVL(B.CMM_CD_TXT, A.CMM_CD_NM) AS CMM_CD_NM FROM CM_0303T A LEFT OUTER JOIN CM_0304T B ON A.CMM_GRP_CD = B.CMM_GRP_CD AND A.CMM_CD = B.CMM_CD AND B.LANG_CD = #{sLangCd} WHERE A.CMM_GRP_CD = 'SAL191' /*자금유형구분*/ AND A.USE_YN = 'Y' ) Z1 WHERE Z1.CMM_CD = DECODE(A.CERT_SECU_CD, NULL, 'N', A.CERT_SECU_CD) ) AS USED_AMT_TP_NM , CASE WHEN A.CERT_SECU_CD = 'L' THEN NVL(A.VINM_BKTX, (SELECT BANK_NM FROM CM_0101T Z2 WHERE Z2.DLR_CD = A.ORD_DLR_CD)) ELSE ' ' END AS BANK_NM -- 은행 , A.SIGNOFF_DT -- 생산일자 , A.PLT_GI_DT -- 출고일자 , A.DLR_GR_DT -- 입고일자 , TO_CHAR(A.PLT_GI_DT, 'YYYY-MM-DD') As PLT_GI_DT_STR , TO_CHAR(A.DLR_GR_DT, 'YYYY-MM-DD') As DLR_GR_DT_STR , A.ENGIN_MODEL_CD || ENGIN_SERIAL_NO AS ENGIN_MODEL_CD -- 엔진번호 , A.CERT_NO -- 합격증번호 , A.CAR_STAT_CD -- 차량상태 , NVL(A.OWN_STAT_CD, 'O') AS OWN_STAT_CD -- 보유상태 , NVL( TRUNC(MONTHS_BETWEEN(SYSDATE, A.PLT_GI_DT)+1, 0), 0) AS MM_CNT , CASE WHEN NVL( TRUNC(MONTHS_BETWEEN(SYSDATE, A.PLT_GI_DT)+1, 0) , 0) <= 2 then '#ffe0d9' WHEN NVL( TRUNC(MONTHS_BETWEEN(SYSDATE, A.PLT_GI_DT)+1, 0) , 0) <= 3 then '#fff4c9' ELSE '#e5f5fa' END AS MM_CNT_COLOR , TRUNC(SYSDATE+1 - TO_DATE(nvl(A.PLT_GI_DT,SYSDATE+1), 'YYYY-MM-DD' )) AS GI_DD_CNT --재고일령(공장출고) , TRUNC(SYSDATE+1 - TO_DATE(A.DLR_GR_DT, 'YYYY-MM-DD' )) AS GR_DD_CNT --재고일령(입고일자) , X.MS_PRC AS WHSL_PRC -- 지도가격 , A.ORD_DLR_CD AS DLR_CD , (SELECT DLR_NM FROM CM_0101T WHERE DLR_CD = A.ORD_DLR_CD) AS DLR_NM , A.VINM_F20010 , A.TEMP2 -- 창고비고 , (SELECT CONTRACT_NO FROM SA_0201T WHERE DLR_CD = A.ORD_DLR_CD AND VIN_NO = A.VIN_NO AND CONTRACT_STAT_CD IN ('10','20') AND ROWNUM = 1) AS CONTRACT_NO , SA0421.REQ_STAT_CD
