mapper.select:
    /* [chn.bhmc.dms.sal.rcp.service.dao.SalesVhclCbinRcitDAO.selectSalesAddTaxReceiptSearchCnt] */
        SELECT COUNT(*) AS VALUE
        FROM SA_0502T receipt
        INNER JOIN SA_0508T receiptCar ON receiptCar.RECEIPT_ID = receipt.RECEIPT_ID
		INNER JOIN SA_0121T car ON car.CAR_ID = receiptCar.CAR_ID


              AND receipt.REG_DT >= #{sUploadStDt}


              AND receipt.REG_DT < #{sUploadEdDt}+1


              AND receipt.RECEIPT_ISS_DT >= #{sReceiptStDt}


              AND receipt.RECEIPT_ISS_DT < #{sReceiptEdDt}+1


              AND receipt.RECEIPT_NO = #{sReceiptNo}


              AND car.VIN_NO LIKE '%'||#{sVinNo}||'%'

/* [chn.bhmc.dms.sal.rcp.service.dao.SalesVhclCbinRcitDAO.selectAddTaxReceiptListCnt] */
    SELECT COUNT(*) AS VALUE
      FROM SA_0502T A
     WHERE A.BINDING_CAR_YN = 'N' AND A.CLAIM_YN = 'N'

          AND A.RECEIPT_ISS_DT >= #{sReceiptStDt}


          AND A.RECEIPT_ISS_DT < #{sReceiptEdDt}+1


          AND A.RECEIPT_NO = #{sReceiptNo}


          AND A.SHOW_YN = #{showYn}

/* [chn.bhmc.dms.sal.rcp.service.dao.SalesVhclCbinRcitDAO.selectAddTaxReceiptListData] */ SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT A.RECEIPT_ID ,A.CAR_ID ,A.SEQ ,A.VIN_NO ,A.VIN_NO1 ,A.VIN_NO2 ,A.DLR_CD ,A.TAXPAYER_ID_CD ,A.RECEIPT_NO ,A.RECEIPT_CD ,A.BUY_NM ,A.RECEIPT_ISS_DT ,A.SCAN_SUM_AMT ,A.CARLINE_CD ,FN_GET_CARLINE_NM(A.CARLINE_CD) AS CARLINE_NM ,A.MODEL_CD ,A.JEONGPAN_NO ,A.EXT_COLOR_CD ,FN_GET_EXT_COLOR_NM(A.EXT_COLOR_CD) AS EXT_COLOR_NM ,A.WS_PRC ,A.PLT_GI_DT ,A.SUM_AMT ,A.TAX_AMT ,A.TMR_CD ,A.EVAL_RSLT_CD ,A.ERR_UP_CD ,A.SDLR_CD ,A.REG_DT AS UPLOAD_DT ,A.SDLR_TP ,A.SHOW_YN FROM SA_0502T A WHERE A.BINDING_CAR_YN = 'N' AND A.CLAIM_YN = 'N'
AND A.RECEIPT_ISS_DT >= #{sReceiptStDt}
AND A.RECEIPT_ISS_DT < #{sReceiptEdDt}+1
AND A.RECEIPT_NO = #{sReceiptNo}
AND A.SHOW_YN = #{showYn}
ORDER BY
SUM_AMT DESCASC TAX_AMT DESCASC RECEIPT_ISS_DT DESCASC
RECEIPT_ISS_DT DESC
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

mapper.insert:
/* [chn.bhmc.dms.sal.rcp.service.dao.SalesVhclCbinRcitDAO.insertSalesAddReceiptAndCarBinDing] */
    INSERT INTO SA_0508T(
